import React, { useEffect, useState } from 'react';
import { Navigate, useLocation } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import { Box, CircularProgress, Typography } from '@mui/material';
import { authService } from '../../services/api';

interface AuthGuardProps {
  children: React.ReactNode;
}

const AuthGuard: React.FC<AuthGuardProps> = ({ children }) => {
  const auth = useAuth();
  const location = useLocation();
  const [isValidating, setIsValidating] = useState(true);
  const [isValid, setIsValid] = useState(false);

  // Log auth state for debugging
  useEffect(() => {
    console.log("AuthGuard - Auth state:", { 
      isAuthenticated: auth.isAuthenticated,
      user: auth.user,
      loading: auth.loading,
      path: location.pathname
    });
  }, [auth, location]);

  // Validate auth on mount and when auth state changes
  useEffect(() => {
    const checkAuth = async () => {
      setIsValidating(true);
      try {
        // Basic checks first
        const hasToken = !!localStorage.getItem('access_token');
        console.log("AuthGuard - Token existence check:", hasToken);
        
        if (!hasToken) {
          setIsValid(false);
          return;
        }
        
        // Use auth context if available, fall back to service
        const valid = auth.validateToken 
          ? await auth.validateToken()
          : await authService.validateToken();
          
        console.log("AuthGuard - Token validation result:", valid);
        setIsValid(valid);
      } catch (error) {
        console.error('AuthGuard - Token validation error:', error);
        setIsValid(false);
      } finally {
        setIsValidating(false);
      }
    };

    if (!auth.loading) {
      checkAuth();
    }
  }, [auth.loading, auth.isAuthenticated, auth.validateToken]);

  // Handle auth:required events from apiClient
  useEffect(() => {
    const handleAuthRequired = (event: Event) => {
      console.log('AuthGuard - Received auth:required event', 
        (event as CustomEvent).detail);
      setIsValid(false);
    };

    window.addEventListener('auth:required', handleAuthRequired);
    return () => {
      window.removeEventListener('auth:required', handleAuthRequired);
    };
  }, []);

  // Show loading state while checking auth
  if (auth.loading || isValidating) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <Box sx={{ textAlign: 'center' }}>
          <CircularProgress />
          <Typography variant="body1" sx={{ mt: 2 }}>
            Verifying authentication...
          </Typography>
        </Box>
      </Box>
    );
  }

  // Redirect to login if not authenticated
  if (!auth.isAuthenticated || !isValid) {
    console.log("AuthGuard - Authentication failed, redirecting to login");
    return <Navigate to="/login" state={{ from: location.pathname }} replace />;
  }

  // User is authenticated, render children
  console.log("AuthGuard - Authentication successful, rendering protected content");
  return <>{children}</>;
};

export default AuthGuard;

import React, { useState } from 'react';
import { 
  Box, 
  Paper, 
  Typography, 
  TextField, 
  Button, 
  CircularProgress, 
  Alert, 
  Link as MuiLink
} from '@mui/material';
import { Link, useNavigate } from 'react-router-dom';
import { authService } from '../../services/api/authService';

const ForgotPassword: React.FC = () => {
  const navigate = useNavigate();
  
  const [email, setEmail] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [success, setSuccess] = useState<string | null>(null);
  
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    
    if (!email.trim() || !email.includes('@')) {
      setError('Please enter a valid email address');
      return;
    }
    
    setLoading(true);
    setError(null);
    setSuccess(null);
    
    try {
      const response = await authService.forgotPassword({ email });
      
      if (response.success) {
        setSuccess('Password reset instructions have been sent to your email address');
      } else {
        setError('Failed to request password reset. Please try again.');
      }
    } catch (err: any) {
      setError(err.message || 'Failed to request password reset. Please try again.');
      console.error('Forgot password error:', err);
    } finally {
      setLoading(false);
    }
  };
  
  return (
    <Box
      sx={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        maxWidth: '500px',
        mx: 'auto',
        py: 4
      }}
    >
      <Paper sx={{ p: 3, width: '100%' }}>
        <Typography variant="h5" component="h1" align="center" gutterBottom>
          Forgot Password
        </Typography>
        
        <Typography variant="body2" align="center" color="text.secondary" paragraph>
          Enter your email address and we'll send you instructions to reset your password.
        </Typography>
        
        {error && (
          <Alert severity="error" sx={{ mb: 3 }}>
            {error}
          </Alert>
        )}
        
        {success && (
          <Alert severity="success" sx={{ mb: 3 }}>
            {success}
          </Alert>
        )}
        
        <Box component="form" onSubmit={handleSubmit} sx={{ mt: 2 }}>
          <TextField
            margin="normal"
            required
            fullWidth
            id="email"
            label="Email Address"
            name="email"
            autoComplete="email"
            autoFocus
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            disabled={loading || !!success}
          />
          
          <Button
            type="submit"
            fullWidth
            variant="contained"
            sx={{ mt: 3, mb: 2 }}
            disabled={loading || !!success}
          >
            {loading ? <CircularProgress size={24} /> : 'Send Reset Link'}
          </Button>
          
          <Box sx={{ mt: 2, textAlign: 'center' }}>
            <Typography variant="body2">
              Remember your password?{' '}
              <MuiLink component={Link} to="/login" underline="hover">
                Back to login
              </MuiLink>
            </Typography>
          </Box>
        </Box>
      </Paper>
    </Box>
  );
};

export default ForgotPassword;

import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Paper, 
  Typography, 
  TextField, 
  Button, 
  CircularProgress, 
  Alert, 
  Stepper, 
  Step, 
  StepLabel,
  InputAdornment,
  IconButton
} from '@mui/material';
import { Visibility, VisibilityOff } from '@mui/icons-material';
import { useNavigate, useLocation } from 'react-router-dom';
import { authService } from '../../services/api';

interface JoinByInvitationProps {
  onRegisterSuccess?: (userData: any) => void;
}

const JoinByInvitation: React.FC<JoinByInvitationProps> = ({ onRegisterSuccess }) => {
  const navigate = useNavigate();
  const location = useLocation();
  
  // Get code from URL query parameter if available
  const urlParams = new URLSearchParams(location.search);
  const codeFromUrl = urlParams.get('code');
  
  // Form state
  const [invitationCode, setInvitationCode] = useState(codeFromUrl || '');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [jobTitle, setJobTitle] = useState('');
  const [department, setDepartment] = useState('');
  const [employeeId, setEmployeeId] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  
  // UI state
  const [activeStep, setActiveStep] = useState(0);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [verifying, setVerifying] = useState(false);
  const [companyName, setCompanyName] = useState<string | null>(null);
  const [codeValid, setCodeValid] = useState(false);
  const [companyId, setCompanyId] = useState<string | null>(null);
  
  // Validate the invitation code on component mount if code is provided
  useEffect(() => {
    if (invitationCode) {
      validateCode();
    }
  }, []);
  
  const validateCode = async () => {
    if (!invitationCode.trim()) {
      setError('Please enter an invitation code');
      return;
    }
    
    setVerifying(true);
    setError(null);
    
    try {
      const response = await authService.validateInvitationCode(invitationCode);
      
      if (response.valid) {
        setCompanyName(response.companyName || null);
        setCodeValid(true);
        setActiveStep(1);
      } else {
        setError('Invalid or expired invitation code. Please contact your administrator.');
      }
    } catch (err: any) {
      setError('Failed to verify invitation code. Please try again.');
      console.error('Error validating invitation code:', err);
    } finally {
      setVerifying(false);
    }
  };
  
  const handleNext = () => {
    setError(null);
    
    if (activeStep === 0) {
      validateCode();
    } else if (activeStep === 1) {
      if (validatePersonalInfo()) {
        setActiveStep(2);
      }
    } else if (activeStep === 2) {
      if (validateEmploymentInfo()) {
        handleRegistration();
      }
    }
  };
  
  const handleBack = () => {
    setActiveStep((prevStep) => prevStep - 1);
  };
  
  const validatePersonalInfo = () => {
    if (!email.trim()) {
      setError('Email is required');
      return false;
    } else if (!email.includes('@')) {
      setError('Please enter a valid email address');
      return false;
    }
    
    if (!password) {
      setError('Password is required');
      return false;
    } else if (password.length < 8) {
      setError('Password must be at least 8 characters long');
      return false;
    }
    
    if (password !== confirmPassword) {
      setError('Passwords do not match');
      return false;
    }
    
    if (!firstName.trim() || !lastName.trim()) {
      setError('First and last name are required');
      return false;
    }
    
    return true;
  };
  
  const validateEmploymentInfo = () => {
    if (!jobTitle.trim()) {
      setError('Job title is required');
      return false;
    }
    
    if (!department.trim()) {
      setError('Department is required');
      return false;
    }
    
    return true;
  };
  
  const handleRegistration = async () => {
    setLoading(true);
    setError(null);
    
    try {
      const response = await authService.registerEmployee({
        email,
        password,
        firstName,
        lastName,
        jobTitle,
        department,
        employeeId,
        companyId: '', // The API will extract this from the invitation code
        invitationCode
      });
      
      if (response.data.requiresVerification) {
        // Redirect to a pending approval page
        navigate('/auth/pending-approval', { state: { email } });
      } else {
        // Direct login successful
        if (onRegisterSuccess) {
          onRegisterSuccess(response.data);
        }
        navigate('/dashboard');
      }
    } catch (err: any) {
      setError(err.message || 'Failed to complete registration. Please try again.');
      console.error('Error during employee registration:', err);
    } finally {
      setLoading(false);
    }
  };
  
  const handleTogglePasswordVisibility = () => {
    setShowPassword((prev) => !prev);
  };
  
  // Render different form steps
  const renderCodeVerification = () => (
    <Box>
      <Typography variant="h6" gutterBottom>
        Join Your Company
      </Typography>
      <Typography variant="body2" color="text.secondary" paragraph>
        Enter the invitation code provided by your company administrator.
      </Typography>
      
      <TextField
        margin="normal"
        fullWidth
        label="Invitation Code"
        value={invitationCode}
        onChange={(e) => setInvitationCode(e.target.value)}
        disabled={verifying}
        autoFocus
      />
      
      <Box sx={{ mt: 3, display: 'flex', justifyContent: 'flex-end' }}>
        <Button
          variant="contained"
          color="primary"
          onClick={handleNext}
          disabled={verifying}
        >
          {verifying ? <CircularProgress size={24} /> : 'Verify Code'}
        </Button>
      </Box>
    </Box>
  );
  
  const renderPersonalInfo = () => (
    <Box>
      <Typography variant="h6" gutterBottom>
        Personal Information
      </Typography>
      {companyName && (
        <Typography variant="body1" paragraph>
          You're joining <strong>{companyName}</strong>
        </Typography>
      )}
      
      <TextField
        margin="normal"
        fullWidth
        label="Email Address"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        type="email"
        disabled={loading}
        required
      />
      
      <TextField
        margin="normal"
        fullWidth
        label="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        type={showPassword ? 'text' : 'password'}
        disabled={loading}
        required
        InputProps={{
          endAdornment: (
            <InputAdornment position="end">
              <IconButton
                aria-label="toggle password visibility"
                onClick={handleTogglePasswordVisibility}
                edge="end"
              >
                {showPassword ? <VisibilityOff /> : <Visibility />}
              </IconButton>
            </InputAdornment>
          ),
        }}
      />
      
      <TextField
        margin="normal"
        fullWidth
        label="Confirm Password"
        value={confirmPassword}
        onChange={(e) => setConfirmPassword(e.target.value)}
        type={showPassword ? 'text' : 'password'}
        disabled={loading}
        required
      />
      
      <Box sx={{ display: 'flex', gap: 2, mt: 2 }}>
        <TextField
          margin="normal"
          fullWidth
          label="First Name"
          value={firstName}
          onChange={(e) => setFirstName(e.target.value)}
          disabled={loading}
          required
        />
        
        <TextField
          margin="normal"
          fullWidth
          label="Last Name"
          value={lastName}
          onChange={(e) => setLastName(e.target.value)}
          disabled={loading}
          required
        />
      </Box>
      
      <Box sx={{ mt: 3, display: 'flex', justifyContent: 'space-between' }}>
        <Button onClick={handleBack} disabled={loading}>
          Back
        </Button>
        <Button
          variant="contained"
          color="primary"
          onClick={handleNext}
          disabled={loading}
        >
          Next
        </Button>
      </Box>
    </Box>
  );
  
  const renderEmploymentInfo = () => (
    <Box>
      <Typography variant="h6" gutterBottom>
        Employment Information
      </Typography>
      
      <TextField
        margin="normal"
        fullWidth
        label="Job Title"
        value={jobTitle}
        onChange={(e) => setJobTitle(e.target.value)}
        disabled={loading}
        required
      />
      
      <TextField
        margin="normal"
        fullWidth
        label="Department"
        value={department}
        onChange={(e) => setDepartment(e.target.value)}
        disabled={loading}
        required
      />
      
      <TextField
        margin="normal"
        fullWidth
        label="Employee ID (Optional)"
        value={employeeId}
        onChange={(e) => setEmployeeId(e.target.value)}
        disabled={loading}
      />
      
      <Box sx={{ mt: 3, display: 'flex', justifyContent: 'space-between' }}>
        <Button onClick={handleBack} disabled={loading}>
          Back
        </Button>
        <Button
          variant="contained"
          color="primary"
          onClick={handleNext}
          disabled={loading}
        >
          {loading ? <CircularProgress size={24} /> : 'Complete Registration'}
        </Button>
      </Box>
    </Box>
  );
  
  return (
    <Box
      sx={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        maxWidth: '600px',
        mx: 'auto',
        py: 4
      }}
    >
      <Paper sx={{ p: 3, width: '100%' }}>
        <Stepper activeStep={activeStep} sx={{ mb: 4 }}>
          <Step>
            <StepLabel>Invitation</StepLabel>
          </Step>
          <Step>
            <StepLabel>Personal Info</StepLabel>
          </Step>
          <Step>
            <StepLabel>Employment Info</StepLabel>
          </Step>
        </Stepper>
        
        {error && (
          <Alert severity="error" sx={{ mb: 3 }}>
            {error}
          </Alert>
        )}
        
        {activeStep === 0 && renderCodeVerification()}
        {activeStep === 1 && renderPersonalInfo()}
        {activeStep === 2 && renderEmploymentInfo()}
      </Paper>
    </Box>
  );
};

export default JoinByInvitation;

import React, { useState, useEffect } from 'react';
import { Box, FormControlLabel, Switch, Snackbar, Alert, Typography, Paper } from '@mui/material';
import { toggleMockApi, isMockApiEnabled } from '../../services/api/apiClient';

const MockApiToggle: React.FC = () => {
  const [isMockEnabled, setIsMockEnabled] = useState(isMockApiEnabled);
  const [showNotification, setShowNotification] = useState(false);
  const [notificationMessage, setNotificationMessage] = useState('');

  const handleToggle = (event: React.ChangeEvent<HTMLInputElement>) => {
    const isEnabled = event.target.checked;
    toggleMockApi(isEnabled);
    setIsMockEnabled(isEnabled);
    setNotificationMessage(`API is now using ${isEnabled ? 'mock' : 'real'} data`);
    setShowNotification(true);
  };

  const handleCloseNotification = () => {
    setShowNotification(false);
  };

  return (
    <Paper 
      elevation={1} 
      sx={{
        position: 'fixed',
        bottom: 16,
        right: 16,
        padding: 2,
        zIndex: 1000,
        backgroundColor: 'rgba(255, 255, 255, 0.9)',
        backdropFilter: 'blur(5px)',
        border: '1px solid #eee',
        borderRadius: 2
      }}
    >
      <Box sx={{ display: 'flex', alignItems: 'center', flexDirection: 'column' }}>
        <Typography variant="subtitle2" color="text.secondary" mb={1}>
          Development Mode
        </Typography>
        <FormControlLabel
          control={
            <Switch 
              checked={isMockEnabled}
              onChange={handleToggle}
              color="primary"
            />
          }
          label={isMockEnabled ? "Using Mock API" : "Using Real API"}
        />
      </Box>
      
      <Snackbar
        open={showNotification}
        autoHideDuration={3000}
        onClose={handleCloseNotification}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert onClose={handleCloseNotification} severity="info" sx={{ width: '100%' }}>
          {notificationMessage}
        </Alert>
      </Snackbar>
    </Paper>
  );
};

export default MockApiToggle;

import React, { useState, useEffect } from 'react';
import {
  Box,
  Paper,
  Typography,
  Button,
  Card,
  CardContent,
  CardActions,
  Divider,
  TextField,
  CircularProgress
} from '@mui/material';

// Import our custom hooks and components
import SearchField from '../ui/SearchField';
import { useNotificationContext } from '../../contexts/NotificationContext';
import useApiRequest from '../../hooks/useApiRequest';
import useTableData from '../../hooks/useTableData';
import useForm from '../../hooks/useForm';

// Example form interface
interface ExampleFormData {
  title: string;
  description: string;
  category: string;
}

/**
 * Example component demonstrating the use of our optimized hooks and components
 */
const OptimizedComponent: React.FC = () => {
  // Use the notification context
  const { success, error, info } = useNotificationContext();
  
  // Example form with validation
  const exampleForm = useForm<ExampleFormData>({
    initialValues: {
      title: '',
      description: '',
      category: 'general'
    },
    validate: (values) => {
      const errors: Partial<Record<keyof ExampleFormData, string>> = {};
      
      if (!values.title.trim()) {
        errors.title = 'Title is required';
      }
      
      if (!values.description.trim()) {
        errors.description = 'Description is required';
      }
      
      return errors;
    },
    onSubmit: async (values) => {
      try {
        // This would be an actual API call in a real component
        console.log('Submitting form data:', values);
        
        // Show success notification
        success('Form submitted successfully!');
        
        // Reset the form
        exampleForm.resetForm();
      } catch (err) {
        // Show error notification
        error('Failed to submit form. Please try again.');
        console.error('Form submission error:', err);
      }
    }
  });
  
  // Example API request
  const dataRequest = useApiRequest(
    async () => {
      // This would be an actual API call in a real component
      // Simulating API call with timeout
      return new Promise((resolve) => {
        setTimeout(() => {
          resolve({
            data: [{ id: 1, name: 'Item 1' }, { id: 2, name: 'Item 2' }],
            totalCount: 2
          });
        }, 1000);
      });
    },
    [],
    {
      onSuccess: (data) => {
        success('Data loaded successfully');
      },
      onError: (err) => {
        error(`Failed to load data: ${err.message}`);
      }
    }
  );
  
  // Example table data
  const tableData = useTableData({
    defaultSortBy: 'id',
    defaultSortDirection: 'asc',
    fetchDataFn: async (params) => {
      // This would call an actual API with the params
      console.log('Fetching table data with params:', params);
      
      // Simulating API response
      return {
        data: [
          { id: 1, name: 'Record 1', category: 'A' },
          { id: 2, name: 'Record 2', category: 'B' }
        ],
        totalCount: 2
      };
    }
  });
  
  // Handle search
  const handleSearch = (query: string) => {
    info(`Searching for: ${query}`);
    tableData.handleSearchQueryChange(query);
  };
  
  // Example of fetching data on mount
  useEffect(() => {
    dataRequest.request();
  }, []);
  
  // Example of fetching table data when needed
  useEffect(() => {
    tableData.fetchData();
  }, [tableData.page, tableData.sortBy, tableData.sortDirection, tableData.searchQuery]);
  
  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" gutterBottom>
        Optimized Component Example
      </Typography>
      
      <Box sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
        {/* Search Example */}
        <Box>
          <Paper sx={{ p: 2 }}>
            <Typography variant="h6" gutterBottom>
              Search Example (with debounced search)
            </Typography>
            <SearchField
              onSearch={handleSearch}
              debounceDelay={500}
              minSearchChars={2}
              placeholder="Search records..."
            />
          </Paper>
        </Box>
        
        {/* API Request and Form Example Side by Side */}
        <Box sx={{ display: 'flex', flexDirection: { xs: 'column', md: 'row' }, gap: 3 }}>
          {/* API Request Example */}
          <Box sx={{ flex: 1 }}>
            <Paper sx={{ p: 2, height: '100%' }}>
              <Typography variant="h6" gutterBottom>
                API Request Example
              </Typography>
              {dataRequest.isLoading ? (
                <Typography>Loading data...</Typography>
              ) : dataRequest.error ? (
                <Typography color="error">Error: {dataRequest.error.message}</Typography>
              ) : (
                <Box>
                  <Typography>Data loaded successfully!</Typography>
                  <Button 
                    variant="outlined" 
                    onClick={() => dataRequest.request()}
                    sx={{ mt: 2 }}
                  >
                    Refresh Data
                  </Button>
                </Box>
              )}
            </Paper>
          </Box>
          
          {/* Form Example */}
          <Box sx={{ flex: 1 }}>
            <Paper sx={{ p: 2, height: '100%' }}>
              <Typography variant="h6" gutterBottom>
                Form Example (with validation)
              </Typography>
              <form onSubmit={(e) => {
                e.preventDefault();
                exampleForm.handleSubmit();
              }}>
                <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
                  <Box>
                    <TextField
                      label="Title"
                      fullWidth
                      {...exampleForm.getFieldProps('title')}
                      error={!!exampleForm.touched.title && !!exampleForm.errors.title}
                      helperText={exampleForm.touched.title ? exampleForm.errors.title : ''}
                    />
                  </Box>
                  <Box>
                    <TextField
                      label="Description"
                      fullWidth
                      multiline
                      rows={3}
                      {...exampleForm.getFieldProps('description')}
                      error={!!exampleForm.touched.description && !!exampleForm.errors.description}
                      helperText={exampleForm.touched.description ? exampleForm.errors.description : ''}
                    />
                  </Box>
                  <Box>
                    <Button
                      type="submit"
                      variant="contained"
                      disabled={exampleForm.isSubmitting}
                    >
                      {exampleForm.isSubmitting ? 'Submitting...' : 'Submit Form'}
                    </Button>
                  </Box>
                </Box>
              </form>
            </Paper>
          </Box>
        </Box>
        
        {/* Notification Examples */}
        <Box>
          <Paper sx={{ p: 2 }}>
            <Typography variant="h6" gutterBottom>
              Notification Examples
            </Typography>
            <Box sx={{ display: 'flex', gap: 2, flexWrap: 'wrap' }}>
              <Button 
                variant="outlined" 
                color="success"
                onClick={() => success('Operation completed successfully!')}
              >
                Success Notification
              </Button>
              <Button 
                variant="outlined" 
                color="error"
                onClick={() => error('An error occurred while processing your request.')}
              >
                Error Notification
              </Button>
              <Button 
                variant="outlined" 
                color="warning"
                onClick={() => info('Information: System maintenance scheduled for tonight.')}
              >
                Info Notification
              </Button>
              <Button 
                variant="outlined" 
                onClick={() => success('Action completed!', { 
                  action: { 
                    label: 'Undo', 
                    onClick: () => info('Undo action triggered') 
                  }
                })}
              >
                Notification with Action
              </Button>
            </Box>
          </Paper>
        </Box>
      </Box>
      
      {/* Documentation */}
      <Box sx={{ mt: 4 }}>
        <Typography variant="h5" gutterBottom>
          Component Documentation
        </Typography>
        <Typography variant="body1" paragraph>
          This component demonstrates the use of our optimized hooks and components:
        </Typography>
        <Typography component="ul">
          <li><strong>useNotificationContext</strong>: For showing success, error, and info messages</li>
          <li><strong>useApiRequest</strong>: For making API calls with loading, success, and error states</li>
          <li><strong>useTableData</strong>: For managing table data with pagination, sorting, and filtering</li>
          <li><strong>useForm</strong>: For form state management with validation</li>
          <li><strong>SearchField</strong>: For debounced search functionality</li>
        </Typography>
        <Typography variant="body1" sx={{ mt: 2 }}>
          These optimized components and hooks make it easier to build consistent, 
          performant, and maintainable React components across the application.
        </Typography>
      </Box>
    </Box>
  );
};

export default OptimizedComponent;

import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  Paper,
  TableContainer,
  Table,
  TableHead,
  TableBody,
  TableRow,
  TableCell,
  TablePagination,
  Chip,
  Alert,
  CircularProgress
} from '@mui/material';
import { format } from 'date-fns';
import { inventoryService } from '../../services/api';
import type { InventoryAuditLog as IAuditLog } from '../../services/api/inventoryService';

interface InventoryAuditLogProps {
  inventoryId: string;
  title?: string;
  maxHeight?: number | string;
}

const InventoryAuditLog: React.FC<InventoryAuditLogProps> = ({
  inventoryId,
  title = 'Inventory Audit Log',
  maxHeight
}) => {
  const [logs, setLogs] = useState<IAuditLog[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [totalRows, setTotalRows] = useState(0);

  useEffect(() => {
    fetchAuditLogs();
  }, [inventoryId, page, rowsPerPage]);

  const fetchAuditLogs = async () => {
    if (!inventoryId) return;
    
    setLoading(true);
    setError(null);

    try {
      const response = await inventoryService.getInventoryAuditLog(inventoryId, {
        page: page + 1, // Convert to 1-based pagination for API
        limit: rowsPerPage,
      });
      
      setLogs(response.data);
      setTotalRows(response.pagination?.total || response.data.length);
    } catch (err: any) {
      console.error('Error fetching inventory audit logs:', err);
      setError(err.message || 'Failed to load audit logs');
    } finally {
      setLoading(false);
    }
  };

  const handleChangePage = (_: unknown, newPage: number) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event: React.ChangeEvent<HTMLInputElement>) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  const formatDate = (dateString: string) => {
    try {
      return format(new Date(dateString), 'MMM dd, yyyy HH:mm:ss');
    } catch (e) {
      return dateString;
    }
  };

  const getChangeTypeChip = (changeType: string) => {
    let color: 'default' | 'primary' | 'secondary' | 'error' | 'info' | 'success' | 'warning' = 'default';
    let label = changeType;
    
    switch (changeType) {
      case 'addition':
        color = 'success';
        label = 'Addition';
        break;
      case 'reduction':
        color = 'error';
        label = 'Reduction';
        break;
      case 'transfer_in':
        color = 'info';
        label = 'Transfer In';
        break;
      case 'transfer_out':
        color = 'warning';
        label = 'Transfer Out';
        break;
      case 'adjustment':
        color = 'secondary';
        label = 'Adjustment';
        break;
      default:
        color = 'default';
    }
    
    return <Chip label={label} color={color} size="small" />;
  };

  const getQuantityChangeDisplay = (previousQuantity: number, newQuantity: number) => {
    const difference = newQuantity - previousQuantity;
    const color = difference > 0 ? 'success.main' : difference < 0 ? 'error.main' : 'text.secondary';
    
    return (
      <Box sx={{ display: 'flex', alignItems: 'center' }}>
        <Typography variant="body2" component="span">
          {previousQuantity}  {newQuantity}
        </Typography>
        <Typography 
          variant="body2" 
          component="span"
          sx={{ 
            color, 
            ml: 1,
            fontWeight: 'bold'
          }}
        >
          ({difference > 0 ? '+' : ''}{difference})
        </Typography>
      </Box>
    );
  };

  return (
    <Paper 
      sx={{ 
        width: '100%', 
        overflow: 'hidden',
        maxHeight: maxHeight ? maxHeight : 'auto'
      }}
      elevation={1}
    >
      <Box sx={{ p: 2, borderBottom: '1px solid #e0e0e0' }}>
        <Typography variant="h6" component="div">
          {title}
        </Typography>
      </Box>

      {loading && logs.length === 0 ? (
        <Box sx={{ display: 'flex', justifyContent: 'center', p: 3 }}>
          <CircularProgress />
        </Box>
      ) : error ? (
        <Box sx={{ p: 2 }}>
          <Alert severity="error">{error}</Alert>
        </Box>
      ) : (
        <Box sx={{ display: 'flex', flexDirection: 'column', height: '100%' }}>
          <TableContainer sx={{ flexGrow: 1, maxHeight: maxHeight ? `calc(${maxHeight}px - 120px)` : undefined }}>
            <Table stickyHeader>
              <TableHead>
                <TableRow>
                  <TableCell>Date & Time</TableCell>
                  <TableCell>Change Type</TableCell>
                  <TableCell>Quantity</TableCell>
                  <TableCell>Reference</TableCell>
                  <TableCell>User</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {logs.length > 0 ? (
                  logs.map((log) => (
                    <TableRow key={log.id} hover>
                      <TableCell>{formatDate(log.created_at)}</TableCell>
                      <TableCell>{getChangeTypeChip(log.change_type)}</TableCell>
                      <TableCell>
                        {getQuantityChangeDisplay(log.previous_quantity, log.new_quantity)}
                      </TableCell>
                      <TableCell>{log.reference_id || ''}</TableCell>
                      <TableCell>{log.created_by}</TableCell>
                    </TableRow>
                  ))
                ) : (
                  <TableRow>
                    <TableCell colSpan={5} align="center">
                      <Typography variant="body2" color="text.secondary">
                        No audit logs found for this inventory item
                      </Typography>
                    </TableCell>
                  </TableRow>
                )}
              </TableBody>
            </Table>
          </TableContainer>

          <TablePagination
            rowsPerPageOptions={[5, 10, 25]}
            component="div"
            count={totalRows}
            rowsPerPage={rowsPerPage}
            page={page}
            onPageChange={handleChangePage}
            onRowsPerPageChange={handleChangeRowsPerPage}
          />
        </Box>
      )}
    </Paper>
  );
};

export default InventoryAuditLog;

import React, { useEffect, useState } from 'react';
import {
  Box,
  Typography,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Button,
  TextField,
  InputAdornment,
  IconButton,
  Chip,
  TablePagination,
  useTheme as useMuiTheme
} from '@mui/material';
import { 
  Search as SearchIcon,
  FilterList as FilterIcon,
  Add as AddIcon,
  Refresh as RefreshIcon
} from '@mui/icons-material';
import { useTheme } from '../../contexts/ThemeContext';
import { useInventory } from '../../hooks/useInventory';
import InventoryAdjustmentModal from './InventoryAdjustmentModal';

interface InventoryItem {
  id: string;
  product_id: string;
  product_name: string;
  warehouse_id: string;
  warehouse_name: string;
  quantity: number;
  unit: string;
  batch_id?: string;
  batch_number?: string;
  last_updated: string;
}

const InventoryManagement: React.FC = () => {
  const muiTheme = useMuiTheme();
  const { theme: whitelabelTheme } = useTheme();
  const { getInventoryItems, loading } = useInventory();

  const [inventoryItems, setInventoryItems] = useState<InventoryItem[]>([]);
  const [filteredItems, setFilteredItems] = useState<InventoryItem[]>([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [adjustModalOpen, setAdjustModalOpen] = useState(false);
  const [selectedItem, setSelectedItem] = useState<InventoryItem | null>(null);
  
  // Pagination
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);

  useEffect(() => {
    loadInventory();
  }, []);

  useEffect(() => {
    if (searchTerm) {
      const lowercasedTerm = searchTerm.toLowerCase();
      const filtered = inventoryItems.filter(item => 
        item.product_name.toLowerCase().includes(lowercasedTerm) ||
        item.warehouse_name.toLowerCase().includes(lowercasedTerm) ||
        (item.batch_number && item.batch_number.toLowerCase().includes(lowercasedTerm))
      );
      setFilteredItems(filtered);
    } else {
      setFilteredItems(inventoryItems);
    }
    setPage(0);
  }, [searchTerm, inventoryItems]);

  const loadInventory = async () => {
    try {
      const data = await getInventoryItems();
      setInventoryItems(data);
      setFilteredItems(data);
    } catch (error) {
      console.error("Failed to load inventory:", error);
    }
  };

  const handleSearchChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setSearchTerm(event.target.value);
  };

  const handleChangePage = (event: unknown, newPage: number) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event: React.ChangeEvent<HTMLInputElement>) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  const openAdjustModal = (item: InventoryItem) => {
    setSelectedItem(item);
    setAdjustModalOpen(true);
  };

  const handleAdjustmentSuccess = () => {
    setAdjustModalOpen(false);
    loadInventory();
  };

  const getStockLevelChip = (quantity: number) => {
    let color = 'success';
    let label = 'Good Stock';
    
    if (quantity <= 0) {
      color = 'error';
      label = 'Out of Stock';
    } else if (quantity < 10) {
      color = 'warning';
      label = 'Low Stock';
    }
    
    return (
      <Chip 
        label={label} 
        color={color as 'success' | 'error' | 'warning'}
        size="small"
      />
    );
  };

  // Custom styles based on the whitelabel theme
  const headerStyles = {
    backgroundColor: whitelabelTheme?.primaryColor || muiTheme.palette.primary.main,
    color: '#fff'
  };

  return (
    <Box>
      <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
        <Typography variant="h5" component="h2" fontWeight="bold">
          Inventory Management
        </Typography>
        <Box>
          <Button
            variant="outlined"
            startIcon={<RefreshIcon />}
            onClick={loadInventory}
            sx={{ mr: 1 }}
          >
            Refresh
          </Button>
          <Button
            variant="contained"
            startIcon={<AddIcon />}
            sx={{
              backgroundColor: whitelabelTheme?.primaryColor || muiTheme.palette.primary.main,
              borderRadius: whitelabelTheme?.buttonRadius !== undefined 
                ? `${whitelabelTheme.buttonRadius}px` 
                : undefined,
            }}
          >
            Add Inventory
          </Button>
        </Box>
      </Box>

      <Paper elevation={2} sx={{ mb: 3, p: 2 }}>
        <Box display="flex" alignItems="center" mb={2}>
          <TextField
            variant="outlined"
            placeholder="Search inventory..."
            fullWidth
            value={searchTerm}
            onChange={handleSearchChange}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <SearchIcon />
                </InputAdornment>
              ),
            }}
            size="small"
          />
          <IconButton sx={{ ml: 1 }}>
            <FilterIcon />
          </IconButton>
        </Box>

        <TableContainer>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell sx={headerStyles}>Product</TableCell>
                <TableCell sx={headerStyles}>Warehouse</TableCell>
                <TableCell sx={headerStyles}>Quantity</TableCell>
                <TableCell sx={headerStyles}>Unit</TableCell>
                <TableCell sx={headerStyles}>Batch</TableCell>
                <TableCell sx={headerStyles}>Status</TableCell>
                <TableCell sx={headerStyles}>Last Updated</TableCell>
                <TableCell sx={headerStyles}>Actions</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {loading ? (
                <TableRow>
                  <TableCell colSpan={8} align="center">
                    <Typography>Loading inventory data...</Typography>
                  </TableCell>
                </TableRow>
              ) : filteredItems.length === 0 ? (
                <TableRow>
                  <TableCell colSpan={8} align="center">
                    <Typography>No inventory items found.</Typography>
                  </TableCell>
                </TableRow>
              ) : (
                filteredItems
                  .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                  .map((item) => (
                    <TableRow key={item.id}>
                      <TableCell>{item.product_name}</TableCell>
                      <TableCell>{item.warehouse_name}</TableCell>
                      <TableCell align="right">{item.quantity.toLocaleString()}</TableCell>
                      <TableCell>{item.unit}</TableCell>
                      <TableCell>{item.batch_number || 'N/A'}</TableCell>
                      <TableCell>{getStockLevelChip(item.quantity)}</TableCell>
                      <TableCell>{new Date(item.last_updated).toLocaleDateString()}</TableCell>
                      <TableCell>
                        <Button
                          size="small"
                          variant="contained"
                          onClick={() => openAdjustModal(item)}
                          sx={{
                            backgroundColor: whitelabelTheme?.secondaryColor || muiTheme.palette.secondary.main,
                            borderRadius: whitelabelTheme?.buttonRadius !== undefined 
                              ? `${whitelabelTheme.buttonRadius}px` 
                              : undefined,
                          }}
                        >
                          Adjust
                        </Button>
                      </TableCell>
                    </TableRow>
                  ))
              )}
            </TableBody>
          </Table>
        </TableContainer>
        
        <TablePagination
          rowsPerPageOptions={[5, 10, 25, 50]}
          component="div"
          count={filteredItems.length}
          rowsPerPage={rowsPerPage}
          page={page}
          onPageChange={handleChangePage}
          onRowsPerPageChange={handleChangeRowsPerPage}
        />
      </Paper>

      {selectedItem && (
        <InventoryAdjustmentModal
          open={adjustModalOpen}
          onClose={() => setAdjustModalOpen(false)}
          item={selectedItem}
          onSuccess={handleAdjustmentSuccess}
        />
      )}
    </Box>
  );
};

export default InventoryManagement;

import React, { useState, useEffect } from 'react';
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  FormHelperText,
  Alert,
  Box,
  Typography,
  CircularProgress,
  SelectChangeEvent
} from '@mui/material';
import { inventoryService } from '../../services/api';

interface InventoryTransferModalProps {
  open: boolean;
  onClose: () => void;
  productId: string;
  productName: string;
  companyId: string;
  onTransferComplete?: () => void;
}

const InventoryTransferModal: React.FC<InventoryTransferModalProps> = ({
  open,
  onClose,
  productId,
  productName,
  companyId,
  onTransferComplete
}) => {
  const [sourceWarehouseId, setSourceWarehouseId] = useState('');
  const [destinationWarehouseId, setDestinationWarehouseId] = useState('');
  const [quantity, setQuantity] = useState('');
  const [notes, setNotes] = useState('');
  const [warehouses, setWarehouses] = useState<{ id: string; name: string }[]>([]);
  const [errors, setErrors] = useState<Record<string, string>>({});
  const [loading, setLoading] = useState(false);
  const [fetchingWarehouses, setFetchingWarehouses] = useState(false);
  const [apiError, setApiError] = useState<string | null>(null);
  const [sourceQuantityAvailable, setSourceQuantityAvailable] = useState<number | null>(null);
  const [isConnectionError, setIsConnectionError] = useState(false);

  useEffect(() => {
    if (open) {
      fetchWarehouses();
    }
  }, [open, companyId]);

  useEffect(() => {
    if (sourceWarehouseId) {
      fetchAvailableQuantity();
    } else {
      setSourceQuantityAvailable(null);
    }
  }, [sourceWarehouseId, productId]);
  const fetchWarehouses = async () => {
    setFetchingWarehouses(true);
    setApiError(null);
    setIsConnectionError(false);
    
    try {
      // Ensure companyId is a string before making the API call
      if (!companyId || typeof companyId !== 'string') {
        throw new Error('Invalid company ID');
      }
      
      const response = await inventoryService.getWarehouses(companyId);
      console.log('Warehouse API response:', response);
      
      // Handle different potential response structures
      let warehousesList: Array<any> = [];
      
      // If response.data is an array
      if (Array.isArray(response.data)) {
        warehousesList = response.data;
      } 
      // If response.data contains a warehouses property that is an array
      else if (response.data && typeof response.data === 'object' && 'warehouses' in response.data && 
              Array.isArray((response.data as any).warehouses)) {
        warehousesList = (response.data as any).warehouses;
      }
      // If response.data.data contains the warehouses array
      else if (response.data && typeof response.data === 'object' && 'data' in response.data && 
              Array.isArray((response.data as any).data)) {
        warehousesList = (response.data as any).data;
      }
      // Default to empty array if no matching structure is found
      else {
        console.error('Unexpected API response format:', response);
        warehousesList = [];
      }
      
      setWarehouses(warehousesList);
    } catch (error: any) {
      console.error('Error fetching warehouses:', error);
      
      // Check if it's a connection error
      if (error.message?.includes('Connection') || error.response?.status === 500) {
        setIsConnectionError(true);
        setApiError('Database connection error. Please try again later.');
      } else {
        setApiError(`Failed to load warehouses: ${error.message || 'Unknown error'}`);
      }
    } finally {
      setFetchingWarehouses(false);
    }
  };

  const fetchAvailableQuantity = async () => {
    try {
      // This is a simplified approach. In a real implementation, you'd need an endpoint 
      // to get the available quantity for a specific product in a specific warehouse
      const response = await inventoryService.getInventory({
        companyId,
        warehouseId: sourceWarehouseId
      });
      
      const productInventory = response.data.find(item => item.product_id === productId);
      setSourceQuantityAvailable(productInventory ? productInventory.quantity : 0);
    } catch (error) {
      console.error('Error fetching available quantity:', error);
      // We don't set API error here to avoid blocking the modal
    }
  };

  const handleSourceWarehouseChange = (event: SelectChangeEvent) => {
    setSourceWarehouseId(event.target.value);
    if (event.target.value === destinationWarehouseId) {
      setDestinationWarehouseId('');
    }
    setErrors({ ...errors, sourceWarehouse: '' });
  };

  const handleDestinationWarehouseChange = (event: SelectChangeEvent) => {
    setDestinationWarehouseId(event.target.value);
    if (event.target.value === sourceWarehouseId) {
      setErrors({ ...errors, destinationWarehouse: 'Source and destination warehouses cannot be the same' });
    } else {
      setErrors({ ...errors, destinationWarehouse: '' });
    }
  };

  const validateForm = (): boolean => {
    const newErrors: Record<string, string> = {};
    
    if (!sourceWarehouseId) {
      newErrors.sourceWarehouse = 'Source warehouse is required';
    }
    
    if (!destinationWarehouseId) {
      newErrors.destinationWarehouse = 'Destination warehouse is required';
    }
    
    if (sourceWarehouseId === destinationWarehouseId) {
      newErrors.destinationWarehouse = 'Source and destination warehouses cannot be the same';
    }
    
    if (!quantity) {
      newErrors.quantity = 'Transfer quantity is required';
    } else if (isNaN(Number(quantity)) || Number(quantity) <= 0) {
      newErrors.quantity = 'Quantity must be a positive number';
    } else if (sourceQuantityAvailable !== null && Number(quantity) > sourceQuantityAvailable) {
      newErrors.quantity = `Only ${sourceQuantityAvailable} units available for transfer`;
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleTransfer = async () => {
    if (!validateForm()) return;
    
    setLoading(true);
    setApiError(null);
    
    try {
      await inventoryService.transferInventory({
        product_id: productId,
        source_warehouse_id: sourceWarehouseId,
        destination_warehouse_id: destinationWarehouseId,
        quantity: Number(quantity),
        notes: notes.trim() || undefined
      });
      
      // Reset form
      setSourceWarehouseId('');
      setDestinationWarehouseId('');
      setQuantity('');
      setNotes('');
      
      if (onTransferComplete) {
        onTransferComplete();
      }
      
      onClose();
    } catch (error: any) {
      console.error('Error transferring inventory:', error);
      setApiError(error.message || 'Failed to transfer inventory. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleClose = () => {
    if (!loading) {
      onClose();
    }
  };

  return (
    <Dialog open={open} onClose={handleClose} maxWidth="sm" fullWidth>
      <DialogTitle>Transfer Inventory: {productName}</DialogTitle>
      <DialogContent>
        {apiError && (
          <Alert severity="error" sx={{ mb: 2 }}>
            {apiError}
          </Alert>
        )}
        
        <Box sx={{ mt: 2 }}>
          {fetchingWarehouses ? (
            <Box sx={{ display: 'flex', justifyContent: 'center', py: 4 }}>
              <CircularProgress />
            </Box>
          ) : (
            <Box component="form" noValidate sx={{ mt: 1 }}>
              <FormControl fullWidth margin="normal" error={!!errors.sourceWarehouse}>
                <InputLabel id="source-warehouse-label">Source Warehouse</InputLabel>
                <Select
                  labelId="source-warehouse-label"
                  value={sourceWarehouseId}
                  onChange={handleSourceWarehouseChange}
                  label="Source Warehouse"
                  disabled={loading}
                >                  {Array.isArray(warehouses) ? warehouses.map((warehouse) => (
                    <MenuItem key={warehouse.id} value={warehouse.id}>
                      {warehouse.name}
                    </MenuItem>
                  )) : (
                    <MenuItem value="">No warehouses available</MenuItem>
                  )}
                </Select>
                {errors.sourceWarehouse && <FormHelperText>{errors.sourceWarehouse}</FormHelperText>}
              </FormControl>

              {sourceQuantityAvailable !== null && (
                <Box sx={{ mt: 1, mb: 2 }}>
                  <Typography variant="body2" color="text.secondary">
                    Available quantity: <strong>{sourceQuantityAvailable}</strong> units
                  </Typography>
                </Box>
              )}

              <FormControl fullWidth margin="normal" error={!!errors.destinationWarehouse}>
                <InputLabel id="destination-warehouse-label">Destination Warehouse</InputLabel>
                <Select
                  labelId="destination-warehouse-label"
                  value={destinationWarehouseId}
                  onChange={handleDestinationWarehouseChange}
                  label="Destination Warehouse"
                  disabled={loading}                >
                  {Array.isArray(warehouses) ? warehouses
                    .filter(w => w.id !== sourceWarehouseId)
                    .map((warehouse) => (
                      <MenuItem key={warehouse.id} value={warehouse.id}>
                        {warehouse.name}
                      </MenuItem>
                    )) : (
                      <MenuItem value="">No warehouses available</MenuItem>
                    )
                  }
                </Select>
                {errors.destinationWarehouse && <FormHelperText>{errors.destinationWarehouse}</FormHelperText>}
              </FormControl>

              <TextField
                margin="normal"
                fullWidth
                label="Quantity to Transfer"
                type="number"
                value={quantity}
                onChange={(e) => {
                  setQuantity(e.target.value);
                  if (errors.quantity) {
                    setErrors({ ...errors, quantity: '' });
                  }
                }}
                disabled={loading}
                error={!!errors.quantity}
                helperText={errors.quantity}
                inputProps={{ min: 1 }}
              />

              <TextField
                margin="normal"
                fullWidth
                label="Notes (Optional)"
                multiline
                rows={3}
                value={notes}
                onChange={(e) => setNotes(e.target.value)}
                disabled={loading}
              />
            </Box>
          )}
        </Box>
      </DialogContent>
      <DialogActions>
        <Button onClick={handleClose} disabled={loading}>
          Cancel
        </Button>
        <Button 
          onClick={handleTransfer} 
          variant="contained" 
          color="primary" 
          disabled={loading || fetchingWarehouses}
        >
          {loading ? <CircularProgress size={24} /> : 'Transfer'}
        </Button>
      </DialogActions>
    </Dialog>
  );
};

