import React, { useState, useEffect } from 'react';
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  TextField,
  FormControl,
  InputLabel,
  Select,  MenuItem,
  Box,
  Typography,
  FormHelperText,
  InputAdornment,
  CircularProgress,
  Tooltip,
  IconButton,
  Autocomplete
} from '@mui/material';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { usePayment } from '../../hooks/usePayment';
import { useCustomer } from '../../hooks/useCustomer';
import { Customer } from '../../services/api/customerService';
import { PaymentMethod, PaymentType, CreatePaymentRequest } from '../../services/api/paymentService';
import { format } from 'date-fns';
import { generateTransactionId, generateOrderId, generateInvoiceId } from '../../utils/idGenerator';
import { Refresh as RefreshIcon } from '@mui/icons-material';

interface CreatePaymentModalProps {
  open: boolean;
  onClose: () => void;
  onPaymentCreated: () => void;
}

const CreatePaymentModal: React.FC<CreatePaymentModalProps> = ({ open, onClose, onPaymentCreated }) => {
  const { createPayment, loading, error } = usePayment();
  const { searchCustomers } = useCustomer();
  const [formErrors, setFormErrors] = useState<Record<string, string>>({});
  const [customerOptions, setCustomerOptions] = useState<Customer[]>([]);
  const [customerSearchTerm, setCustomerSearchTerm] = useState('');
  const [selectedCustomer, setSelectedCustomer] = useState<Customer | null>(null);
    // Form state
  const [formData, setFormData] = useState<Partial<CreatePaymentRequest>>({
    transactionId: generateTransactionId(),
    customerId: '',
    amount: 0,
    paymentMethod: 'bank_transfer' as PaymentMethod,
    paymentType: 'invoice_payment' as PaymentType,
    paymentDate: format(new Date(), 'yyyy-MM-dd'),
    orderId: '',
    invoiceId: ''
  });

  // Reset form when modal is opened
  useEffect(() => {
    if (open) {
      setFormData({
        transactionId: generateTransactionId(),
        customerId: '',
        amount: 0,
        paymentMethod: 'bank_transfer' as PaymentMethod,
        paymentType: 'invoice_payment' as PaymentType,
        paymentDate: format(new Date(), 'yyyy-MM-dd'),
        orderId: '',
        invoiceId: ''
      });
      setFormErrors({});
    }
  }, [open]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | { name?: string; value: unknown }>) => {
    const { name, value } = e.target;
    if (name) {
      setFormData(prev => ({ ...prev, [name]: value }));
      
      // Clear error for this field when changed
      if (formErrors[name]) {
        setFormErrors(prev => {
          const newErrors = { ...prev };
          delete newErrors[name];
          return newErrors;
        });
      }
    }
  };

  const handleDateChange = (date: Date | null) => {
    if (date) {
      setFormData(prev => ({ ...prev, paymentDate: format(date, 'yyyy-MM-dd') }));
      
      // Clear date error if exists
      if (formErrors.paymentDate) {
        setFormErrors(prev => {
          const newErrors = { ...prev };
          delete newErrors.paymentDate;
          return newErrors;
        });
      }
    }
  };
  const validateForm = (): boolean => {
    const errors: Record<string, string> = {};
    
    if (!formData.transactionId) {
      // Generate a transaction ID if it's missing
      setFormData(prev => ({ ...prev, transactionId: generateTransactionId() }));
    }
    
    if (!formData.customerId) {
      errors.customerId = 'Customer ID is required';
    }
    
    if (!formData.amount || formData.amount <= 0) {
      errors.amount = 'Amount must be greater than zero';
    }
    
    if (!formData.paymentDate) {
      errors.paymentDate = 'Payment date is required';
    }
    
    // Auto-generate IDs based on payment type if not provided
    if (formData.paymentType === 'order_payment' && !formData.orderId) {
      setFormData(prev => ({ ...prev, orderId: generateOrderId() }));
    }
    
    if (formData.paymentType === 'invoice_payment' && !formData.invoiceId) {
      setFormData(prev => ({ ...prev, invoiceId: generateInvoiceId() }));
    }
    
    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleSubmit = async () => {
    if (!validateForm()) return;
    
    const paymentData = formData as CreatePaymentRequest;
    const result = await createPayment(paymentData);
    
    if (result) {
      onPaymentCreated();
    }
  };

  return (
    <Dialog open={open} onClose={loading ? undefined : onClose} maxWidth="md" fullWidth>
      <DialogTitle>Create New Payment</DialogTitle>
      <DialogContent>
        {error && (
          <Typography color="error" variant="body2" sx={{ mb: 2 }}>
            {error}
          </Typography>
        )}
          <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 2, mt: 1 }}>          <Box sx={{ width: { xs: '100%', sm: '48%' } }}>
            <TextField
              label="Transaction ID"
              name="transactionId"
              value={formData.transactionId || ''}
              onChange={handleChange}
              fullWidth
              required
              error={!!formErrors.transactionId}
              helperText={formErrors.transactionId || "Auto-generated transaction ID"}
              InputProps={{
                endAdornment: (
                  <InputAdornment position="end">
                    <Tooltip title="Generate new transaction ID">
                      <IconButton 
                        onClick={() => setFormData(prev => ({ ...prev, transactionId: generateTransactionId() }))}
                        edge="end"
                        size="small"
                      >
                        <RefreshIcon fontSize="small" />
                      </IconButton>
                    </Tooltip>
                  </InputAdornment>
                ),
              }}
            />
          </Box>
          
          <Box sx={{ width: { xs: '100%', sm: '48%' } }}>
            <TextField
              label="Customer ID"
              name="customerId"
              value={formData.customerId || ''}
              onChange={handleChange}
              fullWidth
              required
              error={!!formErrors.customerId}
              helperText={formErrors.customerId}
            />
          </Box>
          
          <Box sx={{ width: { xs: '100%', sm: '48%' } }}>
            <TextField
              label="Amount"
              name="amount"
              type="number"
              value={formData.amount || ''}
              onChange={handleChange}
              fullWidth
              required
              InputProps={{
                startAdornment: <InputAdornment position="start">$</InputAdornment>,
              }}
              error={!!formErrors.amount}
              helperText={formErrors.amount}
            />
          </Box>
          
          <Box sx={{ width: { xs: '100%', sm: '48%' } }}>
            <LocalizationProvider dateAdapter={AdapterDateFns}>
              <DatePicker
                label="Payment Date"
                value={formData.paymentDate ? new Date(formData.paymentDate) : null}
                onChange={handleDateChange}
                slotProps={{ 
                  textField: { 
                    fullWidth: true,
                    required: true,
                    error: !!formErrors.paymentDate,
                    helperText: formErrors.paymentDate
                  }
                }}
              />
            </LocalizationProvider>
          </Box>
          
          <Box sx={{ width: { xs: '100%', sm: '48%' } }}>
            <FormControl fullWidth required>
              <InputLabel>Payment Method</InputLabel>
              <Select
                name="paymentMethod"
                value={formData.paymentMethod || ''}
                onChange={handleChange as any}
                label="Payment Method"
              >
                <MenuItem value="credit_card">Credit Card</MenuItem>
                <MenuItem value="bank_transfer">Bank Transfer</MenuItem>
                <MenuItem value="cash">Cash</MenuItem>
                <MenuItem value="check">Check</MenuItem>
                <MenuItem value="paypal">PayPal</MenuItem>
                <MenuItem value="stripe">Stripe</MenuItem>
                <MenuItem value="other">Other</MenuItem>
              </Select>
            </FormControl>
          </Box>
          
          <Box sx={{ width: { xs: '100%', sm: '48%' } }}>
            <FormControl fullWidth required>
              <InputLabel>Payment Type</InputLabel>
              <Select
                name="paymentType"
                value={formData.paymentType || ''}
                onChange={handleChange as any}
                label="Payment Type"
              >
                <MenuItem value="order_payment">Order Payment</MenuItem>
                <MenuItem value="invoice_payment">Invoice Payment</MenuItem>
                <MenuItem value="refund">Refund</MenuItem>
                <MenuItem value="advance_payment">Advance Payment</MenuItem>
                <MenuItem value="adjustment">Adjustment</MenuItem>
              </Select>
            </FormControl>
          </Box>
            <Box sx={{ width: { xs: '100%', sm: '48%' } }}>
            <TextField
              label="Order ID (Optional)"
              name="orderId"
              value={formData.orderId || ''}
              onChange={handleChange}
              fullWidth
              helperText="Click refresh to generate a new Order ID"
              InputProps={{
                endAdornment: (
                  <InputAdornment position="end">
                    <Tooltip title="Generate new order ID">
                      <IconButton 
                        onClick={() => setFormData(prev => ({ ...prev, orderId: generateOrderId() }))}
                        edge="end"
                        size="small"
                      >
                        <RefreshIcon fontSize="small" />
                      </IconButton>
                    </Tooltip>
                  </InputAdornment>
                ),
              }}
            />
          </Box>
          
          <Box sx={{ width: { xs: '100%', sm: '48%' } }}>
            <TextField
              label="Invoice ID (Optional)"
              name="invoiceId"
              value={formData.invoiceId || ''}
              onChange={handleChange}
              fullWidth
              helperText="Click refresh to generate a new Invoice ID"
              InputProps={{
                endAdornment: (
                  <InputAdornment position="end">
                    <Tooltip title="Generate new invoice ID">
                      <IconButton 
                        onClick={() => setFormData(prev => ({ ...prev, invoiceId: generateInvoiceId() }))}
                        edge="end"
                        size="small"
                      >
                        <RefreshIcon fontSize="small" />
                      </IconButton>
                    </Tooltip>
                  </InputAdornment>
                ),
              }}
            />
          </Box>
          
          <Box sx={{ width: '100%' }}>
            <TextField
              label="External Reference ID (Optional)"
              name="externalReferenceId"
              value={formData.externalReferenceId || ''}            onChange={handleChange}
              fullWidth
            />
          </Box>
          
          <Box sx={{ width: '100%' }}>
            <TextField
              label="Notes (Optional)"
              name="notes"
              value={formData.notes || ''}
              onChange={handleChange}
              fullWidth
              multiline
              rows={3}
            />
          </Box>
        </Box>
      </DialogContent>
      <DialogActions sx={{ p: 2 }}>
        <Button onClick={onClose} disabled={loading}>
          Cancel
        </Button>
        <Button 
          onClick={handleSubmit} 
          variant="contained" 
          color="primary" 
          disabled={loading}
          startIcon={loading ? <CircularProgress size={20} /> : undefined}
        >
          {loading ? 'Creating...' : 'Create Payment'}
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default CreatePaymentModal;

import React, { useEffect, useState } from 'react';
import {
  Typography,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,  Button,
  TextField,
  InputAdornment,
  IconButton,
  Chip,
  TablePagination,
  useTheme as useMuiTheme,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Box,
  Tooltip
} from '@mui/material';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { 
  Search as SearchIcon,
  FilterList as FilterIcon,
  Add as AddIcon,
  Refresh as RefreshIcon,
  Edit as EditIcon,
  Delete as DeleteIcon,
  Check as CheckIcon
} from '@mui/icons-material';
import { useTheme } from '../../contexts/ThemeContext';
import { usePayment } from '../../hooks/usePayment';
import { useCustomer } from '../../hooks/useCustomer';
import { Payment, PaymentMethod, PaymentType, ReconciliationStatus } from '../../services/api/paymentService';
import { Customer } from '../../services/api/customerService';
import CreatePaymentModal from './CreatePaymentModal';
import UpdatePaymentModal from './UpdatePaymentModal';
import ReconcilePaymentModal from './ReconcilePaymentModal';
import { format } from 'date-fns';
import { useSnackbar } from 'notistack';

const PaymentManagement: React.FC = () => {
  const { theme: whitelabelTheme } = useTheme();
  const { 
    loading, 
    error, 
    getPayments, 
    deletePayment 
  } = usePayment();
  const {
    getCustomersByIds,
    searchCustomers
  } = useCustomer();
  const { enqueueSnackbar } = useSnackbar();

  // State variables
  const [payments, setPayments] = useState<Payment[]>([]);
  const [totalItems, setTotalItems] = useState(0);
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [searchTerm, setSearchTerm] = useState('');
  const [customerMap, setCustomerMap] = useState<Record<string, Customer>>({});
    // Filter states
  const [startDate, setStartDate] = useState<Date | null>(null);
  const [endDate, setEndDate] = useState<Date | null>(null);
  const [selectedPaymentMethod, setSelectedPaymentMethod] = useState<string>('');
  const [selectedStatus, setSelectedStatus] = useState<string>('');
  const [selectedCustomerId, setSelectedCustomerId] = useState('');
  const [selectedOrderId, setSelectedOrderId] = useState('');
  const [selectedInvoiceId, setSelectedInvoiceId] = useState('');
  const [customerOptions, setCustomerOptions] = useState<Customer[]>([]);
  const [customerSearchTerm, setCustomerSearchTerm] = useState('');

  // Modal states
  const [isCreateModalOpen, setIsCreateModalOpen] = useState(false);
  const [isUpdateModalOpen, setIsUpdateModalOpen] = useState(false);
  const [isReconcileModalOpen, setIsReconcileModalOpen] = useState(false);
  const [isDeleteConfirmOpen, setIsDeleteConfirmOpen] = useState(false);
  const [selectedPayment, setSelectedPayment] = useState<Payment | null>(null);

  // Fetch payments on initial load and when filters change
  useEffect(() => {
    fetchPayments();
    
    // Initialize customer filter options
    if (Object.keys(customerMap).length === 0) {
      // Load some initial customers for the filter dropdown
      searchCustomers({ limit: 10 }).then(result => {
        if (result && result.data.length > 0) {
          const initialCustomers = result.data.reduce((acc, customer) => {
            acc[customer.id] = customer;
            return acc;
          }, {} as Record<string, Customer>);
          setCustomerMap(prevMap => ({ ...prevMap, ...initialCustomers }));
        }
      });
    }
  }, [page, rowsPerPage, startDate, endDate, selectedPaymentMethod, selectedStatus, selectedCustomerId]);const fetchPayments = async () => {
    let filters: any = {
      page: page + 1, // API uses 1-based pagination
      limit: rowsPerPage
    };

    // Add filters if they are set
    if (startDate) filters.startDate = format(startDate, 'yyyy-MM-dd');
    if (endDate) filters.endDate = format(endDate, 'yyyy-MM-dd');
    if (selectedPaymentMethod) filters.paymentMethod = selectedPaymentMethod;
    if (selectedStatus) filters.reconciliationStatus = selectedStatus;
    if (selectedCustomerId) filters.customerId = selectedCustomerId;
    if (selectedOrderId) filters.orderId = selectedOrderId;
    if (selectedInvoiceId) filters.invoiceId = selectedInvoiceId;

    const result = await getPayments(filters);
      if (result) {
      // Extract all unique customer IDs
      const customerIds = Array.from(new Set(result.data.map(payment => payment.customerId)));
      
      // Fetch all customers in one batch request
      if (customerIds.length > 0) {
        const customers = await getCustomersByIds(customerIds) || {};
        setCustomerMap(customers);
        
        // Enhance payments with customer data
        const enhancedPayments = result.data.map(payment => ({
          ...payment,
          customerName: customers[payment.customerId]?.name || `Customer ${payment.customerId.substring(0, 8)}`
        }));
        
        setPayments(enhancedPayments);
      } else {
        setPayments(result.data);
      }
      
      setTotalItems(result.pagination.totalItems);
    }
  };

  const handleChangePage = (_event: unknown, newPage: number) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event: React.ChangeEvent<HTMLInputElement>) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };
  const handleSearch = (event: React.ChangeEvent<HTMLInputElement>) => {
    setSearchTerm(event.target.value);
    // Local filtering for the search term
    // Note: For better performance in larger datasets, consider API-based search
  };
  const handleCustomerSearch = async (searchTerm: string) => {
    setCustomerSearchTerm(searchTerm);
    if (searchTerm.length > 2) {
      const result = await searchCustomers({ search: searchTerm });
      if (result) {
        setCustomerOptions(result.data);
      }
    } else if (searchTerm.length === 0) {
      setCustomerOptions([]);
    }
  };

  const clearFilters = () => {
    setSelectedPaymentMethod('');
    setSelectedStatus('');
    setStartDate(null);
    setEndDate(null);
    setSelectedCustomerId('');
    setSelectedOrderId('');
    setSelectedInvoiceId('');
    setSearchTerm('');
    setCustomerSearchTerm('');
    setCustomerOptions([]);
  };

  const handleDeletePayment = async () => {
    if (!selectedPayment) return;
    
    const success = await deletePayment(selectedPayment.id);
    if (success) {
      enqueueSnackbar('Payment successfully deleted', { variant: 'success' });
      fetchPayments();
    } else {
      enqueueSnackbar('Failed to delete payment', { variant: 'error' });
    }
    
    setIsDeleteConfirmOpen(false);
    setSelectedPayment(null);
  };

  // Handle successful payment creation
  const handlePaymentCreated = () => {
    fetchPayments();
    setIsCreateModalOpen(false);
    enqueueSnackbar('Payment successfully created', { variant: 'success' });
  };

  // Handle successful payment update
  const handlePaymentUpdated = () => {
    fetchPayments();
    setIsUpdateModalOpen(false);
    setSelectedPayment(null);
    enqueueSnackbar('Payment successfully updated', { variant: 'success' });
  };

  // Handle successful payment reconciliation
  const handlePaymentReconciled = () => {
    fetchPayments();
    setIsReconcileModalOpen(false);
    setSelectedPayment(null);
    enqueueSnackbar('Payment successfully reconciled', { variant: 'success' });
  };

  const getStatusChipColor = (status: ReconciliationStatus) => {
    switch (status) {
      case 'reconciled':
        return 'success';
      case 'disputed':
        return 'error';
      case 'pending_review':
        return 'warning';
      case 'unreconciled':
      default:
        return 'default';
    }
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 2
    }).format(amount);
  };

  return (
