    {
      id: 'product_name',
      label: 'Product',
      minWidth: 170,
    },
    {
      id: 'quantity',
      label: 'Quantity',
      minWidth: 120,
      align: 'right',
      format: (value, row) => formatQuantityWithUnit(value, row?.unit)
    },
    {
      id: 'warehouse_name',
      label: 'Warehouse',
      minWidth: 150,
    },
    {
      id: 'batch_number',
      label: 'Batch',
      minWidth: 120,
    },
    {
      id: 'stock_level',
      label: 'Status',
      minWidth: 120,
      format: (_, row) => getStockLevelIndicator(
        row.quantity, 
        row.threshold || 0, 
        row.warehouse_capacity || 1000
      )
    },
    {
      id: 'last_updated',
      label: 'Last Updated',
      minWidth: 150,
      format: (value) => value ? new Date(value).toLocaleString() : 'N/A'
    }
  ];
  
  // Add actions column if required
  if (!hideActions) {
    columns.push({
      id: 'actions',
      label: 'Actions',
      minWidth: 120,
      align: 'right',
      format: (_, row) => (
        <Box>
          {onAdjustInventory && (
            <Tooltip title="Adjust Inventory">
              <IconButton 
                size="small" 
                onClick={(e) => {
                  e.stopPropagation();
                  onAdjustInventory(row.id);
                }}
              >
                <EditIcon fontSize="small" />
              </IconButton>
            </Tooltip>
          )}
          
          {onTransferInventory && (
            <Tooltip title="Transfer Inventory">
              <IconButton 
                size="small" 
                onClick={(e) => {
                  e.stopPropagation();
                  onTransferInventory(row.id);
                }}
              >
                <TransferWithinAStationIcon fontSize="small" />
              </IconButton>
            </Tooltip>
          )}
        </Box>
      )
    });
  }
  
  return (
    <Box sx={{ maxHeight }}>
      <Box sx={{ 
        mb: 3, 
        display: 'flex', 
        flexDirection: { xs: 'column', md: 'row' }, 
        gap: 2, 
        justifyContent: 'space-between', 
        alignItems: { xs: 'stretch', md: 'center' } 
      }}>
        <Typography variant="h6" component="h2">
