                    <MenuItem key={warehouse.id} value={warehouse.id}>
                      {warehouse.name}
                    </MenuItem>
                  )) : null}
                </Select>
              </FormControl>
            )}
            
            <Tooltip title="Advanced Filters">
              <IconButton>
                <TuneIcon />
              </IconButton>
            </Tooltip>
          </Box>
          
          {onAddInventory && (
            <Button
              variant="contained"
              startIcon={<AddIcon />}
              onClick={onAddInventory}
              sx={buttonStyle}
            >
              Add Inventory
            </Button>
          )}
        </Box>
      </Box>
      
      <DataTable
        columns={columns}
        data={state.inventory}
        loading={state.loading}
        error={state.error}
        pagination={{
          page: state.page,
          totalCount: state.totalCount,
          rowsPerPage: state.rowsPerPage,
          onPageChange: (newPage) => dispatch({ type: 'SET_PAGE', payload: newPage }),
          onRowsPerPageChange: (newRowsPerPage) => {
            dispatch({ type: 'SET_ROWS_PER_PAGE', payload: newRowsPerPage });
            // Page reset is handled in the reducer
          },
        }}
        onRowClick={onInventorySelect ? (row) => onInventorySelect(row.id) : undefined}
      />
    </Box>
  );
};

