      color = 'success';
      label = 'Good';
    }
    
    return <Chip size="small" color={color} label={label} />;
  };
  
  // Validate the required companyId after all hooks are called
  if (!companyId) {
    return (
      <Box sx={{ p: 2 }}>
        <Alert severity="error">
          Company ID is required to display inventory
        </Alert>
      </Box>
    );
  }
  
  // Define columns
  const columns: Column[] = [
    {
      id: 'product_name',
      label: 'Product',
      minWidth: 170,
    },
    {
      id: 'quantity',
      label: 'Quantity',
      minWidth: 120,
      align: 'right',
      format: (value, row) => formatQuantityWithUnit(value, row?.unit)
    },
    {
      id: 'warehouse_name',
      label: 'Warehouse',
      minWidth: 150,
    },
    {
      id: 'batch_number',
      label: 'Batch',
      minWidth: 120,
    },
    {
      id: 'stock_level',
      label: 'Status',
      minWidth: 120,
      format: (_, row) => getStockLevelIndicator(
        row.quantity, 
        row.threshold || 0, 
        row.warehouse_capacity || 1000
      )
    },
    {
      id: 'last_updated',
      label: 'Last Updated',
      minWidth: 150,
      format: (value) => value ? new Date(value).toLocaleString() : 'N/A'
    }
  ];
  
  // Add actions column if required
  if (!hideActions) {
    columns.push({
      id: 'actions',
      label: 'Actions',
      minWidth: 120,
      align: 'right',
      format: (_, row) => (
        <Box>
          {onAdjustInventory && (
            <Tooltip title="Adjust Inventory">
              <IconButton 
                size="small" 
                onClick={(e) => {
                  e.stopPropagation();
                  onAdjustInventory(row.id);
                }}
              >
                <EditIcon fontSize="small" />
              </IconButton>
            </Tooltip>
          )}
          
          {onTransferInventory && (
            <Tooltip title="Transfer Inventory">
              <IconButton 
                size="small" 
                onClick={(e) => {
                  e.stopPropagation();
                  onTransferInventory(row.id);
                }}
              >
                <TransferWithinAStationIcon fontSize="small" />
              </IconButton>
            </Tooltip>
          )}
        </Box>
      )
    });
  }
  
  return (
    <Box sx={{ maxHeight }}>
      <Box sx={{ 
        mb: 3, 
        display: 'flex', 
        flexDirection: { xs: 'column', md: 'row' }, 
        gap: 2, 
        justifyContent: 'space-between', 
        alignItems: { xs: 'stretch', md: 'center' } 
      }}>
        <Typography variant="h6" component="h2">
          Inventory
          {state.inventory.some(item => item.quantity <= (item.threshold || 0)) && (
            <Tooltip title="Some products have low stock">
              <WarningIcon 
                color="warning" 
                fontSize="small" 
                sx={{ ml: 1, verticalAlign: 'middle' }} 
              />
            </Tooltip>
          )}
        </Typography>
        
        <Box sx={{ 
          display: 'flex', 
          flexDirection: { xs: 'column', sm: 'row' }, 
          gap: 2 
        }}>
          <Box sx={{ display: 'flex', gap: 2 }}>
            <TextField
              placeholder="Search products..."
              size="small"
              value={state.searchTerm}
              onChange={(e) => dispatch({ type: 'SET_SEARCH_TERM', payload: e.target.value })}
              onKeyPress={handleSearchKeyPress}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <SearchIcon />
                  </InputAdornment>
                ),
                endAdornment: state.searchTerm && (
                  <InputAdornment position="end">
                    <Button 
                      size="small" 
                      onClick={() => {
                        dispatch({ type: 'SET_SEARCH_TERM', payload: '' });
                        if (!state.searchTerm) return;
                        setTimeout(() => handleSearch(), 0);
                      }}
                    >
                      Clear
                    </Button>
                  </InputAdornment>
                ),
              }}
            />
            
            {!warehouseId && state.warehouses.length > 0 && (
              <FormControl sx={{ minWidth: 150 }} size="small">
                <InputLabel id="warehouse-filter-label">Warehouse</InputLabel>
                <Select
                  labelId="warehouse-filter-label"
                  value={state.selectedWarehouse}
                  onChange={handleWarehouseChange}
                  label="Warehouse"
                >
                  <MenuItem value="">All Warehouses</MenuItem>
                  {Array.isArray(state.warehouses) ? state.warehouses.map((warehouse) => (
                    <MenuItem key={warehouse.id} value={warehouse.id}>
                      {warehouse.name}
                    </MenuItem>
                  )) : null}
                </Select>
              </FormControl>
            )}
            
            <Tooltip title="Advanced Filters">
              <IconButton>
                <TuneIcon />
              </IconButton>
            </Tooltip>
          </Box>
          
          {onAddInventory && (
            <Button
              variant="contained"
              startIcon={<AddIcon />}
              onClick={onAddInventory}
              sx={buttonStyle}
            >
              Add Inventory
            </Button>
          )}
        </Box>
      </Box>
      
      <DataTable
        columns={columns}
        data={state.inventory}
        loading={state.loading}
        error={state.error}
        pagination={{
          page: state.page,
          totalCount: state.totalCount,
          rowsPerPage: state.rowsPerPage,
          onPageChange: (newPage) => dispatch({ type: 'SET_PAGE', payload: newPage }),
          onRowsPerPageChange: (newRowsPerPage) => {
            dispatch({ type: 'SET_ROWS_PER_PAGE', payload: newRowsPerPage });
            // Page reset is handled in the reducer
          },
        }}
        onRowClick={onInventorySelect ? (row) => onInventorySelect(row.id) : undefined}
      />
    </Box>
  );
};

export default InventoryList;

import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Typography, 
  Paper, 
  Divider, 
  Chip, 
  Button, 
  Stack,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  IconButton,
  Menu,
  MenuItem,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  CircularProgress,
  TextField
} from '@mui/material';
import {
  GetApp as DownloadIcon,
  Print as PrintIcon,
  MoreVert as MoreVertIcon,
  Email as EmailIcon,
  CheckCircle as CheckCircleIcon,
} from '@mui/icons-material';
import { format } from 'date-fns';
import { useNavigate } from 'react-router-dom';
import { 
  getInvoiceById, 
  generateInvoicePdf, 
  sendInvoiceByEmail,
  markInvoiceAsPaid,
  InvoiceStatus, 
  type Invoice
} from '../../../services/api/invoiceService';

interface InvoiceDetailProps {
  invoiceId: string;
  onEdit?: () => void;
}

const InvoiceDetail: React.FC<InvoiceDetailProps> = ({ invoiceId, onEdit }) => {
  const navigate = useNavigate();
  const [invoice, setInvoice] = useState<Invoice | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [actionMenuAnchor, setActionMenuAnchor] = useState<null | HTMLElement>(null);
  const [emailDialogOpen, setEmailDialogOpen] = useState(false);
  const [paymentDialogOpen, setPaymentDialogOpen] = useState(false);
  const [emailRecipient, setEmailRecipient] = useState('');
  const [emailSubject, setEmailSubject] = useState('');
  const [emailMessage, setEmailMessage] = useState('');
  const [sendingEmail, setSendingEmail] = useState(false);
  const [emailError, setEmailError] = useState<string | null>(null);
  const [paymentAmount, setPaymentAmount] = useState('');
  const [paymentDate, setPaymentDate] = useState(format(new Date(), 'yyyy-MM-dd'));
  const [paymentMethod, setPaymentMethod] = useState('');
  const [paymentNotes, setPaymentNotes] = useState('');
  const [processingPayment, setProcessingPayment] = useState(false);
  const [paymentError, setPaymentError] = useState<string | null>(null);

  useEffect(() => {
    fetchInvoiceDetails();
  }, [invoiceId]);

  const fetchInvoiceDetails = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await getInvoiceById(invoiceId);
      setInvoice(response);
      
      // Pre-fill email fields
      if (response) {
        setEmailRecipient(''); // This should ideally be filled with customer email from the backend
        setEmailSubject(`Invoice ${response.invoiceNumber} from Your Company`);
        setEmailMessage(`Dear Customer,\n\nPlease find attached invoice ${response.invoiceNumber} for your recent order.\n\nThank you for your business.\n\nBest regards,\nYour Company`);
        
        // Pre-fill payment amount
        if (response.total) {
          setPaymentAmount(response.total.toString());
        }
      }
    } catch (err: any) {
      setError(err.message || 'Failed to fetch invoice details');
      console.error('Error fetching invoice details:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleActionMenuOpen = (event: React.MouseEvent<HTMLElement>) => {
    setActionMenuAnchor(event.currentTarget);
  };

  const handleActionMenuClose = () => {
    setActionMenuAnchor(null);
  };

  const handleEmailDialogOpen = () => {
    setEmailDialogOpen(true);
    handleActionMenuClose();
  };

  const handleEmailDialogClose = () => {
    setEmailDialogOpen(false);
    setEmailError(null);
  };

  const handlePaymentDialogOpen = () => {
    setPaymentDialogOpen(true);
    handleActionMenuClose();
  };

  const handlePaymentDialogClose = () => {
    setPaymentDialogOpen(false);
    setPaymentError(null);
  };

  const handleSendEmail = async () => {
    if (!emailRecipient) {
      setEmailError('Recipient email is required');
      return;
    }

    setSendingEmail(true);
    setEmailError(null);

    try {
      await sendInvoiceByEmail(invoiceId, {
        recipientEmail: emailRecipient,
        subject: emailSubject,
        message: emailMessage,
      });
      handleEmailDialogClose();
      // Show success message or notification
    } catch (err: any) {
      setEmailError(err.message || 'Failed to send email');
      console.error('Error sending invoice email:', err);
    } finally {
      setSendingEmail(false);
    }
  };

  const handleRecordPayment = async () => {
    if (!paymentAmount || !paymentMethod) {
      setPaymentError('Amount and payment method are required');
      return;
    }

    setProcessingPayment(true);
    setPaymentError(null);

    try {
      await markInvoiceAsPaid(invoiceId, {
        amount: parseFloat(paymentAmount),
        paymentDate,
        paymentMethod,
        notes: paymentNotes
      });
      handlePaymentDialogClose();
      fetchInvoiceDetails(); // Refresh invoice data
      // Show success message or notification
    } catch (err: any) {
      setPaymentError(err.message || 'Failed to record payment');
      console.error('Error recording payment:', err);
    } finally {
      setProcessingPayment(false);
    }
  };

  const handleDownloadPdf = async () => {
    try {
      const pdfBlob = await generateInvoicePdf(invoiceId);
      const url = URL.createObjectURL(pdfBlob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `Invoice-${invoice?.invoiceNumber || invoiceId}.pdf`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      URL.revokeObjectURL(url);
    } catch (err: any) {
      console.error('Error downloading PDF:', err);
      // Show error message
    }
  };

  const handlePrint = async () => {
    try {
      const pdfBlob = await generateInvoicePdf(invoiceId);
      const url = URL.createObjectURL(pdfBlob);
      const iframe = document.createElement('iframe');
      iframe.style.display = 'none';
      iframe.src = url;
      document.body.appendChild(iframe);
      iframe.onload = () => {
        iframe.contentWindow?.print();
      };
      
      // Cleanup after print dialog closes (this is approximate)
      setTimeout(() => {
        document.body.removeChild(iframe);
        URL.revokeObjectURL(url);
      }, 5000);
    } catch (err: any) {
      console.error('Error printing invoice:', err);
      // Show error message
    }
  };

  const handleEdit = () => {
    if (onEdit) {
      onEdit();
    } else {
      navigate(`/invoices/${invoiceId}/edit`);
    }
    handleActionMenuClose();
  };

  const handleBack = () => {
    navigate('/invoices');
  };

  const getStatusChipColor = (status: InvoiceStatus) => {
    switch (status) {
      case InvoiceStatus.PAID:
        return 'success';
      case InvoiceStatus.OVERDUE:
        return 'error';
      case InvoiceStatus.PARTIALLY_PAID:
        return 'info';
      default:
        return 'default';
    }
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', { style: 'currency', currency: 'USD' }).format(amount);
  };

  const formatDate = (dateString?: string) => {
    if (!dateString) return 'N/A';
    return format(new Date(dateString), 'MMMM dd, yyyy');
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="60vh">
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Box textAlign="center" p={3}>
        <Typography color="error" variant="h6">
          {error}
        </Typography>
        <Button onClick={fetchInvoiceDetails} sx={{ mt: 2 }}>
          Retry
        </Button>
      </Box>
    );
  }

  if (!invoice) {
    return (
      <Box textAlign="center" p={3}>
        <Typography variant="h6">
          Invoice not found
        </Typography>
        <Button onClick={handleBack} sx={{ mt: 2 }}>
          Back to Invoices
        </Button>
      </Box>
    );
  }

  return (
    <Box>
      {/* Sticky Header */}
      <Box position="sticky" top={0} zIndex={10} bgcolor="background.paper" boxShadow={1} mb={3} px={2} py={2} borderRadius={2}>
        <Box display="flex" justifyContent="space-between" alignItems="center">
          <Box display="flex" alignItems="center" gap={2}>
            <Button variant="outlined" onClick={handleBack} sx={{ mr: 2 }}>
              Back
            </Button>
            <Typography variant="h5" component="span">
              Invoice #{invoice.invoiceNumber}
            </Typography>
            <Chip 
              label={invoice.status ? invoice.status.replace('_', ' ').toUpperCase() : 'DRAFT'} 
              size="small" 
              color={getStatusChipColor(invoice.status as InvoiceStatus)}
              sx={{ ml: 2, verticalAlign: 'middle' }}
            />
          </Box>
          <Box display="flex" alignItems="center" gap={1}>
            <Button 
              variant="outlined" 
              startIcon={<DownloadIcon />}
              onClick={handleDownloadPdf}
            >
              Download
            </Button>
            <Button 
              variant="outlined" 
              startIcon={<PrintIcon />}
              onClick={handlePrint}
            >
              Print
            </Button>
            <IconButton onClick={handleActionMenuOpen} sx={{ ml: 1 }}>
              <MoreVertIcon />
            </IconButton>
            <Menu
              anchorEl={actionMenuAnchor}
              open={Boolean(actionMenuAnchor)}
              onClose={handleActionMenuClose}
            >
              <MenuItem onClick={handleEdit}>Edit Invoice</MenuItem>
              <MenuItem onClick={handleEmailDialogOpen}>
                <EmailIcon fontSize="small" sx={{ mr: 1 }} />
                Send by Email
              </MenuItem>
              {invoice.status !== InvoiceStatus.PAID && (
                <MenuItem onClick={handlePaymentDialogOpen}>
                  <CheckCircleIcon fontSize="small" sx={{ mr: 1 }} />
                  Record Payment
                </MenuItem>
              )}
            </Menu>
          </Box>
        </Box>
      </Box>

      <Paper sx={{ p: { xs: 2, md: 4 }, mb: 3, borderRadius: 3, boxShadow: 2 }}>
        <Box sx={{ display: 'flex', flexDirection: 'column', gap: 4 }}>
          {/* Header Info */}
          <Box sx={{ width: '100%', display: 'flex', flexDirection: { xs: 'column', md: 'row' }, gap: 4 }}>
            <Box sx={{ flex: 1, bgcolor: 'grey.50', p: 2, borderRadius: 2 }}>
              <Typography variant="subtitle1" fontWeight={700} gutterBottom sx={{ color: 'primary.main' }}>
                Invoice Information
              </Typography>
              <Box>
                <Typography variant="body2">
                  <strong>Invoice Number:</strong> {invoice.invoiceNumber}
                </Typography>
                <Typography variant="body2">
                  <strong>Issue Date:</strong> {formatDate(invoice.issueDate)}
                </Typography>
                <Typography variant="body2">
                  <strong>Due Date:</strong> {formatDate(invoice.dueDate)}
                </Typography>
                <Typography variant="body2">
                  <strong>Payment Term:</strong> {invoice.paymentTerm.replace('_', ' ')}
                </Typography>
              </Box>
            </Box>
            <Box sx={{ flex: 1, bgcolor: 'grey.50', p: 2, borderRadius: 2 }}>
              <Typography variant="subtitle1" fontWeight={700} gutterBottom sx={{ color: 'primary.main' }}>
                Customer Information
              </Typography>
              <Box>
                <Typography variant="body2">
                  <strong>Customer ID:</strong> {invoice.customerId}
                </Typography>
                <Typography variant="body2">
                  <strong>Billing Address:</strong>
                </Typography>
                <Typography variant="body2" sx={{ whiteSpace: 'pre-wrap' }}>
                  {invoice.billingAddress}
                </Typography>
              </Box>
            </Box>
          </Box>

          {/* Invoice Items */}
          <Box sx={{ width: '100%' }}>
            <Typography variant="subtitle1" fontWeight={700} gutterBottom sx={{ color: 'primary.main', mb: 1 }}>
              Invoice Items
            </Typography>
            <TableContainer component={Paper} variant="outlined" sx={{ borderRadius: 2, boxShadow: 0 }}>
              <Table>
                <TableHead sx={{ bgcolor: 'grey.100' }}>
                  <TableRow>
                    <TableCell><strong>Description</strong></TableCell>
                    <TableCell align="right"><strong>Quantity</strong></TableCell>
                    <TableCell align="right"><strong>Unit Price</strong></TableCell>
                    <TableCell align="right"><strong>Discount</strong></TableCell>
                    <TableCell align="right"><strong>Tax</strong></TableCell>
                    <TableCell align="right"><strong>Total</strong></TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {invoice.items.map((item, index) => {
                    const itemTotal = item.quantity * item.unitPrice;
                    const discountAmount = item.discount || 0;
                    const taxAmount = item.tax || 0;
                    const lineTotal = itemTotal - discountAmount + taxAmount;
                    return (
                      <TableRow key={index} hover sx={{ transition: 'background 0.2s', cursor: 'pointer' }}>
                        <TableCell>{item.description || `Product ID: ${item.productId}`}</TableCell>
                        <TableCell align="right">{item.quantity}</TableCell>
                        <TableCell align="right">{formatCurrency(item.unitPrice)}</TableCell>
                        <TableCell align="right">{item.discount ? formatCurrency(item.discount) : '-'}</TableCell>
                        <TableCell align="right">{item.tax ? formatCurrency(item.tax) : '-'}</TableCell>
                        <TableCell align="right">{formatCurrency(lineTotal)}</TableCell>
                      </TableRow>
                    );
                  })}
                </TableBody>
              </Table>
            </TableContainer>
          </Box>

          {/* Totals */}
          <Box sx={{ width: '100%' }}>
            <Box sx={{ display: 'flex', justifyContent: 'flex-end', mt: 2 }}>
              <Box sx={{ width: { xs: '100%', sm: '320px' }, bgcolor: 'grey.100', p: 2, borderRadius: 2, boxShadow: 0 }}>
                <Stack spacing={1}>
                  <Box display="flex" justifyContent="space-between">
                    <Typography variant="body2">Subtotal:</Typography>
                    <Typography variant="body2">{formatCurrency(invoice.subtotal || 0)}</Typography>
                  </Box>
                  <Box display="flex" justifyContent="space-between">
                    <Typography variant="body2">Discount:</Typography>
                    <Typography variant="body2">{formatCurrency(invoice.discountTotal || 0)}</Typography>
                  </Box>
                  <Box display="flex" justifyContent="space-between">
                    <Typography variant="body2">Tax:</Typography>
                    <Typography variant="body2">{formatCurrency(invoice.taxTotal || 0)}</Typography>
                  </Box>
                  <Divider />
                  <Box display="flex" justifyContent="space-between">
                    <Typography variant="subtitle1" fontWeight={700}><strong>Total:</strong></Typography>
                    <Typography variant="subtitle1" fontWeight={700}><strong>{formatCurrency(invoice.total || 0)}</strong></Typography>
                  </Box>
                </Stack>
              </Box>
            </Box>
          </Box>

          {/* Notes */}
          {invoice.notes && (
            <Box sx={{ width: '100%' }}>
              <Typography variant="subtitle1" fontWeight={700} gutterBottom sx={{ color: 'primary.main' }}>
                Notes
              </Typography>
              <Paper variant="outlined" sx={{ p: 2, bgcolor: 'grey.50', borderRadius: 2 }}>
                <Typography variant="body2" sx={{ whiteSpace: 'pre-wrap' }}>
                  {invoice.notes}
                </Typography>
              </Paper>
            </Box>
          )}
        </Box>
      </Paper>

      {/* Email Dialog */}
      <Dialog open={emailDialogOpen} onClose={handleEmailDialogClose} maxWidth="md" fullWidth>
        <DialogTitle>Send Invoice by Email</DialogTitle>
        <DialogContent>
          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, mt: 1 }}>
            <Typography variant="body2" gutterBottom>
              Send invoice #{invoice.invoiceNumber} to the customer via email.
            </Typography>
            <TextField
              label="Recipient Email"
              value={emailRecipient}
              onChange={(e) => setEmailRecipient(e.target.value)}
              fullWidth
              required
              error={!emailRecipient && !!emailError}
              helperText={!emailRecipient && emailError ? 'Recipient email is required' : ''}
              sx={{ mb: 1 }}
            />
            <TextField
              label="Email Subject"
              value={emailSubject}
              onChange={(e) => setEmailSubject(e.target.value)}
              fullWidth
              sx={{ mb: 1 }}
            />
            <TextField
              label="Email Message"
              value={emailMessage}
              onChange={(e) => setEmailMessage(e.target.value)}
              multiline
              rows={6}
              fullWidth
              sx={{ mb: 1 }}
            />
            {emailError && (
              <Typography color="error">{emailError}</Typography>
            )}
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleEmailDialogClose} disabled={sendingEmail}>
            Cancel
          </Button>
          <Button
            onClick={handleSendEmail}
            variant="contained"
            color="primary"
            disabled={sendingEmail}
            startIcon={sendingEmail ? <CircularProgress size={20} /> : <EmailIcon />}
          >
            {sendingEmail ? 'Sending...' : 'Send'}
          </Button>
        </DialogActions>
      </Dialog>

      {/* Payment Dialog */}
      <Dialog open={paymentDialogOpen} onClose={handlePaymentDialogClose} maxWidth="sm" fullWidth>
        <DialogTitle>Record Payment</DialogTitle>
        <DialogContent>
          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, mt: 1 }}>
            <Typography variant="body2" gutterBottom>
              Record a payment for invoice #{invoice.invoiceNumber}
            </Typography>
            <Box sx={{ display: 'flex', flexDirection: { xs: 'column', md: 'row' }, gap: 2 }}>
              <Box sx={{ flex: 1 }}>
                <TextField
                  label="Amount"
                  type="number"
                  value={paymentAmount}
                  onChange={(e) => setPaymentAmount(e.target.value)}
                  fullWidth
                  required
                  InputProps={{
                    startAdornment: <Box component="span" sx={{ mr: 1 }}>$</Box>
                  }}
                  error={!paymentAmount && !!paymentError}
                  sx={{ mb: 1 }}
                />
              </Box>
              <Box sx={{ flex: 1 }}>
                <TextField
                  label="Payment Date"
                  type="date"
                  value={paymentDate}
                  onChange={(e) => setPaymentDate(e.target.value)}
                  fullWidth
                  InputLabelProps={{ shrink: true }}
                  sx={{ mb: 1 }}
                />
              </Box>
            </Box>
            <TextField
              label="Payment Method"
              value={paymentMethod}
              onChange={(e) => setPaymentMethod(e.target.value)}
              fullWidth
              required
              select
              error={!paymentMethod && !!paymentError}
              sx={{ mb: 1 }}
            >
              <MenuItem value="cash">Cash</MenuItem>
              <MenuItem value="check">Check</MenuItem>
              <MenuItem value="credit_card">Credit Card</MenuItem>
              <MenuItem value="bank_transfer">Bank Transfer</MenuItem>
              <MenuItem value="paypal">PayPal</MenuItem>
              <MenuItem value="other">Other</MenuItem>
            </TextField>
            <TextField
              label="Notes"
              value={paymentNotes}
              onChange={(e) => setPaymentNotes(e.target.value)}
              fullWidth
              multiline
              rows={3}
              sx={{ mb: 1 }}
            />
            {paymentError && (
              <Typography color="error">{paymentError}</Typography>
            )}
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handlePaymentDialogClose} disabled={processingPayment}>
            Cancel
          </Button>
          <Button
            onClick={handleRecordPayment}
            variant="contained"
            color="primary"
            disabled={processingPayment}
            startIcon={processingPayment ? <CircularProgress size={20} /> : <CheckCircleIcon />}
          >
            {processingPayment ? 'Processing...' : 'Record Payment'}
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default InvoiceDetail;

import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  TextField,
  Button,
  Paper,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  IconButton,
  Divider,
  Autocomplete,
  InputAdornment,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  FormHelperText,
  CircularProgress,
} from '@mui/material';
import {
  Add as AddIcon,
  Delete as DeleteIcon,
  CalendarToday as CalendarIcon,
  Save as SaveIcon,
} from '@mui/icons-material';
import { format } from 'date-fns';
import FormContainer from '../FormContainer';
import FormButtons from '../FormButtons';
import {
  createInvoice,
  updateInvoice,
  getInvoiceById,
  PaymentTerm,
  InvoiceStatus,
  type Invoice,
  type InvoiceItem,
  type CreateInvoiceRequest
} from '../../../services/api/invoiceService';
import { productService } from '../../../services/api';

interface InvoiceFormProps {
  invoiceId?: string;
  onSave?: (invoice: Invoice) => void;
  onCancel?: () => void;
}

interface Customer {
  id: string;
  name: string;
  email?: string;
  billingAddress?: string;
}

interface ProductOption {
  id: string;
  name: string;
  price: number;
  description?: string;
}

const InvoiceForm: React.FC<InvoiceFormProps> = ({
  invoiceId,
  onSave,
  onCancel,
}) => {
  const isEditMode = !!invoiceId;

  const [invoice, setInvoice] = useState<Partial<Invoice>>({
    items: [],
    paymentTerm: PaymentTerm.DAYS_30,
    billingAddress: '',
    issueDate: format(new Date(), 'yyyy-MM-dd'),
  });

  const [customers, setCustomers] = useState<Customer[]>([]);
  const [products, setProducts] = useState<ProductOption[]>([]);
  const [loading, setLoading] = useState(false);
  const [fetchLoading, setFetchLoading] = useState(false);
  const [errors, setErrors] = useState<Record<string, string>>({});
  const [isCalculating, setIsCalculating] = useState(false);
  const [isDirty, setIsDirty] = useState(false);
  const [showConfirmDialog, setShowConfirmDialog] = useState(false);

  // Fetch data on component mount
  useEffect(() => {
    fetchData();
  }, []);

  // Fetch invoice details if in edit mode
  useEffect(() => {
    if (isEditMode && invoiceId) {
      fetchInvoiceDetails(invoiceId);
    }
  }, [invoiceId, isEditMode]);

  // Calculate totals whenever items change
  useEffect(() => {
    calculateTotals();
  }, [invoice.items]);

  const fetchData = async () => {
    setFetchLoading(true);
    try {
      // Fetch customers
      // This is a placeholder - replace with actual API call
      // const customersResponse = await customerService.getCustomers();
      // setCustomers(customersResponse.data);
      
      // Mock customers for now
      setCustomers([
        { id: 'cust1', name: 'Acme Corp', email: 'billing@acmecorp.com', billingAddress: '123 Main St\nNew York, NY 10001\nUSA' },
        { id: 'cust2', name: 'Globex Industries', email: 'accounts@globex.com', billingAddress: '456 Business Ave\nChicago, IL 60601\nUSA' },
        { id: 'cust3', name: 'Umbrella Corporation', email: 'finance@umbrella.com', billingAddress: '789 Corporate Blvd\nLos Angeles, CA 90001\nUSA' },
      ]);

      // Fetch products
      const productsResponse = await productService.getProducts();
      const productOptions = productsResponse.data.map((product: any) => ({
        id: product.id,
        name: product.name,
        price: product.base_price || 0,
        description: product.description || '',
      }));
      setProducts(productOptions);
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setFetchLoading(false);
    }
  };

  const fetchInvoiceDetails = async (id: string) => {
    setFetchLoading(true);
    try {
      const response = await getInvoiceById(id);
      setInvoice(response);
    } catch (error) {
      console.error('Error fetching invoice:', error);
    } finally {
      setFetchLoading(false);
    }
  };

  const calculateTotals = () => {
    if (!invoice.items || invoice.items.length === 0) {
      setInvoice(prev => ({
        ...prev,
        subtotal: 0,
        discountTotal: 0,
        taxTotal: 0,
        total: 0,
      }));
      return;
    }

    setIsCalculating(true);
    
    let subtotal = 0;
    let discountTotal = 0;
    let taxTotal = 0;

    invoice.items.forEach(item => {
      const lineTotal = item.quantity * item.unitPrice;
      subtotal += lineTotal;
      
      if (item.discount) {
        discountTotal += item.discount;
      }
      
      if (item.tax) {
        taxTotal += item.tax;
      }
    });

    const total = subtotal - discountTotal + taxTotal;

    setInvoice(prev => ({
      ...prev,
      subtotal: parseFloat(subtotal.toFixed(2)),
      discountTotal: parseFloat(discountTotal.toFixed(2)),
      taxTotal: parseFloat(taxTotal.toFixed(2)),
      total: parseFloat(total.toFixed(2)),
    }));

    setIsCalculating(false);
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | { name?: string; value: unknown }>) => {
    const { name, value } = e.target;
    if (!name) return;

    setInvoice(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear error for the field
    if (errors[name]) {
      setErrors(prev => {
        const newErrors = { ...prev };
        delete newErrors[name];
        return newErrors;
      });
    }

    setIsDirty(true);
  };

  const handleSelectChange = (name: string, value: unknown) => {
    setInvoice(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear error for the field
    if (errors[name]) {
      setErrors(prev => {
        const newErrors = { ...prev };
        delete newErrors[name];
        return newErrors;
      });
    }

    setIsDirty(true);
  };

  const handleCustomerSelect = (customer: Customer | null) => {
    if (customer) {
      setInvoice(prev => ({
        ...prev,
        customerId: customer.id,
        billingAddress: customer.billingAddress || ''
      }));

      // Clear errors
      if (errors.customerId || errors.billingAddress) {
        setErrors(prev => {
          const newErrors = { ...prev };
          delete newErrors.customerId;
          delete newErrors.billingAddress;
          return newErrors;
        });
      }
    } else {
      setInvoice(prev => ({
        ...prev,
        customerId: '',
        billingAddress: ''
      }));
    }

    setIsDirty(true);
  };

  const handleAddItem = () => {
    setInvoice(prev => ({
      ...prev,
      items: [
        ...(prev.items || []),
        {
          productId: '',
          description: '',
          quantity: 1,
          unitPrice: 0,
        }
      ]
    }));

    setIsDirty(true);
  };

  const handleRemoveItem = (index: number) => {
    setInvoice(prev => ({
      ...prev,
      items: prev.items?.filter((_, i) => i !== index) || []
    }));

    setIsDirty(true);
  };

  const handleItemChange = (index: number, field: keyof InvoiceItem, value: any) => {
    setInvoice(prev => {
      const updatedItems = [...(prev.items || [])];
      if (updatedItems[index]) {
        updatedItems[index] = {
          ...updatedItems[index],
          [field]: value
        };
      }
      return {
        ...prev,
        items: updatedItems
      };
    });

    // Clear item error
    const errorKey = `items[${index}].${field}`;
    if (errors[errorKey]) {
      setErrors(prev => {
        const newErrors = { ...prev };
        delete newErrors[errorKey];
        return newErrors;
      });
    }

    setIsDirty(true);
  };

  const handleProductSelect = (index: number, product: ProductOption | null) => {
    if (product) {
      setInvoice(prev => {
        const updatedItems = [...(prev.items || [])];
        if (updatedItems[index]) {
          updatedItems[index] = {
            ...updatedItems[index],
            productId: product.id,
            description: product.description || product.name,
            unitPrice: product.price
          };
        }
        return {
          ...prev,
          items: updatedItems
        };
      });
    }

    setIsDirty(true);
  };

  const validateForm = (): boolean => {
    const newErrors: Record<string, string> = {};

    // Validate required fields
    if (!invoice.customerId) {
      newErrors.customerId = 'Customer is required';
    }

    if (!invoice.billingAddress || invoice.billingAddress.trim() === '') {
      newErrors.billingAddress = 'Billing address is required';
    }

    if (!invoice.paymentTerm) {
      newErrors.paymentTerm = 'Payment term is required';
    }

    // Validate items
    if (!invoice.items || invoice.items.length === 0) {
      newErrors.items = 'At least one item is required';
    } else {
      invoice.items.forEach((item, index) => {
        if (!item.productId) {
          newErrors[`items[${index}].productId`] = 'Product is required';
        }
        if (item.quantity <= 0) {
          newErrors[`items[${index}].quantity`] = 'Quantity must be greater than 0';
        }
        if (item.unitPrice < 0) {
          newErrors[`items[${index}].unitPrice`] = 'Unit price cannot be negative';
        }
      });
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async () => {
    if (!validateForm()) return;

    setLoading(true);
    try {
      let savedInvoice;
      
      if (isEditMode && invoiceId) {
        // Update existing invoice
        const updateData: Partial<Invoice> = {
          customerId: invoice.customerId!,
          items: invoice.items!,
          paymentTerm: invoice.paymentTerm!,
          billingAddress: invoice.billingAddress!,
          notes: invoice.notes,
          issueDate: invoice.issueDate,
        };
        
        const response = await updateInvoice(invoiceId, updateData);
        savedInvoice = response;
      } else {
        // Create new invoice
        const newInvoice: CreateInvoiceRequest = {
          customerId: invoice.customerId!,
          items: invoice.items!,
          paymentTerm: invoice.paymentTerm!,
          billingAddress: invoice.billingAddress!,
          notes: invoice.notes,
          issueDate: invoice.issueDate,
        };
        
        const response = await createInvoice(newInvoice);
        savedInvoice = response;
      }

      if (onSave) {
        onSave(savedInvoice);
      }
      setIsDirty(false);
    } catch (error) {
      console.error('Error saving invoice:', error);
      // Handle error, show notification, etc.
    } finally {
      setLoading(false);
    }
  };

  const handleCancel = () => {
    if (isDirty) {
      setShowConfirmDialog(true);
    } else if (onCancel) {
      onCancel();
    }
  };

  const handleConfirmCancel = () => {
    setShowConfirmDialog(false);
    if (onCancel) {
      onCancel();
    }
  };

  const handleCloseConfirmDialog = () => {
    setShowConfirmDialog(false);
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', { style: 'currency', currency: 'USD' }).format(amount);
  };

  // Find current customer
  const currentCustomer = customers.find(c => c.id === invoice.customerId);

  if (fetchLoading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="60vh">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <FormContainer title={isEditMode ? 'Edit Invoice' : 'Create Invoice'} maxWidth="md">
      <Paper sx={{ p: 3, mb: 3 }}>
        <Box sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
          {/* Customer Information and Invoice Details Row */}
          <Box sx={{ display: 'flex', flexDirection: { xs: 'column', md: 'row' }, gap: 3 }}>
            {/* Customer Selection */}
            <Box sx={{ flex: 1 }}>
              <Typography variant="subtitle1" gutterBottom>
                Customer Information
              </Typography>
              <Autocomplete
                options={customers}
                getOptionLabel={(option) => option.name}
                value={currentCustomer || null}
                onChange={(_, newValue) => handleCustomerSelect(newValue)}
                renderInput={(params) => (
                  <TextField
                    {...params}
                    label="Customer"
                    required
                    error={!!errors.customerId}
                    helperText={errors.customerId}
                  />
                )}
                fullWidth
              />
            </Box>

            {/* Invoice Details */}
            <Box sx={{ flex: 1 }}>
              <Typography variant="subtitle1" gutterBottom>
                Invoice Details
              </Typography>
              <Box sx={{ display: 'flex', gap: 2, flexDirection: { xs: 'column', md: 'row' } }}>
                <Box sx={{ flex: 1 }}>
                  <TextField
                    label="Issue Date"
                    name="issueDate"
                    type="date"
                    value={invoice.issueDate || format(new Date(), 'yyyy-MM-dd')}
                    onChange={handleChange}
                    fullWidth
                    InputLabelProps={{ shrink: true }}
                    InputProps={{
                      startAdornment: (
                        <InputAdornment position="start">
                          <CalendarIcon />
                        </InputAdornment>
                      ),
                    }}
                  />
                </Box>
                <Box sx={{ flex: 1 }}>
                  <FormControl fullWidth required error={!!errors.paymentTerm}>
                    <InputLabel>Payment Terms</InputLabel>
                    <Select
                      name="paymentTerm"
                      value={invoice.paymentTerm || PaymentTerm.DAYS_30}
                      onChange={(e) => handleSelectChange('paymentTerm', e.target.value)}
                      label="Payment Terms"
                    >
                      <MenuItem value={PaymentTerm.IMMEDIATE}>Immediate</MenuItem>
                      <MenuItem value={PaymentTerm.DAYS_7}>Net 7 Days</MenuItem>
                      <MenuItem value={PaymentTerm.DAYS_15}>Net 15 Days</MenuItem>
                      <MenuItem value={PaymentTerm.DAYS_30}>Net 30 Days</MenuItem>
                      <MenuItem value={PaymentTerm.DAYS_45}>Net 45 Days</MenuItem>
                      <MenuItem value={PaymentTerm.DAYS_60}>Net 60 Days</MenuItem>
                      <MenuItem value={PaymentTerm.DAYS_90}>Net 90 Days</MenuItem>
                    </Select>
                    {errors.paymentTerm && <FormHelperText>{errors.paymentTerm}</FormHelperText>}
                  </FormControl>
                </Box>
              </Box>
            </Box>
          </Box>

          {/* Billing Address */}
          <Box>
            <Typography variant="subtitle1" gutterBottom>
              Billing Address
            </Typography>
            <TextField
              name="billingAddress"
              label="Billing Address"
              value={invoice.billingAddress || ''}
              onChange={handleChange}
              multiline
              rows={3}
              fullWidth
              required
              error={!!errors.billingAddress}
              helperText={errors.billingAddress}
            />
          </Box>

          {/* Invoice Items */}
          <Box>
            <Box display="flex" justifyContent="space-between" alignItems="center" mb={2}>
              <Typography variant="subtitle1">
                Invoice Items
              </Typography>
              <Button
                variant="outlined"
                startIcon={<AddIcon />}
                onClick={handleAddItem}
              >
                Add Item
              </Button>
            </Box>

            {errors.items && (
              <Typography color="error" variant="body2" sx={{ mb: 2 }}>
                {errors.items}
              </Typography>
            )}

            <TableContainer component={Paper} variant="outlined">
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell width="35%">Product</TableCell>
                    <TableCell>Description</TableCell>
                    <TableCell align="right" width="10%">Quantity</TableCell>
                    <TableCell align="right" width="15%">Unit Price</TableCell>
                    <TableCell align="right" width="15%">Amount</TableCell>
                    <TableCell align="center" width="5%">Actions</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {(invoice.items || []).map((item, index) => {
                    const lineTotal = item.quantity * item.unitPrice;
                    const hasProductError = !!errors[`items[${index}].productId`];
                    const hasQuantityError = !!errors[`items[${index}].quantity`];
                    const hasPriceError = !!errors[`items[${index}].unitPrice`];
                    
                    return (
                      <TableRow key={index}>
                        <TableCell>
                          <Autocomplete
                            options={products}
                            getOptionLabel={(option) => option.name}
                            value={products.find(p => p.id === item.productId) || null}
                            onChange={(_, newValue) => handleProductSelect(index, newValue)}
                            renderInput={(params) => (
                              <TextField
                                {...params}
                                label="Product"
                                variant="outlined"
                                size="small"
                                required
                                error={hasProductError}
                                helperText={hasProductError ? errors[`items[${index}].productId`] : ''}
                              />
                            )}
                            fullWidth
                          />
                        </TableCell>
                        <TableCell>
                          <TextField
                            value={item.description || ''}
                            onChange={(e) => handleItemChange(index, 'description', e.target.value)}
                            variant="outlined"
                            size="small"
                            fullWidth
                            placeholder="Description"
                          />
                        </TableCell>
                        <TableCell align="right">
                          <TextField
                            type="number"
                            value={item.quantity}
                            onChange={(e) => handleItemChange(index, 'quantity', parseInt(e.target.value) || 0)}
                            variant="outlined"
                            size="small"
                            inputProps={{ min: 1, style: { textAlign: 'right' } }}
                            error={hasQuantityError}
                            helperText={hasQuantityError ? errors[`items[${index}].quantity`] : ''}
                          />
                        </TableCell>
                        <TableCell align="right">
                          <TextField
                            type="number"
                            value={item.unitPrice}
                            onChange={(e) => handleItemChange(index, 'unitPrice', parseFloat(e.target.value) || 0)}
                            variant="outlined"
                            size="small"
                            InputProps={{
                              startAdornment: <InputAdornment position="start">$</InputAdornment>,
                              inputProps: { min: 0, step: 0.01, style: { textAlign: 'right' } }
                            }}
                            error={hasPriceError}
                            helperText={hasPriceError ? errors[`items[${index}].unitPrice`] : ''}
                          />
                        </TableCell>
                        <TableCell align="right">
                          {formatCurrency(lineTotal)}
                        </TableCell>
                        <TableCell align="center">
                          <IconButton color="error" onClick={() => handleRemoveItem(index)}>
                            <DeleteIcon />
                          </IconButton>
                        </TableCell>
                      </TableRow>
                    );
                  })}

                  {(!invoice.items || invoice.items.length === 0) && (
                    <TableRow>
                      <TableCell colSpan={6} align="center" sx={{ py: 3 }}>
                        <Typography color="textSecondary">
                          No items added yet. Click "Add Item" to add products to the invoice.
                        </Typography>
                      </TableCell>
                    </TableRow>
                  )}
                </TableBody>
              </Table>
            </TableContainer>
          </Box>

          {/* Totals */}
          <Box>
            <Box display="flex" justifyContent="flex-end" mt={2}>
              <Box width="300px">
                <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1 }}>
                  <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
                    <Typography variant="body2">Subtotal:</Typography>
                    <Typography variant="body2">
                      {isCalculating ? '...' : formatCurrency(invoice.subtotal || 0)}
                    </Typography>
                  </Box>
                  
                  <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
                    <Typography variant="body2">Discount:</Typography>
                    <Typography variant="body2">
                      {isCalculating ? '...' : formatCurrency(invoice.discountTotal || 0)}
                    </Typography>
                  </Box>
                  
                  <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
                    <Typography variant="body2">Tax:</Typography>
                    <Typography variant="body2">
                      {isCalculating ? '...' : formatCurrency(invoice.taxTotal || 0)}
                    </Typography>
                  </Box>
                  
                  <Box sx={{ width: '100%' }}>
                    <Divider sx={{ my: 1 }} />
                  </Box>
                  
                  <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
                    <Typography variant="subtitle1"><strong>Total:</strong></Typography>
                    <Typography variant="subtitle1">
                      <strong>{isCalculating ? '...' : formatCurrency(invoice.total || 0)}</strong>
                    </Typography>
                  </Box>
                </Box>
              </Box>
            </Box>
          </Box>

          {/* Notes */}
          <Box>
            <Typography variant="subtitle1" gutterBottom>
              Notes
            </Typography>
            <TextField
              name="notes"
              value={invoice.notes || ''}
              onChange={handleChange}
              multiline
              rows={3}
              fullWidth
              placeholder="Add any notes or payment instructions here"
            />
          </Box>
        </Box>
      </Paper>      <FormButtons
        onCancel={handleCancel}
        onSubmit={handleSubmit}
        loading={loading}
        submitText={isEditMode ? 'Update Invoice' : 'Create Invoice'}
      />

      <Dialog open={showConfirmDialog} onClose={handleCloseConfirmDialog}>
        <DialogTitle>Discard Changes?</DialogTitle>
        <DialogContent>
          <Typography>
            You have unsaved changes. Are you sure you want to discard them?
          </Typography>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseConfirmDialog}>Cancel</Button>
          <Button onClick={handleConfirmCancel} color="error">
            Discard Changes
          </Button>
        </DialogActions>
      </Dialog>
    </FormContainer>
  );
};

export default InvoiceForm;

import React, { useReducer, useEffect, useCallback } from 'react';
import { 
  Box, 
  Typography, 
  Chip,
  Button,
  TextField,
  InputAdornment,
  IconButton,
  Menu,
  MenuItem,
  FormControl,
  InputLabel,
  Select,
  Stack,
  SelectChangeEvent
} from '@mui/material';
import {
  Add as AddIcon,
  Search as SearchIcon,
  FilterList as FilterListIcon,
  GetApp as DownloadIcon,
  CalendarToday as CalendarIcon,
  Refresh as RefreshIcon
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import { format } from 'date-fns';
import DataTable from '../DataTable';
import { getInvoices, InvoiceStatus, PaymentTerm, type Invoice, type InvoiceFilters } from '../../../services/api/invoiceService';

interface InvoiceListProps {
  companyId?: string;
}

// Define state types
interface InvoiceListState {
  invoices: Invoice[];
  loading: boolean;
  error: string | null;
  filters: InvoiceFilters;
  totalInvoices: number;
  searchTerm: string;
  filterMenuAnchor: null | HTMLElement;
  statusFilter: InvoiceStatus | 'all';
  dateRange: {
    startDate: string;
    endDate: string;
  };
}

// Define action types
type InvoiceListAction =
  | { type: 'SET_INVOICES'; payload: { invoices: Invoice[]; total: number } }
  | { type: 'SET_LOADING'; payload: boolean }
  | { type: 'SET_ERROR'; payload: string | null }
  | { type: 'UPDATE_FILTERS'; payload: Partial<InvoiceFilters> }
  | { type: 'SET_SEARCH_TERM'; payload: string }
  | { type: 'SET_FILTER_MENU_ANCHOR'; payload: null | HTMLElement }
  | { type: 'SET_STATUS_FILTER'; payload: InvoiceStatus | 'all' }
  | { type: 'SET_DATE_RANGE'; payload: { field: 'startDate' | 'endDate'; value: string } }
  | { type: 'RESET_FILTERS' };

// Define initial state
const initialState: InvoiceListState = {
  invoices: [],
  loading: false,
  error: null,
  filters: {
    page: 1,
    limit: 10,
    sortBy: 'createdAt',
    sortOrder: 'desc'
  },
  totalInvoices: 0,
  searchTerm: '',
  filterMenuAnchor: null,
  statusFilter: 'all',
  dateRange: {
    startDate: '',
    endDate: ''
  }
};

// Define reducer function
const invoiceListReducer = (state: InvoiceListState, action: InvoiceListAction): InvoiceListState => {
  switch (action.type) {
    case 'SET_INVOICES':
      return {
        ...state,
        invoices: action.payload.invoices,
        totalInvoices: action.payload.total
      };
    case 'SET_LOADING':
      return {
        ...state,
        loading: action.payload
      };
    case 'SET_ERROR':
      return {
        ...state,
        error: action.payload
      };
    case 'UPDATE_FILTERS':
      return {
        ...state,
        filters: {
          ...state.filters,
          ...action.payload
        }
      };
    case 'SET_SEARCH_TERM':
      return {
        ...state,
        searchTerm: action.payload
      };
    case 'SET_FILTER_MENU_ANCHOR':
      return {
        ...state,
        filterMenuAnchor: action.payload
      };
    case 'SET_STATUS_FILTER':
      return {
        ...state,
        statusFilter: action.payload
      };
    case 'SET_DATE_RANGE':
      return {
        ...state,
        dateRange: {
          ...state.dateRange,
          [action.payload.field]: action.payload.value
        }
      };
    case 'RESET_FILTERS':
      return {
        ...state,
        statusFilter: 'all',
        dateRange: { startDate: '', endDate: '' },
        searchTerm: '',
        filters: {
          page: 1,
          limit: 10,
          sortBy: 'createdAt',
          sortOrder: 'desc'
        }
      };
    default:
      return state;
  }
};

const InvoiceList: React.FC<InvoiceListProps> = ({ companyId }) => {
  const navigate = useNavigate();
  const [state, dispatch] = useReducer(invoiceListReducer, initialState);
  useEffect(() => {
    fetchInvoices();
  }, [state.filters, companyId]);

  const fetchInvoices = useCallback(async () => {
    dispatch({ type: 'SET_LOADING', payload: true });
    dispatch({ type: 'SET_ERROR', payload: null });
    try {
      // Apply filters
      const apiFilters: InvoiceFilters = {
        ...state.filters
      };
      
      if (state.statusFilter !== 'all') {
        apiFilters.status = state.statusFilter;
      }
      
      if (state.dateRange.startDate) {
        apiFilters.startDate = state.dateRange.startDate;
      }
      
      if (state.dateRange.endDate) {
        apiFilters.endDate = state.dateRange.endDate;
      }
      
      if (state.searchTerm) {
        // Search by invoice number or customer name - backend should handle this
        // This is a placeholder - actual implementation depends on backend search capability
        apiFilters.sortBy = state.searchTerm;
      }

      console.log('Fetching invoices with filters:', apiFilters);

      const response = await getInvoices(apiFilters);
      dispatch({ 
        type: 'SET_INVOICES', 
        payload: { 
          invoices: response.data, 
          total: response.pagination?.totalPages || 0 
        } 
      });
    } catch (err: any) {
      dispatch({ type: 'SET_ERROR', payload: err.message || 'Failed to fetch invoices' });
      console.error('Error fetching invoices:', err);
    } finally {
      dispatch({ type: 'SET_LOADING', payload: false });
    }
  }, [state.filters, state.statusFilter, state.dateRange, state.searchTerm, companyId]);

  const handleCreateInvoice = () => {
    navigate('/invoices/create');
  };

  const handleViewInvoice = (id: string) => {
    navigate(`/invoices/${id}`);
  };
  const handleFilterOpen = (event: React.MouseEvent<HTMLElement>) => {
    dispatch({ type: 'SET_FILTER_MENU_ANCHOR', payload: event.currentTarget });
  };

  const handleFilterClose = () => {
    dispatch({ type: 'SET_FILTER_MENU_ANCHOR', payload: null });
  };

  const handleStatusFilterChange = (event: SelectChangeEvent) => {
    dispatch({ type: 'SET_STATUS_FILTER', payload: event.target.value as InvoiceStatus | 'all' });
  };

  const handleDateRangeChange = (field: 'startDate' | 'endDate', value: string) => {
    dispatch({ type: 'SET_DATE_RANGE', payload: { field, value } });
  };

  const handleApplyFilters = () => {
    dispatch({ type: 'UPDATE_FILTERS', payload: { page: 1 } }); // Reset to first page when applying new filters
    handleFilterClose();
    fetchInvoices();
  };

  const handleResetFilters = () => {
    dispatch({ type: 'RESET_FILTERS' });
    handleFilterClose();
  };

  const handlePageChange = (page: number) => {
    dispatch({ type: 'UPDATE_FILTERS', payload: { page } });
  };

  const handleSearchChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    dispatch({ type: 'SET_SEARCH_TERM', payload: event.target.value });
  };

  const handleSearch = () => {
    // Reset to first page when searching
    dispatch({ type: 'UPDATE_FILTERS', payload: { page: 1 } });
    fetchInvoices();
  };

  const getStatusChipColor = (status: InvoiceStatus) => {
    switch (status) {
      case InvoiceStatus.PAID:
        return 'success';
      case InvoiceStatus.PENDING:
        return 'warning';
      case InvoiceStatus.OVERDUE:
        return 'error';
      case InvoiceStatus.PARTIALLY_PAID:
        return 'info';
      case InvoiceStatus.CANCELLED:
        return 'default';
      default:
        return 'default';
    }
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', { style: 'currency', currency: 'USD' }).format(amount);
  };

  const columns = [
    {
      id: 'invoiceNumber',
      label: 'Invoice #',
      accessor: 'invoiceNumber',
      cell: (row: Invoice) => (
        <Typography variant="body2" fontWeight="medium">
          {row.invoiceNumber}
        </Typography>
      )
    },
    {
      id: 'customer',
      label: 'Customer',
      accessor: 'customerId',
      cell: (row: Invoice) => (
        <Typography variant="body2">
          {row.customerId.substring(0, 10)}... {/* This should be replaced with actual customer name */}
        </Typography>
      )
    },
    {
      id: 'amount',
      label: 'Amount',
      accessor: 'total',
      cell: (row: Invoice) => (
        <Typography variant="body2">
          {row.total ? formatCurrency(row.total) : '-'}
        </Typography>
      )
    },
    {
      id: 'issueDate',
      label: 'Issue Date',
      accessor: 'issueDate',
      cell: (row: Invoice) => (
        <Typography variant="body2">
          {row.issueDate ? format(new Date(row.issueDate), 'MMM dd, yyyy') : '-'}
        </Typography>
      )
    },
    {
      id: 'dueDate',
      label: 'Due Date',
      accessor: 'dueDate',
      cell: (row: Invoice) => (
        <Typography variant="body2">
          {row.dueDate ? format(new Date(row.dueDate), 'MMM dd, yyyy') : '-'}
        </Typography>
      )
    },
    {
      id: 'status',
      label: 'Status',
      accessor: 'status',
      cell: (row: Invoice) => (
        <Chip 
          label={row.status ? row.status.replace('_', ' ').toUpperCase() : 'DRAFT'} 
          size="small" 
          color={getStatusChipColor(row.status as InvoiceStatus)}
        />
      )
    },
    {
      id: 'actions',
      label: 'Actions',
      accessor: 'id',
      cell: (row: Invoice) => (
        <Button
          variant="outlined"
          size="small"
          onClick={() => handleViewInvoice(row.id!)}
        >
          View
        </Button>
      )
    }
  ];
  // Filter panel JSX
  const filterPanel = (
    <Box sx={{ p: 2, mb: 3, borderRadius: 1, border: '1px solid #e0e0e0' }}>
      <Box display="flex" justifyContent="space-between" alignItems="center" mb={2}>
        <TextField
          placeholder="Search invoices..."
          value={state.searchTerm}
          onChange={handleSearchChange}
          onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
          sx={{ width: '40%' }}
          InputProps={{
            startAdornment: (
              <InputAdornment position="start">
                <SearchIcon />
              </InputAdornment>
            ),
            endAdornment: (
              <InputAdornment position="end">
                <IconButton onClick={handleSearch}>
                  <SearchIcon />
                </IconButton>
              </InputAdornment>
            )
          }}
        />
        <Box>
          <Button
            startIcon={<RefreshIcon />}
            onClick={fetchInvoices}
            sx={{ mr: 1 }}
          >
            Refresh
          </Button>
          <Button
            startIcon={<FilterListIcon />}
            onClick={handleFilterOpen}
            variant="outlined"
          >
            Filters
          </Button>
          <Menu
            anchorEl={state.filterMenuAnchor}
            open={Boolean(state.filterMenuAnchor)}
            onClose={handleFilterClose}
            PaperProps={{
              sx: { width: 300, p: 2 }
            }}
          >
            <Typography variant="subtitle1" gutterBottom>
              Filter Invoices
            </Typography>
            
            <FormControl fullWidth margin="normal">
              <InputLabel>Status</InputLabel>
              <Select
                value={state.statusFilter}
                onChange={handleStatusFilterChange}
                label="Status"
              >
                <MenuItem value="all">All</MenuItem>
                <MenuItem value={InvoiceStatus.DRAFT}>Draft</MenuItem>
                <MenuItem value={InvoiceStatus.PENDING}>Pending</MenuItem>
                <MenuItem value={InvoiceStatus.PAID}>Paid</MenuItem>
                <MenuItem value={InvoiceStatus.PARTIALLY_PAID}>Partially Paid</MenuItem>
                <MenuItem value={InvoiceStatus.OVERDUE}>Overdue</MenuItem>
                <MenuItem value={InvoiceStatus.CANCELLED}>Cancelled</MenuItem>
              </Select>
            </FormControl>
            
            <Typography variant="subtitle2" sx={{ mt: 2 }}>
              Date Range
            </Typography>
            
            <Stack spacing={2} sx={{ mt: 1 }}>
              <TextField
                label="From"
                type="date"
                value={state.dateRange.startDate}
                onChange={(e) => handleDateRangeChange('startDate', e.target.value)}
                InputLabelProps={{ shrink: true }}
                fullWidth
              />
              <TextField
                label="To"
                type="date"
                value={state.dateRange.endDate}
                onChange={(e) => handleDateRangeChange('endDate', e.target.value)}
                InputLabelProps={{ shrink: true }}
                fullWidth
              />
            </Stack>
            
            <Stack direction="row" spacing={1} sx={{ mt: 3 }}>
              <Button
                variant="outlined"
                onClick={handleResetFilters}
                fullWidth
              >
                Reset
              </Button>
              <Button
                variant="contained"
                onClick={handleApplyFilters}
                fullWidth
              >
                Apply
              </Button>
            </Stack>
          </Menu>
        </Box>
      </Box>
    </Box>
  );
  return (
    <Box>
      {/* Filter panel only, no page title/header here */}
      <Box sx={{ mb: 2 }}>
        {filterPanel}
      </Box>
      <DataTable
        columns={columns}
        data={state.invoices}
        loading={state.loading}
        error={state.error}
        pagination={{
          page: state.filters.page || 1,
          rowsPerPage: state.filters.limit || 10,
          totalCount: state.totalInvoices,
          onPageChange: handlePageChange,
          onRowsPerPageChange: (pageSize) => {
            dispatch({ 
              type: 'UPDATE_FILTERS', 
              payload: {
                limit: pageSize,
                page: 1 // Reset to first page when changing page size
              }
            });
          }
        }}
        onRowClick={(row: Invoice) => handleViewInvoice(row.id!)}
      />
    </Box>
  );
};

export default InvoiceList;

import React, { useState, useEffect } from 'react';
import {
  Box,
  Paper,
  Typography,
  TextField,
  Button,
  Divider,
  IconButton,
  MenuItem,
  FormControl,
  InputLabel,
  Select,
  FormHelperText,
  CircularProgress,
  Alert,
  Autocomplete,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import AddIcon from '@mui/icons-material/Add';
import { orderService, productService, warehouseService } from '../../../services/api';

interface CreateOrderFormProps {
  companyId: string;
  onSave: (orderData: any) => void;
  onCancel: () => void;
}

const CreateOrderForm: React.FC<CreateOrderFormProps> = ({
  companyId,
  onSave,
  onCancel
}) => {
  // Form data
  const [formData, setFormData] = useState({
    customer_name: '',
    customer_email: '',
    customer_phone: '',
    shipping_address: {
      street: '',
      city: '',
      state: '',
      zip: '',
      country: 'USA'
    },
    payment_method: 'credit_card',
    payment_status: 'pending',
    warehouse_id: '',
    notes: '',
  });

  // Order items
  const [orderItems, setOrderItems] = useState<Array<{
    product_id: string;
    product_name: string;
    sku: string;
    price: number;
    quantity: number;
  }>>([]);

  // Available products for selection
  const [availableProducts, setAvailableProducts] = useState<Array<any>>([]);
  const [warehouses, setWarehouses] = useState<Array<any>>([]);
  
  // Loading and error states
  const [loading, setLoading] = useState(false);
  const [productLoading, setProductLoading] = useState(false);
  const [warehouseLoading, setWarehouseLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [formErrors, setFormErrors] = useState<Record<string, string>>({});

  // Fetch available products and warehouses when component mounts
  useEffect(() => {
    fetchProducts();
    fetchWarehouses();
  }, []);

  // Fixing the property name for productService.getProducts
  const fetchProducts = async () => {
    setProductLoading(true);
    try {
      const response = await productService.getProducts({
        status: 'active', // Example filter for active products
        search: '', // Optional search query
      });
      setAvailableProducts(response.products || []); // Adjusted to use 'products'
    } catch (err: any) {
      console.error('Error fetching products:', err);
    } finally {
      setProductLoading(false);
    }
  };
  // Fixing the warehouse data extraction from API response
  const fetchWarehouses = async () => {
    setWarehouseLoading(true);
    try {
      const response = await warehouseService.getWarehouses(companyId); // Pass companyId directly
      console.log('Warehouse API response:', response);
      
      // Handle different potential response structures
      let warehousesList: Array<any> = [];
      
      // If response.data is an array
      if (Array.isArray(response.data)) {
        warehousesList = response.data;
      } 
      // If response.data contains a warehouses property that is an array
      else if (response.data && typeof response.data === 'object' && 'warehouses' in response.data && 
              Array.isArray((response.data as any).warehouses)) {
        warehousesList = (response.data as any).warehouses;
      }
      // If response.data.data contains the warehouses array
      else if (response.data && typeof response.data === 'object' && 'data' in response.data && 
              Array.isArray((response.data as any).data)) {
        warehousesList = (response.data as any).data;
      }
      // Default to empty array if no matching structure is found
      else {
        console.error('Unexpected API response format:', response);
        warehousesList = [];
      }
      
      // Set warehouses state with the extracted array
      setWarehouses(warehousesList);

      // Set default warehouse if available
      if (warehousesList.length > 0) {
        setFormData(prev => ({ ...prev, warehouse_id: warehousesList[0].id }));
      }
    } catch (err: any) {
      console.error('Error fetching warehouses:', err);
      // Always set warehouses to an empty array on error
      setWarehouses([]);
    } finally {
      setWarehouseLoading(false);
    }
  };

  const handleFormChange = (field: string, value: any) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    // Clear validation error when field is changed
    if (formErrors[field]) {
      setFormErrors(prev => ({ ...prev, [field]: '' }));
    }
  };

  const handleAddressChange = (field: string, value: string) => {
    setFormData(prev => ({
      ...prev,
      shipping_address: { ...prev.shipping_address, [field]: value }
    }));
    // Clear validation error
    if (formErrors[`shipping_address.${field}`]) {
      setFormErrors(prev => ({ ...prev, [`shipping_address.${field}`]: '' }));
    }
  };

  const handleAddProduct = () => {
    setOrderItems(prev => [
      ...prev,
      {
        product_id: '',
        product_name: '',
        sku: '',
        price: 0,
        quantity: 1
      }
    ]);
  };

  const handleRemoveProduct = (index: number) => {
    setOrderItems(prev => prev.filter((_, i) => i !== index));
  };

  const handleProductSelect = (index: number, productId: string) => {
    const selectedProduct = availableProducts.find(p => p.id === productId);
    
    if (selectedProduct) {
      const updatedItems = [...orderItems];
      updatedItems[index] = {
        product_id: selectedProduct.id,
        product_name: selectedProduct.name,
        sku: selectedProduct.sku || '',
        price: selectedProduct.price || 0,
        quantity: 1
      };
      setOrderItems(updatedItems);
    }
  };

  const handleProductQuantityChange = (index: number, quantity: number) => {
    const updatedItems = [...orderItems];
    updatedItems[index] = {
      ...updatedItems[index],
      quantity: quantity
    };
    setOrderItems(updatedItems);
  };

  const validateForm = () => {
    const errors: Record<string, string> = {};
    
    // Basic validation
    if (!formData.customer_name.trim()) {
      errors['customer_name'] = 'Customer name is required';
    }
    
    if (!formData.customer_email.trim()) {
      errors['customer_email'] = 'Customer email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.customer_email)) {
      errors['customer_email'] = 'Invalid email format';
    }
    
    if (!formData.shipping_address.street.trim()) {
      errors['shipping_address.street'] = 'Street address is required';
    }
    
    if (!formData.shipping_address.city.trim()) {
      errors['shipping_address.city'] = 'City is required';
    }
    
    if (!formData.shipping_address.zip.trim()) {
      errors['shipping_address.zip'] = 'ZIP code is required';
    }
    
    if (!formData.warehouse_id) {
      errors['warehouse_id'] = 'Please select a warehouse';
    }
    
    // Order items validation
    if (orderItems.length === 0) {
      errors['orderItems'] = 'Please add at least one product to the order';
    } else {
      orderItems.forEach((item, index) => {
        if (!item.product_id) {
          errors[`orderItems[${index}].product_id`] = 'Please select a product';
        }
        if (item.quantity < 1) {
          errors[`orderItems[${index}].quantity`] = 'Quantity must be at least 1';
        }
      });
    }
    
    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  // Fixing the orderService.createOrder call
  const handleSubmit = async () => {
    if (!validateForm()) {
      return;
    }
    
    setLoading(true);
    setError(null);
    
    try {
      const orderData = {
        ...formData,
        company_id: companyId, // Corrected property name
        deliveryAddress: {
          ...formData.shipping_address,
          zipCode: formData.shipping_address.zip // Renamed 'zip' to 'zipCode'
        },
        paymentMethod: formData.payment_method, // Added missing property
        items: orderItems.map(item => ({
          productId: item.product_id, // Corrected property name
          quantity: item.quantity,
          discount: 0 // Assuming no discount for now
        }))
      };
      
      const response = await orderService.createOrder(orderData);
      onSave(response);
    } catch (err: any) {
      console.error('Error creating order:', err);
      setError(err.message || 'Failed to create order');
    } finally {
      setLoading(false);
    }
  };

  const formatCurrency = (value: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
    }).format(value);
  };

  return (
    <Box>
      <Typography variant="h5" gutterBottom>
        Create New Order
      </Typography>
      
      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}
      
      {formErrors['orderItems'] && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {formErrors['orderItems']}
        </Alert>
      )}
      
      <Paper sx={{ p: 3, mb: 3 }}>
        <Typography variant="h6" gutterBottom>
          Customer Information
        </Typography>
        
        <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 2, mb: 3 }}>
          <TextField
            label="Customer Name"
            value={formData.customer_name}
            onChange={(e) => handleFormChange('customer_name', e.target.value)}
            error={!!formErrors['customer_name']}
            helperText={formErrors['customer_name']}
            fullWidth
            sx={{ flex: '1 1 300px' }}
          />
          
          <TextField
            label="Email"
            type="email"
            value={formData.customer_email}
            onChange={(e) => handleFormChange('customer_email', e.target.value)}
            error={!!formErrors['customer_email']}
            helperText={formErrors['customer_email']}
            fullWidth
            sx={{ flex: '1 1 300px' }}
          />
          
          <TextField
            label="Phone"
            value={formData.customer_phone}
            onChange={(e) => handleFormChange('customer_phone', e.target.value)}
            fullWidth
            sx={{ flex: '1 1 300px' }}
          />
        </Box>
        
        <Typography variant="subtitle1" gutterBottom>
          Shipping Address
        </Typography>
        
        <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 2 }}>
          <TextField
            label="Street Address"
            value={formData.shipping_address.street}
            onChange={(e) => handleAddressChange('street', e.target.value)}
            error={!!formErrors['shipping_address.street']}
            helperText={formErrors['shipping_address.street']}
            fullWidth
            sx={{ flex: '1 1 100%' }}
          />
          
          <TextField
            label="City"
            value={formData.shipping_address.city}
            onChange={(e) => handleAddressChange('city', e.target.value)}
            error={!!formErrors['shipping_address.city']}
            helperText={formErrors['shipping_address.city']}
            sx={{ flex: '1 1 200px' }}
          />
          
          <TextField
            label="State/Province"
            value={formData.shipping_address.state}
            onChange={(e) => handleAddressChange('state', e.target.value)}
            sx={{ flex: '1 1 150px' }}
          />
          
          <TextField
            label="ZIP/Postal Code"
            value={formData.shipping_address.zip}
            onChange={(e) => handleAddressChange('zip', e.target.value)}
            error={!!formErrors['shipping_address.zip']}
            helperText={formErrors['shipping_address.zip']}
            sx={{ flex: '1 1 150px' }}
          />
          
          <TextField
            label="Country"
            value={formData.shipping_address.country}
            onChange={(e) => handleAddressChange('country', e.target.value)}
            sx={{ flex: '1 1 200px' }}
          />
        </Box>
      </Paper>
      
      <Paper sx={{ p: 3, mb: 3 }}>
        <Typography variant="h6" gutterBottom>
          Order Items
        </Typography>
        
        {orderItems.length > 0 ? (
          <TableContainer>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Product</TableCell>
                  <TableCell>Price</TableCell>
                  <TableCell>Quantity</TableCell>
                  <TableCell>Total</TableCell>
                  <TableCell width={50}></TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {orderItems.map((item, index) => (
                  <TableRow key={index}>
                    <TableCell>
                      <Autocomplete
                        options={availableProducts}
                        getOptionLabel={(option) => option.name || ''}
                        loading={productLoading}
                        onChange={(_, value) => handleProductSelect(index, value?.id || '')}
                        renderInput={(params) => (
                          <TextField
                            {...params}
                            label="Select Product"
                            error={!!formErrors[`orderItems[${index}].product_id`]}
                            helperText={formErrors[`orderItems[${index}].product_id`]}
                          />
                        )}
                      />
                      {item.sku && (
                        <Typography variant="caption" color="text.secondary">
                          SKU: {item.sku}
                        </Typography>
                      )}
                    </TableCell>
                    <TableCell>{formatCurrency(item.price)}</TableCell>
                    <TableCell>
                      <TextField
                        type="number"
                        value={item.quantity}
                        onChange={(e) => handleProductQuantityChange(index, parseInt(e.target.value) || 0)}
                        inputProps={{ min: 1 }}
                        error={!!formErrors[`orderItems[${index}].quantity`]}
                        helperText={formErrors[`orderItems[${index}].quantity`]}
                        sx={{ width: 80 }}
                      />
                    </TableCell>
                    <TableCell>{formatCurrency(item.price * item.quantity)}</TableCell>
                    <TableCell>
                      <IconButton color="error" onClick={() => handleRemoveProduct(index)}>
                        <DeleteIcon />
                      </IconButton>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        ) : (
          <Typography variant="body1" color="text.secondary" sx={{ my: 3, textAlign: 'center' }}>
            No products added to this order yet.
          </Typography>
        )}
        
        <Button
          startIcon={<AddIcon />}
          onClick={handleAddProduct}
          sx={{ mt: 2 }}
        >
          Add Product
        </Button>
      </Paper>
      
      <Paper sx={{ p: 3, mb: 3 }}>
        <Typography variant="h6" gutterBottom>
          Order Details
        </Typography>
        
        <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 2 }}>
          <FormControl sx={{ flex: '1 1 200px' }} error={!!formErrors['warehouse_id']}>
            <InputLabel>Warehouse</InputLabel>
            <Select
              value={formData.warehouse_id}
              label="Warehouse"
              onChange={(e) => handleFormChange('warehouse_id', e.target.value)}
              disabled={warehouseLoading}
            >              {Array.isArray(warehouses) ? warehouses.map(warehouse => (
                <MenuItem key={warehouse.id} value={warehouse.id}>
                  {warehouse.name}
                </MenuItem>
              )) : (
                <MenuItem value="">No warehouses available</MenuItem>
              )}
            </Select>
            {formErrors['warehouse_id'] && (
              <FormHelperText>{formErrors['warehouse_id']}</FormHelperText>
            )}
          </FormControl>
          
          <FormControl sx={{ flex: '1 1 200px' }}>
            <InputLabel>Payment Method</InputLabel>
            <Select
              value={formData.payment_method}
              label="Payment Method"
              onChange={(e) => handleFormChange('payment_method', e.target.value)}
            >
              <MenuItem value="credit_card">Credit Card</MenuItem>
              <MenuItem value="paypal">PayPal</MenuItem>
              <MenuItem value="bank_transfer">Bank Transfer</MenuItem>
              <MenuItem value="cash">Cash</MenuItem>
            </Select>
          </FormControl>
          
          <FormControl sx={{ flex: '1 1 200px' }}>
            <InputLabel>Payment Status</InputLabel>
            <Select
              value={formData.payment_status}
              label="Payment Status"
              onChange={(e) => handleFormChange('payment_status', e.target.value)}
            >
              <MenuItem value="pending">Pending</MenuItem>
              <MenuItem value="paid">Paid</MenuItem>
              <MenuItem value="failed">Failed</MenuItem>
            </Select>
          </FormControl>
        </Box>
        
        <TextField
          label="Order Notes (Optional)"
          multiline
          rows={3}
          value={formData.notes}
          onChange={(e) => handleFormChange('notes', e.target.value)}
          fullWidth
          sx={{ mt: 2 }}
        />
      </Paper>
      
      <Box sx={{ display: 'flex', justifyContent: 'flex-end', gap: 2 }}>
        <Button 
          variant="outlined" 
          onClick={onCancel}
          disabled={loading}
        >
          Cancel
        </Button>
        <Button 
          variant="contained" 
          color="primary" 
          onClick={handleSubmit}
          disabled={loading}
        >
          {loading ? <CircularProgress size={24} /> : 'Create Order'}
        </Button>
      </Box>
    </Box>
  );
};

export default CreateOrderForm;

import React, { useReducer, useEffect, useCallback } from 'react';
import { 
  Box, 
  Typography, 
  Paper, 
  Table, 
  TableBody, 
  TableCell, 
  TableContainer, 
  TableHead, 
  TableRow,
  TablePagination,
  Chip,
  IconButton,
  TextField,
  InputAdornment,
  MenuItem,
  Select,
  FormControl,
  InputLabel,
  Button,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
  Skeleton,
  SelectChangeEvent
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import FilterListIcon from '@mui/icons-material/FilterList';
import VisibilityIcon from '@mui/icons-material/Visibility';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import { orderService } from '../../../services/api';
import { formatCurrency, formatDate } from '../../../utils/formatters';

// Order status colors
const STATUS_COLORS = {
  pending: { color: 'warning', label: 'Pending' },
  processing: { color: 'info', label: 'Processing' },
  shipped: { color: 'primary', label: 'Shipped' },
  delivered: { color: 'success', label: 'Delivered' },
  cancelled: { color: 'error', label: 'Cancelled' },
  refunded: { color: 'default', label: 'Refunded' },
};

// Types
interface OrderResponse {
  items: Order[];
  meta: {
    total: number;
    page: number;
    limit: number;
  };
}

interface Order {
  id: string;
  order_number: string;
  customer: {
    id: string;
    name: string;
    email: string;
  };
  status: keyof typeof STATUS_COLORS;
  total: number;
  created_at: string;
  items_count: number;
}

interface OrderListProps {
  companyId: string;
  onViewOrder?: (orderId: string) => void;
  onEditOrder?: (orderId: string) => void;
  onDeleteOrder?: (orderId: string) => void;
  allowEdit?: boolean;
  allowDelete?: boolean;
}

// Define OrderList state type
interface OrderListState {
  orders: Order[];
  loading: boolean;
  page: number;
  rowsPerPage: number;
  totalCount: number;
  searchQuery: string;
  statusFilter: string;
  sortBy: string;
  sortDirection: 'asc' | 'desc';
  error: string | null;
  deleteDialogOpen: boolean;
  selectedOrderId: string | null;
  isSearching: boolean;
  searchAbortController: AbortController | null;
}

// Define the initial state
const initialState: OrderListState = {
  orders: [],
  loading: false,
  page: 0,
  rowsPerPage: 10,
  totalCount: 0,
  searchQuery: '',
  statusFilter: 'all',
  sortBy: 'created_at',
  sortDirection: 'desc',
  error: null,
  deleteDialogOpen: false,
  selectedOrderId: null,
  isSearching: false,
  searchAbortController: null
};

// Define action types
type OrderListAction =
  | { type: 'SET_ORDERS'; payload: { orders: Order[]; totalCount: number } }
  | { type: 'SET_LOADING'; payload: boolean }
  | { type: 'SET_PAGE'; payload: number }
  | { type: 'SET_ROWS_PER_PAGE'; payload: number }
  | { type: 'SET_SEARCH_QUERY'; payload: string }
  | { type: 'SET_STATUS_FILTER'; payload: string }
  | { type: 'SET_SORT_BY'; payload: string }
  | { type: 'SET_SORT_DIRECTION'; payload: 'asc' | 'desc' }
  | { type: 'SET_ERROR'; payload: string | null }
  | { type: 'SET_DELETE_DIALOG_OPEN'; payload: boolean }
  | { type: 'SET_SELECTED_ORDER_ID'; payload: string | null }
  | { type: 'SET_IS_SEARCHING'; payload: boolean }
  | { type: 'SET_SEARCH_ABORT_CONTROLLER'; payload: AbortController | null };

// Define the reducer function
const orderListReducer = (state: OrderListState, action: OrderListAction): OrderListState => {
  switch (action.type) {
    case 'SET_ORDERS':
      return {
        ...state,
        orders: action.payload.orders,
        totalCount: action.payload.totalCount
      };
    case 'SET_LOADING':
      return {
        ...state,
        loading: action.payload
      };
    case 'SET_PAGE':
      return {
        ...state,
        page: action.payload
      };
    case 'SET_ROWS_PER_PAGE':
      return {
        ...state,
        rowsPerPage: action.payload,
        page: 0 // Reset to first page when changing rows per page
      };
    case 'SET_SEARCH_QUERY':
      return {
        ...state,
        searchQuery: action.payload
      };
    case 'SET_STATUS_FILTER':
      return {
        ...state,
        statusFilter: action.payload
      };
    case 'SET_SORT_BY':
      return {
        ...state,
        sortBy: action.payload
      };
    case 'SET_SORT_DIRECTION':
      return {
        ...state,
        sortDirection: action.payload
      };
    case 'SET_ERROR':
      return {
        ...state,
        error: action.payload
      };
    case 'SET_DELETE_DIALOG_OPEN':
      return {
        ...state,
        deleteDialogOpen: action.payload
      };
    case 'SET_SELECTED_ORDER_ID':
      return {
        ...state,
        selectedOrderId: action.payload
      };
    case 'SET_IS_SEARCHING':
      return {
        ...state,
        isSearching: action.payload
      };
    case 'SET_SEARCH_ABORT_CONTROLLER':
      return {
        ...state,
        searchAbortController: action.payload
      };
    default:
      return state;
  }
};

const OrderList: React.FC<OrderListProps> = ({
  companyId,
  onViewOrder,
  onEditOrder,
  onDeleteOrder,
  allowEdit = true,
  allowDelete = true,
}) => {
  // Use the reducer for state management
  const [state, dispatch] = useReducer(orderListReducer, initialState);
    // Memoize fetchOrders to prevent unnecessary recreations
  const fetchOrders = useCallback(async (isSearchRequest = false) => {
    if (!companyId) return;
    
    dispatch({ type: 'SET_LOADING', payload: true });
    dispatch({ type: 'SET_ERROR', payload: null });

    try {
      const params = {
        page: state.page + 1,
        limit: state.rowsPerPage,
        status: state.statusFilter !== 'all' ? state.statusFilter : undefined,
        paymentStatus: undefined,
        fromDate: undefined,
        toDate: undefined
      };

      if (isSearchRequest && state.isSearching) {
        return;
      }

      if (isSearchRequest) {
        dispatch({ type: 'SET_IS_SEARCHING', payload: true });
      }

      const response = await orderService.getOrders(params);
      
      // Safely handle the response based on the API structure
      const orders = Array.isArray(response) ? response : (response as any).orders || [];
      const totalItems = typeof (response as any).count === 'number' ? (response as any).count : orders.length;
      
      dispatch({ 
        type: 'SET_ORDERS', 
        payload: { 
          orders: orders,
          totalCount: totalItems
        }
      });
      dispatch({ type: 'SET_ERROR', payload: null });
    } catch (err: any) {
      dispatch({ type: 'SET_ERROR', payload: err.message || 'Failed to fetch orders' });
      console.error('Error fetching orders:', err);
      dispatch({ 
        type: 'SET_ORDERS', 
        payload: { 
          orders: [],
          totalCount: 0
        }
      });
    } finally {
      dispatch({ type: 'SET_LOADING', payload: false });
      if (isSearchRequest) {
        dispatch({ type: 'SET_IS_SEARCHING', payload: false });
      }
    }
  }, [companyId, state.page, state.rowsPerPage, state.statusFilter, state.isSearching]);
  // Load orders on initial render and when dependencies change
  useEffect(() => {
    if (!state.isSearching) { // Only fetch if not currently searching
      fetchOrders();
    }
  }, [fetchOrders]);

  // Debounce search query with cleanup
  useEffect(() => {
    if (!state.searchQuery && state.page !== 0) {
      dispatch({ type: 'SET_PAGE', payload: 0 });
      return;
    }

    const timer = setTimeout(() => {
      if (state.searchQuery) {
        fetchOrders(true);
      }
    }, 500);

    return () => {
      clearTimeout(timer);
    };
  }, [state.searchQuery, state.page, fetchOrders]);
  // Reset pagination when filters change
  useEffect(() => {
    if (state.page !== 0) {
      dispatch({ type: 'SET_PAGE', payload: 0 });
    }
  }, [state.statusFilter, state.sortBy, state.sortDirection]);
  // Handle page change
  const handleChangePage = (_: unknown, newPage: number) => {
    dispatch({ type: 'SET_PAGE', payload: newPage });
  };

  // Handle rows per page change with reset
  const handleChangeRowsPerPage = (event: React.ChangeEvent<HTMLInputElement>) => {
    const newRowsPerPage = parseInt(event.target.value, 10);
    dispatch({ type: 'SET_ROWS_PER_PAGE', payload: newRowsPerPage });
    // Page reset is handled in the reducer
  };

  // Handle status filter change with validation
  const handleStatusFilterChange = (event: SelectChangeEvent<string>) => {
    const newStatus = event.target.value;
    if (newStatus === state.statusFilter) return; // Prevent unnecessary updates
    
    dispatch({ type: 'SET_STATUS_FILTER', payload: newStatus });
    // Page reset is handled by the useEffect
  };

  // Handle sort change with validation
  const handleSortChange = (event: SelectChangeEvent<string>) => {
    const newSortBy = event.target.value;
    if (newSortBy === state.sortBy) return; // Prevent unnecessary updates
    
    dispatch({ type: 'SET_SORT_BY', payload: newSortBy });
    // Page reset is handled by the useEffect
  };

  // Optimized sort direction change
  const handleSortDirectionChange = () => {
    dispatch({ 
      type: 'SET_SORT_DIRECTION', 
      payload: state.sortDirection === 'asc' ? 'desc' : 'asc' 
    });
    // Page reset is handled by the useEffect
  };
  // Enhanced delete handling
  const handleDeleteClick = useCallback((orderId: string) => {
    dispatch({ type: 'SET_SELECTED_ORDER_ID', payload: orderId });
    dispatch({ type: 'SET_DELETE_DIALOG_OPEN', payload: true });
  }, []);

  // Handle confirm delete with proper cleanup
  const handleConfirmDelete = async () => {
    if (!state.selectedOrderId) return;
    
    dispatch({ type: 'SET_LOADING', payload: true });
    dispatch({ type: 'SET_ERROR', payload: null });
    
    try {
      await orderService.deleteOrder(state.selectedOrderId);
      
      // Update local state optimistically
      dispatch({ 
        type: 'SET_ORDERS', 
        payload: { 
          orders: state.orders.filter(order => order.id !== state.selectedOrderId),
          totalCount: Math.max(0, state.totalCount - 1)
        }
      });
      
      // Notify parent component
      if (onDeleteOrder && state.selectedOrderId) {
        onDeleteOrder(state.selectedOrderId);
      }

    } catch (err: any) {
      dispatch({ type: 'SET_ERROR', payload: err.message || 'Failed to delete order' });
      console.error('Error deleting order:', err);
      
      // Refresh orders to ensure consistent state
      fetchOrders();
    } finally {
      dispatch({ type: 'SET_DELETE_DIALOG_OPEN', payload: false });
      dispatch({ type: 'SET_SELECTED_ORDER_ID', payload: null });
      dispatch({ type: 'SET_LOADING', payload: false });
    }
  };
  // Handle search input changes with cleanup
  const handleSearchChange = useCallback((event: React.ChangeEvent<HTMLInputElement>) => {
    const newValue = event.target.value;
    
    // Cancel any pending search request
    if (state.searchAbortController) {
      state.searchAbortController.abort();
    }

    // Create new abort controller for this search
    const newController = new AbortController();
    dispatch({ type: 'SET_SEARCH_ABORT_CONTROLLER', payload: newController });
    
    dispatch({ type: 'SET_SEARCH_QUERY', payload: newValue });

    if (!newValue) {
      // Clear search results immediately when search is emptied
      dispatch({ 
        type: 'SET_ORDERS', 
        payload: { 
          orders: [],
          totalCount: 0
        }
      });
      fetchOrders(); // Fetch default orders
    }
  }, [state.searchAbortController, fetchOrders]);
  // Cleanup abort controller on unmount
  useEffect(() => {
    return () => {
      if (state.searchAbortController) {
        state.searchAbortController.abort();
      }
    };
  }, [state.searchAbortController]);
  // Render table skeleton while loading
  const renderSkeleton = () => (
    <TableBody>
      {Array.from(new Array(state.rowsPerPage)).map((_, index) => (
        <TableRow key={index}>
          <TableCell><Skeleton animation="wave" /></TableCell>
          <TableCell><Skeleton animation="wave" /></TableCell>
          <TableCell><Skeleton animation="wave" /></TableCell>
          <TableCell><Skeleton animation="wave" /></TableCell>
          <TableCell><Skeleton animation="wave" /></TableCell>
          <TableCell><Skeleton animation="wave" /></TableCell>
          <TableCell><Skeleton animation="wave" width={100} /></TableCell>
        </TableRow>
      ))}
    </TableBody>
  );

  return (
    <Box>
      <Paper elevation={0} sx={{ p: 3, mb: 3 }}>
        <Box sx={{ display: 'flex', flexDirection: 'row', gap: 2 }}>
          <Box sx={{ flex: 1 }}>
            <Typography variant="h6" component="h2">
              Orders
            </Typography>
          </Box>
          <Box sx={{ flex: 2 }}>            <TextField
              placeholder="Search orders..."
              size="small"
              fullWidth
              value={state.searchQuery}
              onChange={handleSearchChange}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <SearchIcon />
                  </InputAdornment>
                ),
              }}
            />
          </Box>
        </Box>
        
        <Box sx={{ display: 'flex', gap: 2, mt: 2 }}>
          <FormControl size="small" sx={{ minWidth: 120 }}>
            <InputLabel id="status-filter-label">Status</InputLabel>            <Select
              labelId="status-filter-label"
              value={state.statusFilter}
              onChange={handleStatusFilterChange}
              label="Status"
            >
              <MenuItem value="all">All Statuses</MenuItem>
              {Object.entries(STATUS_COLORS).map(([value, { label }]) => (
                <MenuItem key={value} value={value}>{label}</MenuItem>
              ))}
            </Select>
          </FormControl>
          
          <FormControl size="small" sx={{ minWidth: 150 }}>
            <InputLabel id="sort-by-label">Sort By</InputLabel>            <Select
              labelId="sort-by-label"
              value={state.sortBy}
              onChange={handleSortChange}
              label="Sort By"
            >
              <MenuItem value="created_at">Date</MenuItem>
              <MenuItem value="order_number">Order Number</MenuItem>
              <MenuItem value="customer.name">Customer Name</MenuItem>
              <MenuItem value="status">Status</MenuItem>
              <MenuItem value="total">Total</MenuItem>
            </Select>
          </FormControl>
          
          <Button
            variant="outlined"
            size="small"
            startIcon={<FilterListIcon />}
            onClick={handleSortDirectionChange}
            sx={{ minWidth: 120 }}
          >
            {state.sortDirection === 'asc' ? 'Ascending' : 'Descending'}
          </Button>
        </Box>
      </Paper>
        {state.error && (
        <Typography color="error" sx={{ mb: 2 }}>
          Error: {state.error}
        </Typography>
      )}
      
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Order #</TableCell>
              <TableCell>Customer</TableCell>
              <TableCell>Date</TableCell>
              <TableCell>Status</TableCell>
              <TableCell align="right">Items</TableCell>
              <TableCell align="right">Total</TableCell>
              <TableCell align="right">Actions</TableCell>
            </TableRow>
          </TableHead>
          
          {state.loading ? (
            renderSkeleton()
          ) : (
            <TableBody>
              {state.orders.length === 0 ? (
                <TableRow>
                  <TableCell colSpan={7} align="center" sx={{ py: 3 }}>
                    <Typography color="text.secondary">
                      No orders found
                    </Typography>
                  </TableCell>
                </TableRow>
              ) : (
                state.orders.map((order: Order) => {
                  const statusConfig = STATUS_COLORS[order.status] || STATUS_COLORS.pending;
                  
                  return (
                    <TableRow key={order.id} hover>
                      <TableCell>{order.order_number}</TableCell>
                      <TableCell>
                        <Typography variant="body2">{order.customer.name}</Typography>
                        <Typography variant="caption" color="text.secondary">
                          {order.customer.email}
                        </Typography>
                      </TableCell>
                      <TableCell>{formatDate(order.created_at)}</TableCell>
                      <TableCell>
                        <Chip 
                          label={statusConfig.label}
                          color={statusConfig.color as any}
                          size="small"
                        />
                      </TableCell>
                      <TableCell align="right">{order.items_count}</TableCell>
                      <TableCell align="right">{formatCurrency(order.total)}</TableCell>
                      <TableCell align="right">
                        <Box sx={{ display: 'flex', justifyContent: 'flex-end' }}>
                          {onViewOrder && (
                            <IconButton 
                              size="small"
                              onClick={() => onViewOrder(order.id)}
                              color="primary"
                            >
                              <VisibilityIcon fontSize="small" />
                            </IconButton>
                          )}
                          
                          {allowEdit && onEditOrder && (
                            <IconButton 
                              size="small"
                              onClick={() => onEditOrder(order.id)}
                              color="secondary"
                            >
                              <EditIcon fontSize="small" />
                            </IconButton>
                          )}
                          
                          {allowDelete && onDeleteOrder && (
                            <IconButton 
                              size="small"
                              onClick={() => handleDeleteClick(order.id)}
                              color="error"
                            >
                              <DeleteIcon fontSize="small" />
                            </IconButton>
                          )}
                        </Box>
                      </TableCell>
                    </TableRow>
                  );
                })
              )}
            </TableBody>
          )}
        </Table>
      </TableContainer>
        <TablePagination
        component="div"
        count={state.totalCount}
        page={state.page}
        onPageChange={handleChangePage}
        rowsPerPage={state.rowsPerPage}
        onRowsPerPageChange={handleChangeRowsPerPage}
        rowsPerPageOptions={[5, 10, 25, 50]}
      />
      
      {/* Delete Confirmation Dialog */}
      <Dialog
        open={state.deleteDialogOpen}
        onClose={() => dispatch({ type: 'SET_DELETE_DIALOG_OPEN', payload: false })}
      >
        <DialogTitle>Confirm Delete</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Are you sure you want to delete this order? This action cannot be undone.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => dispatch({ type: 'SET_DELETE_DIALOG_OPEN', payload: false })}>Cancel</Button>
          <Button onClick={handleConfirmDelete} color="error" autoFocus>
            Delete
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default OrderList;

import React, { useState, useEffect } from 'react';
import { 
  TextField, 
  Box, 
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  FormHelperText,
  InputAdornment,
  Button,
  Typography,
  Divider,
  Tabs,
  Tab,
  IconButton,
  Stack,
  SelectChangeEvent,
  Tooltip,
} from '@mui/material';
import AddPhotoAlternateIcon from '@mui/icons-material/AddPhotoAlternate';
import DeleteOutlineIcon from '@mui/icons-material/DeleteOutline';
import HelpOutlineIcon from '@mui/icons-material/HelpOutline';
import FormContainer from '../FormContainer';
import FormButtons from '../FormButtons';
import ProductPriceHistory from './ProductPriceHistory';
import { productService, companyService } from '../../../services/api';

// Types
interface TabPanelProps {
  children?: React.ReactNode;
  index: number;
  value: number;
  'aria-labelledby': string;
}

interface Category {
  id: string;
  name: string;
}

interface Supplier {
  id: string;
  name: string;
  type: 'supplier';
}

interface ProductFormData {
  name: string;
  description: string;
  price: string;
  cost: string;
  supplierId: string;
  status: 'active' | 'inactive' | 'out_of_stock' | 'discontinued';
  weight: string;
