    height?: number;
  };
  inventory?: {
    quantity?: number;
    lowStockThreshold?: number;
  };
  images?: string[];
  specifications?: Record<string, string>;
}

function TabPanel({ children, value, index, 'aria-labelledby': ariaLabelledBy, ...other }: TabPanelProps) {
  const isActive = value === index;
  
  return (
    <div
      role="tabpanel"
      hidden={!isActive}
      id={`product-tabpanel-${index}`}
      aria-labelledby={ariaLabelledBy}
      {...other}
    >
      {isActive && (
        <Box sx={{ p: 2 }}>
          {children}
        </Box>
      )}
    </div>
  );
}

const ProductForm: React.FC<ProductFormProps> = ({
  productId,
  companyId,
  onSave,
  onCancel,
}) => {
  const isEditMode = !!productId;
    const [tabValue, setTabValue] = useState(0);
  const [categories, setCategories] = useState<Category[]>([]);
  const [suppliers, setSuppliers] = useState<Supplier[]>([]);
  const [formData, setFormData] = useState<ProductFormData>({
    name: '',
    description: '',
    price: '',
    cost: '',
    supplierId: '',
    status: 'active',
    weight: '',
    dimensions: {
      length: '',
      width: '',
      height: '',
    },
    inventory: {
      quantity: '',
      lowStockThreshold: '',
    },
    images: [],
    specifications: {},
  });
  
  const [errors, setErrors] = useState<Record<string, string>>({});
  const [loading, setLoading] = useState(false);
  const [fetchLoading, setFetchLoading] = useState(false);  const [apiError, setApiError] = useState<string | null>(null);
  const [specKeys, setSpecKeys] = useState<string[]>([]);

  useEffect(() => {
    fetchCategories();
    fetchSuppliers();
    
    if (productId) {
      fetchProductDetails();
    }
  }, [productId, companyId]);
    const fetchCategories = async () => {
    try {
      const response = await productService.getCategories(companyId);
      setCategories(response.data || []);
    } catch (err: any) {
      console.error('Error fetching categories:', err);
    }
  };
  
  const fetchSuppliers = async () => {
    try {
      const response = await companyService.getSuppliers();
      const suppliersList = response.data || [];
      setSuppliers(suppliersList
        .filter(supplier => supplier.type === 'supplier')
        .map(supplier => ({
          id: supplier.id,
          name: supplier.name,
          type: supplier.type as 'supplier' // Type assertion to ensure it's treated as 'supplier'
        }))
      );
    } catch (err: any) {
      console.error('Error fetching suppliers:', err);
    }
  };
    // Updated the fetchProductDetails function to align with the Product type from productService
  const fetchProductDetails = async () => {
    if (!productId) return;

    setFetchLoading(true);
    setApiError(null);

    try {
      const productResponse = await productService.getProductById(productId);
      const product = productResponse.data || productResponse; // Handle different response structures

      // Map API response to form data
      setFormData({
        name: product.name || '',
        description: product.description || '',
        price: product.base_price?.toString() || '',
        cost: '', // Cost is not part of the Product interface
        supplierId: product.supplier_id || '',
        status: product.status || 'active',
        weight: '', // Weight is not directly in Product interface
        dimensions: {
          length: product.dimensions?.length?.toString() || '',
          width: product.dimensions?.width?.toString() || '',
          height: product.dimensions?.height?.toString() || '',
        },
        inventory: {
          quantity: product.inventory?.quantity?.toString() || '',
          lowStockThreshold: product.inventory?.lowStockThreshold?.toString() || '',
        },
        images: product.images || [],
        specifications: product.specifications || {},
      });

      // Set specification keys
      setSpecKeys(Object.keys(product.specifications || {}));
    } catch (err: any) {      setApiError(err.message || 'Failed to load product details');
      console.error('Error fetching product:', err);
    } finally {
      setFetchLoading(false);
    }
  };  const validateForm = () => {
    const newErrors: Record<string, string> = {};
    
    // Required fields according to ProductCreateInput in productService
    if (!formData.name.trim()) {
      newErrors.name = 'Product name is required';
    }
    
    if (!formData.price.trim()) {
      newErrors.price = 'Price is required';
    } else if (isNaN(parseFloat(formData.price)) || parseFloat(formData.price) < 0) {
      newErrors.price = 'Price must be a valid positive number';
    }    if (!formData.supplierId || !suppliers.some(s => s.id === formData.supplierId)) {
      newErrors.supplierId = 'Please select a valid supplier';
    }
    
    // Optional fields validation
    if (formData.cost.trim() && (isNaN(parseFloat(formData.cost)) || parseFloat(formData.cost) < 0)) {
      newErrors.cost = 'Cost must be a valid positive number';
    }
    
    if (formData.inventory.quantity.trim() && (isNaN(parseInt(formData.inventory.quantity)) || parseInt(formData.inventory.quantity) < 0)) {
      newErrors.quantity = 'Quantity must be a valid non-negative integer';
    }
    
    if (formData.inventory.lowStockThreshold.trim() && (isNaN(parseInt(formData.inventory.lowStockThreshold)) || parseInt(formData.inventory.lowStockThreshold) < 0)) {
      newErrors.lowStockThreshold = 'Low stock threshold must be a valid non-negative integer';
    }
    
    if (formData.weight.trim() && (isNaN(parseFloat(formData.weight)) || parseFloat(formData.weight) < 0)) {
      newErrors.weight = 'Weight must be a valid positive number';
    }
    
    // Validate dimensions
    if (formData.dimensions.length.trim() && (isNaN(parseFloat(formData.dimensions.length)) || parseFloat(formData.dimensions.length) < 0)) {
      newErrors.length = 'Length must be a valid positive number';
    }
    
    if (formData.dimensions.width.trim() && (isNaN(parseFloat(formData.dimensions.width)) || parseFloat(formData.dimensions.width) < 0)) {
      newErrors.width = 'Width must be a valid positive number';
    }
    
    if (formData.dimensions.height.trim() && (isNaN(parseFloat(formData.dimensions.height)) || parseFloat(formData.dimensions.height) < 0)) {
      newErrors.height = 'Height must be a valid positive number';
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };
  
  const calculateMargins = (price: string, cost: string) => {
    const priceNum = parseFloat(price);
    const costNum = parseFloat(cost);
    
    if (!isNaN(priceNum) && !isNaN(costNum) && costNum > 0) {
      const margin = priceNum - costNum;
      const marginPercentage = (margin / costNum) * 100;
      return {
        margin: Number(margin.toFixed(2)),
        marginPercentage: Number(marginPercentage.toFixed(2))
      };
    }
    return {
      margin: 0,
      marginPercentage: 0
    };
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | { name?: string; value: unknown }>) => {
    const { name, value } = e.target;
    if (!name) return;
    
    // Handle nested properties
    if (name.includes('.')) {
      const [parent, child] = name.split('.');
      setFormData((prev) => ({
        ...prev,
        [parent]: {
          ...(typeof prev[parent as keyof typeof prev] === 'object' && prev[parent as keyof typeof prev] !== null
            ? (prev[parent as keyof typeof prev] as Record<string, unknown>)
            : {}),
          [child]: value
        }
      }));
    } else {      setFormData((prev) => {
        const newData = {
          ...prev,
          [name]: value
        };
        
        // Recalculate margins when price or cost changes
        if (name === 'price' || name === 'cost') {
          const newPrice = name === 'price' ? String(value) : newData.price;
          const newCost = name === 'cost' ? String(value) : newData.cost;
          const margins = calculateMargins(newPrice, newCost);
          return {
            ...newData,
            computedFields: margins
          };
        }
        
        return newData;
      });
    }
    
    // Clear error for the field being edited
    if (errors[name]) {
      setErrors((prev) => ({
        ...prev,
        [name]: ''
      }));
    }
  };
  const handleSelectChange = (e: SelectChangeEvent<string>) => {
    const { name = e.target.name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    // Clear error if it exists
    if (errors[name]) {
      setErrors(prev => ({ ...prev, [name]: '' }));
    }
  };

  const handleStatusChange = (e: SelectChangeEvent<string>) => {
    setFormData(prev => ({
      ...prev,
      status: e.target.value as 'active' | 'inactive' | 'out_of_stock' | 'discontinued'
    }));
    // Clear error if it exists
    if (errors.status) {
      setErrors(prev => ({ ...prev, status: '' }));
    }
  };
  
  const handleSpecificationChange = (key: string, value: string) => {
    setFormData((prev) => ({
      ...prev,
      specifications: {
        ...prev.specifications,
        [key]: value
      }
    }));
  };
  
  const addSpecification = () => {
    const newKey = `spec_${specKeys.length + 1}`;
    setSpecKeys([...specKeys, newKey]);
    
    setFormData((prev) => ({
      ...prev,
      specifications: {
        ...prev.specifications,
        [newKey]: ''
      }
    }));
  };
  
  const updateSpecKey = (oldKey: string, newKey: string) => {
    if (newKey.trim() === '') return;
    
    // Update the key in specKeys array
    const updatedSpecKeys = [...specKeys];
    const index = updatedSpecKeys.indexOf(oldKey);
    if (index !== -1) {
      updatedSpecKeys[index] = newKey;
      setSpecKeys(updatedSpecKeys);
    }
    
    // Update the key in specifications object
    setFormData((prev) => {
      const { [oldKey]: value, ...rest } = prev.specifications;
      return {
        ...prev,
        specifications: {
          ...rest,
          [newKey]: value
        }
      };
    });
  };
  
  const removeSpecification = (key: string) => {
    setSpecKeys(specKeys.filter((k: string) => k !== key));
    
    setFormData((prev) => {
      const { [key]: _, ...restSpecs } = prev.specifications;
      return {
        ...prev,
        specifications: restSpecs
      };
    });
  };
  
  const handleAddImage = (url: string) => {
    if (url.trim() === '') return;
    
    setFormData((prev) => ({
      ...prev,
      images: [...prev.images, url]
    }));
  };
  
  const handleRemoveImage = (index: number) => {
    setFormData((prev) => ({
      ...prev,
      images: prev.images.filter((_, i) => i !== index)
    }));
  };  const handleSubmit = async () => {
    if (!validateForm()) return;
    
    setLoading(true);
    setApiError(null);
    
    try {      // Prepare data for API according to ProductCreateInput or ProductUpdateInput from productService
      const productData: ProductApiInput = {
        name: formData.name,
        description: formData.description,
        base_price: parseFloat(formData.price),
        supplier_id: formData.supplierId,
        status: formData.status === 'discontinued' ? 'inactive' : formData.status,
        unit: 'piece', // Default unit value
      };
      
      // Add optional fields only if they have values
      
      if (formData.dimensions.length || formData.dimensions.width || formData.dimensions.height) {
        productData.dimensions = {};
        if (formData.dimensions.length) {
          productData.dimensions.length = parseFloat(formData.dimensions.length);
        }
        if (formData.dimensions.width) {
          productData.dimensions.width = parseFloat(formData.dimensions.width);
        }
        if (formData.dimensions.height) {
          productData.dimensions.height = parseFloat(formData.dimensions.height);
        }
      }
      
      if (formData.inventory.quantity || formData.inventory.lowStockThreshold) {
        productData.inventory = {};
        if (formData.inventory.quantity) {
          productData.inventory.quantity = parseInt(formData.inventory.quantity);
        }
        if (formData.inventory.lowStockThreshold) {
          productData.inventory.lowStockThreshold = parseInt(formData.inventory.lowStockThreshold);
        }
      }
      
      if (formData.images.length > 0) {
        productData.images = formData.images;
      }
      
      if (Object.keys(formData.specifications).length > 0) {
        productData.specifications = formData.specifications;
      }
      
      let savedProduct;
      
      if (isEditMode && productId) {
        savedProduct = await productService.updateProduct(productId, productData);
      } else {
        savedProduct = await productService.createProduct(productData);
      }
      
      if (onSave) {
        onSave(savedProduct);
      }
    } catch (err: any) {
      setApiError(err.message || `Failed to ${isEditMode ? 'update' : 'create'} product`);
      console.error(`Error ${isEditMode ? 'updating' : 'creating'} product:`, err);
    } finally {
      setLoading(false);
    }
  };
    const handleTabChange = (event: React.SyntheticEvent, newValue: number) => {
    // Validate the current tab before moving to another
    if (tabValue === 0) {
      validateBasicInfo(formData, setErrors);
    }
    
    setTabValue(newValue);
  };

  // Basic Info Tab Panel Content
  const renderBasicInfoTab = () => (
    <Box>
      <FormSection 
        title="Basic Information" 
        tooltip="Enter the fundamental details about your product"
      >
        <Stack spacing={3}>
          <Stack direction={{ xs: 'column', sm: 'row' }} spacing={2}>
            <TextField
              name="name"
              label="Product Name"
              fullWidth
              required
              value={formData.name}
              onChange={handleChange}
              error={!!errors.name}
              helperText={errors.name || "Enter a descriptive name for your product"}
              disabled={loading}
              inputProps={{
                'aria-describedby': 'product-name-helper-text'
              }}
            />
          </Stack>

          <TextField
            name="description"
            label="Description"
            fullWidth
            multiline
            rows={4}
            value={formData.description}
            onChange={handleChange}
            disabled={loading}
            helperText="Provide a detailed description of the product's features and benefits"
            inputProps={{
              'aria-describedby': 'product-description-helper-text'
            }}
          />
        </Stack>
      </FormSection>
      
      <FormSection 
        title="Pricing" 
        tooltip="Set product pricing and view profit margins"
      >
        <Stack spacing={3}>
          <Stack direction={{ xs: 'column', sm: 'row' }} spacing={2}>
            <TextField
              name="price"
              label="Selling Price"
              fullWidth
              required
              type="number"
              inputProps={{ min: 0, step: 0.01, 'aria-describedby': 'price-helper-text' }}
              value={formData.price}
              onChange={handleChange}
              error={!!errors.price}
              helperText={errors.price || "The price customers will pay for this product"}
              disabled={loading}
              InputProps={{
                startAdornment: <InputAdornment position="start">$</InputAdornment>,
              }}
            />
            <TextField
              name="cost"
              label="Cost"
              fullWidth
              type="number"
              inputProps={{ min: 0, step: 0.01, 'aria-describedby': 'cost-helper-text' }}
              value={formData.cost}
              onChange={handleChange}
              error={!!errors.cost}
              helperText={errors.cost || "Your cost to acquire or produce this product"}
              disabled={loading}
              InputProps={{
                startAdornment: <InputAdornment position="start">$</InputAdornment>,
              }}
            />
          </Stack>

          {formData.computedFields && (formData.price || formData.cost) && (
            <Stack direction={{ xs: 'column', sm: 'row' }} spacing={2}>
              <TextField
                label="Profit Margin"
                fullWidth
                value={`$${formData.computedFields.margin.toFixed(2)}`}
                InputProps={{
                  readOnly: true,
                }}
                disabled
                helperText="The dollar amount of profit per unit"
              />
              <TextField
                label="Margin Percentage"
                fullWidth
                value={`${formData.computedFields.marginPercentage.toFixed(2)}%`}
                InputProps={{
                  readOnly: true,
                }}
                disabled
                helperText="Percentage of profit relative to cost"
              />
            </Stack>
          )}
        </Stack>
      </FormSection>
      
      <FormSection title="Product Classification">
        <Stack direction={{ xs: 'column', sm: 'row' }} spacing={2}>
          <FormControl fullWidth required error={!!errors.supplierId}>
            <InputLabel id="supplierId-label">Supplier</InputLabel>
            <Select
              labelId="supplierId-label"
              name="supplierId"
              value={formData.supplierId}
              onChange={handleSelectChange}
              disabled={loading}
              label="Supplier"
              inputProps={{
                'aria-describedby': 'supplier-helper-text'
              }}
            >
              <MenuItem value="" disabled>Select a supplier</MenuItem>
              {suppliers.map((supplier) => (
                <MenuItem key={supplier.id} value={supplier.id}>
                  {supplier.name}
                </MenuItem>
              ))}
            </Select>
            <FormHelperText id="supplier-helper-text">
              {errors.supplierId || "Select the supplier you purchase this product from"}
            </FormHelperText>
          </FormControl>

          <FormControl fullWidth>
            <InputLabel id="status-label">Status</InputLabel>
            <Select
              labelId="status-label"
              name="status"
              value={formData.status}
              onChange={handleStatusChange}
              disabled={loading}
              label="Status"
              inputProps={{
                'aria-describedby': 'status-helper-text'
              }}
            >
              <MenuItem value="active">Active</MenuItem>
              <MenuItem value="inactive">Inactive</MenuItem>
              <MenuItem value="out_of_stock">Out of Stock</MenuItem>
              <MenuItem value="discontinued">Discontinued</MenuItem>
            </Select>
            <FormHelperText id="status-helper-text">
              Determines if the product is available for purchase
            </FormHelperText>
          </FormControl>
        </Stack>
      </FormSection>
    </Box>
  );

  return (
    <FormContainer 
      title={isEditMode ? 'Edit Product' : 'Add New Product'}
      loading={fetchLoading}
      error={apiError}
      maxWidth="900px"
    >
      <Box sx={{ width: '100%' }}>
        <Box sx={{ borderBottom: 1, borderColor: 'divider', mb: 2 }}>
          <Tabs 
            value={tabValue} 
            onChange={handleTabChange}
            aria-label="product form tabs"
            variant="scrollable"
            scrollButtons="auto"
          >
            <Tab 
              label="Basic Info" 
              id="product-tab-0" 
              aria-controls="product-tabpanel-0"
            />
            <Tab 
              label="Inventory" 
              id="product-tab-1" 
              aria-controls="product-tabpanel-1"
            />
            <Tab 
              label="Details" 
              id="product-tab-2" 
              aria-controls="product-tabpanel-2"
            />
            <Tab 
              label="Images" 
              id="product-tab-3" 
              aria-controls="product-tabpanel-3"
            />
            {isEditMode && (
              <Tab 
                label="Price History" 
                id="product-tab-4" 
                aria-controls="product-tabpanel-4"
              />
            )}
          </Tabs>
        </Box>

        <TabPanel 
          value={tabValue} 
          index={0}
          aria-labelledby="product-tab-0"
        >
          {renderBasicInfoTab()}
        </TabPanel>

        {/* Inventory Tab */}
        <TabPanel value={tabValue} index={1} aria-labelledby="product-tab-1">
          <Stack spacing={3}>
            <Stack direction={{ xs: 'column', sm: 'row' }} spacing={2}>
              <TextField
                name="inventory.quantity"
                label="Current Stock"
                fullWidth
                type="number"
                inputProps={{ min: 0, step: 1 }}
                value={formData.inventory.quantity}
                onChange={handleChange}
                error={!!errors.quantity}
                helperText={errors.quantity}
                disabled={loading}
              />
              <TextField
                name="inventory.lowStockThreshold"
                label="Low Stock Threshold"
                fullWidth
                type="number"
                inputProps={{ min: 0, step: 1 }}
                value={formData.inventory.lowStockThreshold}
                onChange={handleChange}
                error={!!errors.lowStockThreshold}
                helperText={errors.lowStockThreshold || 'Send alert when stock falls below this level'}
                disabled={loading}
              />
            </Stack>

            <Typography variant="subtitle1" gutterBottom>
              Shipping Information
            </Typography>

            <TextField
              name="weight"
              label="Weight (kg)"
              fullWidth
              type="number"
              inputProps={{ min: 0, step: 0.01 }}
              value={formData.weight}
              onChange={handleChange}
              error={!!errors.weight}
              helperText={errors.weight}
              disabled={loading}
            />

            <Typography variant="subtitle2" gutterBottom>
              Dimensions
            </Typography>

            <Stack direction={{ xs: 'column', sm: 'row' }} spacing={2}>
              <TextField
                name="dimensions.length"
                label="Length (cm)"
                fullWidth
                type="number"
                inputProps={{ min: 0, step: 0.1 }}
                value={formData.dimensions.length}
                onChange={handleChange}
                error={!!errors.length}
                helperText={errors.length}
                disabled={loading}
              />
              <TextField
                name="dimensions.width"
                label="Width (cm)"
                fullWidth
                type="number"
                inputProps={{ min: 0, step: 0.1 }}
                value={formData.dimensions.width}
                onChange={handleChange}
                error={!!errors.width}
                helperText={errors.width}
                disabled={loading}
              />
              <TextField
                name="dimensions.height"
                label="Height (cm)"
                fullWidth
                type="number"
                inputProps={{ min: 0, step: 0.1 }}
                value={formData.dimensions.height}
                onChange={handleChange}
                error={!!errors.height}
                helperText={errors.height}
                disabled={loading}
              />
            </Stack>
          </Stack>
        </TabPanel>

        {/* Details Tab */}
        <TabPanel value={tabValue} index={2} aria-labelledby="product-tab-2">
          <Box>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
              <Typography variant="h6">Specifications</Typography>
              <Button 
                variant="outlined" 
                onClick={addSpecification}
                disabled={loading}
              >
                Add Specification
              </Button>
            </Box>
            
            <Divider sx={{ mb: 2 }} />
            
            {specKeys.length === 0 && (
              <Typography color="text.secondary" sx={{ my: 4, textAlign: 'center' }}>
                No specifications added yet. Click "Add Specification" to add product details.
              </Typography>
            )}
            
            {specKeys.map((key: string, index: number) => (
              <Stack direction={{ xs: 'column', sm: 'row' }} spacing={2} key={key} sx={{ mb: 2 }}>
                <TextField
                  fullWidth
                  label="Specification Name"
                  value={key.startsWith('spec_') ? '' : key}
                  onChange={(e) => updateSpecKey(key, e.target.value)}
                  placeholder="e.g., Material, Color"
                  disabled={loading}
                />
                <TextField
                  fullWidth
                  label="Value"
                  value={formData.specifications[key] || ''}
                  onChange={(e) => handleSpecificationChange(key, e.target.value)}
                  disabled={loading}
                />
                <Box sx={{ display: 'flex', alignItems: 'center' }}>
                  <IconButton 
                    onClick={() => removeSpecification(key)}
                    color="error"
                    disabled={loading}
                  >
                    <DeleteOutlineIcon />
                  </IconButton>
                </Box>
              </Stack>
            ))}
          </Box>
        </TabPanel>

        {/* Images Tab */}
        <TabPanel value={tabValue} index={3} aria-labelledby="product-tab-3">
          <Box>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
              <Typography variant="h6">Product Images</Typography>
              <Button 
                variant="outlined" 
                startIcon={<AddPhotoAlternateIcon />}
                disabled={loading}
                onClick={() => {
                  const url = prompt('Enter image URL:');
                  if (url) handleAddImage(url);
                }}
              >
                Add Image URL
              </Button>
            </Box>
            
            <Divider sx={{ mb: 2 }} />
            
            {formData.images.length === 0 && (
              <Typography color="text.secondary" sx={{ my: 4, textAlign: 'center' }}>
                No images added yet. Click "Add Image URL" to add product images.
              </Typography>
            )}
            
            <Stack 
              direction="row" 
              spacing={2} 
              sx={{ 
                flexWrap: 'wrap',
                gap: 2,
                '& > *': {
                  flex: '1 1 calc(33.333% - 16px)',
                  minWidth: '250px',
                }
              }}
            >
              {formData.images.map((image, index) => (
                <Box
                  key={index}
                  sx={{ 
                    position: 'relative',
                    height: 0, 
                    paddingTop: '100%', 
                    border: '1px solid #ddd',
                    borderRadius: 1,
                    overflow: 'hidden'
                  }}
                >
                  <Box 
                    component="img"
                    src={image}
                    alt={`Product image ${index + 1}`}
                    sx={{ 
                      position: 'absolute',
                      top: 0,
                      left: 0,
                      width: '100%',
                      height: '100%',
                      objectFit: 'contain',
                    }}
                    onError={(e: React.SyntheticEvent<HTMLImageElement>) => {
                      e.currentTarget.src = 'https://via.placeholder.com/400?text=Image+Error';
                    }}
                  />
                  <IconButton 
                    sx={{ 
                      position: 'absolute',
                      top: 5,
                      right: 5,
                      backgroundColor: 'rgba(255,255,255,0.7)',
                      '&:hover': {
                        backgroundColor: 'rgba(255,255,255,0.9)',
                      }
                    }}
                    size="small"
                    onClick={() => handleRemoveImage(index)}
                    disabled={loading}
                  >
                    <DeleteOutlineIcon fontSize="small" />
                  </IconButton>
                </Box>
              ))}
            </Stack>
          </Box>
        </TabPanel>

        {/* Price History Tab - Only shown in edit mode */}
        {isEditMode && (
          <TabPanel value={tabValue} index={4} aria-labelledby="product-tab-4">
            <ProductPriceHistory 
              productId={productId!}
              productName={formData.name}
              currentPrice={parseFloat(formData.price)}
              canUpdate={true}
              onPriceUpdated={() => {
                fetchProductDetails(); // Refresh product details after price update
              }}
            />
          </TabPanel>
        )}
      </Box>
      
      <Box sx={{ mt: 4 }}>
        <FormButtons
          onCancel={onCancel}
          onSubmit={handleSubmit}
          loading={loading}
          submitText={isEditMode ? 'Update Product' : 'Create Product'}
        />
      </Box>
    </FormContainer>
  );
};

export default ProductForm;

// New component for form section with tooltip support
interface FormSectionProps {
  title: string;
  tooltip?: string;
  children: React.ReactNode;
}

const FormSection: React.FC<FormSectionProps> = ({ title, tooltip, children }) => {
  return (
    <Box sx={{ mb: 4 }}>
      <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
        <Typography variant="h6" component="h3">{title}</Typography>
        {tooltip && (
          <Tooltip title={tooltip} arrow>
            <IconButton size="small" sx={{ ml: 0.5 }}>
              <HelpOutlineIcon fontSize="small" />
            </IconButton>
          </Tooltip>
        )}
      </Box>
      <Divider sx={{ mb: 2 }} />
      {children}
    </Box>
  );
};

// Add this function to validate only the basic info tab
const validateBasicInfo = (formData: any, setErrors: React.Dispatch<React.SetStateAction<Record<string, string>>>, showErrors = true) => {
  const newErrors: Record<string, string> = {};
  
  if (!formData.name.trim()) {
    newErrors.name = 'Product name is required';
  }
  
  if (!formData.price.trim()) {
    newErrors.price = 'Price is required';
  } else if (isNaN(parseFloat(formData.price)) || parseFloat(formData.price) < 0) {
    newErrors.price = 'Price must be a valid positive number';
  }
  
  if (!formData.supplierId) {
    newErrors.supplierId = 'Please select a valid supplier';
  }
  
  if (showErrors) {
    setErrors(prev => ({ ...prev, ...newErrors }));
  }
  
  return Object.keys(newErrors).length === 0;
};

import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Typography, 
  TextField,
  InputAdornment,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Chip,
  SelectChangeEvent,
  useTheme as useMuiTheme
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import AddIcon from '@mui/icons-material/Add';
import { useTheme } from '../../../contexts/ThemeContext';
import { productService } from '../../../services/api';
import WhiteLabelButton from '../WhiteLabelButton';
import DataTable from '../DataTable';

interface ProductListProps {
  companyId?: string;
  onAddProduct?: () => void;
  onEditProduct?: (productId: string) => void;
  maxHeight?: number | string;
  hideActions?: boolean;
  categoryFilter?: string;
  statusFilter?: string;
}

const ProductList: React.FC<ProductListProps> = ({
  companyId,
  onAddProduct,
  onEditProduct,
  maxHeight,
  hideActions = false,
  categoryFilter,
  statusFilter,
}) => {
  const { theme: whitelabelTheme } = useTheme();
  const muiTheme = useMuiTheme();

  const [products, setProducts] = useState<any[]>([]);
  const [categories, setCategories] = useState<any[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState(categoryFilter || '');
  const [selectedStatus, setSelectedStatus] = useState(statusFilter || '');
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [totalCount, setTotalCount] = useState(0);

  // Custom button style based on the whitelabel theme
  const buttonStyle = {
    backgroundColor: whitelabelTheme?.primaryColor || muiTheme.palette.primary.main,
    color: '#fff',
    borderRadius: whitelabelTheme?.buttonRadius !== undefined ? `${whitelabelTheme.buttonRadius}px` : undefined,
    '&:hover': {
      backgroundColor: whitelabelTheme?.primaryColor ? 
        `${whitelabelTheme.primaryColor}dd` : muiTheme.palette.primary.dark,
    },
  };

  // Fetch product categories for filtering
  const fetchCategories = async () => {
    try {
      if (companyId) {
        const response = await productService.getCategories(companyId);
        setCategories(response.data || []);
      }
    } catch (err: any) {
      console.error('Error fetching categories:', err);
      // Don't set error state here to avoid blocking the main products fetch
    }
  };

  const fetchProducts = async () => {
    setLoading(true);
    setError(null);

    try {
      const response = await productService.getProducts({
        page: page + 1, // API uses 1-based pagination
        limit: rowsPerPage,
        search: searchTerm,
      });

      setProducts(response.products || []); // Updated to use `response.products`
      setTotalCount(response.pagination?.total || 0);
    } catch (err: any) {
      setError(err.message || 'Failed to fetch products');
      console.error('Error fetching products:', err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchCategories();
  }, [companyId]);

  useEffect(() => {
    fetchProducts();
  }, [page, rowsPerPage, selectedCategory, selectedStatus, companyId]);

  const handleSearch = () => {
    setPage(0); // Reset to first page when searching
    fetchProducts();
  };

  const handleSearchKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };

  const handleCategoryChange = (e: SelectChangeEvent) => {
    setSelectedCategory(e.target.value as string);
    setPage(0);
  };

  const handleStatusChange = (e: SelectChangeEvent) => {
    setSelectedStatus(e.target.value as string);
    setPage(0);
  };

  const getStatusChip = (status: string) => {
    let color: 'default' | 'primary' | 'secondary' | 'error' | 'info' | 'success' | 'warning' = 'default';
    switch (status.toLowerCase()) {
      case 'active':
        color = 'success';
        break;
      case 'inactive':
        color = 'default';
        break;
      case 'out_of_stock':
        color = 'error';
        break;
      case 'discontinued':
        color = 'warning';
        break;
    }
    
    return <Chip 
      label={status.replace('_', ' ')} 
      color={color} 
      size="small" 
      sx={{ textTransform: 'capitalize' }}
    />;
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
    }).format(amount);
  };

  // Define the columns for the data table
  const tableColumns = [
    {
      id: 'name',
      label: 'Product Name',
      type: 'text' as const,
      sortable: true,
      filterable: true,
    },
    {
      id: 'sku',
      label: 'SKU',
      type: 'text' as const,
      sortable: true,
    },
    {
      id: 'category',
      label: 'Category',
      type: 'text' as const,
      sortable: true,
      filterable: true,
      render: (value: any, row: any) => row.category?.name || 'Uncategorized',
    },
    {
      id: 'base_price',
      label: 'Price',
      type: 'currency' as const,
      sortable: true,
      align: 'right' as const,
    },
    {
      id: 'inventory',
      label: 'Stock',
      type: 'number' as const,
      sortable: true,
      align: 'right' as const,
      render: (value: any, row: any) => row.inventory?.quantity || 0,
    },
    {
      id: 'status',
      label: 'Status',
      type: 'status' as const,
      sortable: true,
      filterable: true,
      render: (value: string) =>  getStatusChip(value),
    },
  ];

  // Row actions similar to Odoo's action menu
  const rowActions = [
    {
      id: 'edit',
      label: 'Edit',
      onClick: (row: any) => onEditProduct && onEditProduct(row.id),
    },
    {
      id: 'view',
      label: 'View Details',
      onClick: (row: any) => onEditProduct && onEditProduct(row.id),
    },
    {
      id: 'divider1',
      label: '',
      onClick: () => {},
      divider: true,
    },
    {
      id: 'deactivate',
      label: 'Change Status',
      onClick: (row: any) => {
        // Handle status change
        const newStatus = row.status === 'active' ? 'inactive' : 'active';
        productService.updateProduct(row.id, { status: newStatus })
          .then(() => {
            fetchProducts();
          })
          .catch(err => {
            console.error('Failed to update product status', err);
            setError('Failed to update product status');
          });
      },
    },
  ];

  // Bulk actions like in Odoo
  const bulkActions = [
    {
      id: 'activate',
      label: 'Activate',
      onClick: (selectedRows: any[]) => {
        // Handle bulk activate
        console.log('Bulk activate', selectedRows);
      },
    },
    {
      id: 'deactivate',
      label: 'Deactivate',
      onClick: (selectedRows: any[]) => {
        // Handle bulk deactivate
        console.log('Bulk deactivate', selectedRows);
      },
    },
  ];

  // Filter panel in Odoo style
  const filterPanel = (
    <Box sx={{ display: 'flex', gap: 2, flexWrap: 'wrap', alignItems: 'center' }}>
      <TextField
        size="small"
        placeholder="Search products..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        onKeyPress={handleSearchKeyPress}
        InputProps={{
          startAdornment: (
            <InputAdornment position="start">
              <SearchIcon />
            </InputAdornment>
          ),
        }}
        sx={{ flexGrow: 1, minWidth: '200px' }}
      />
      
      <FormControl sx={{ minWidth: 150 }} size="small">
        <InputLabel id="category-filter-label">Category</InputLabel>
        <Select
          labelId="category-filter-label"
          value={selectedCategory}
          onChange={handleCategoryChange}
          label="Category"
        >
          <MenuItem value="">All Categories</MenuItem>
          {categories.map((category) => (
            <MenuItem key={category.id} value={category.id}>
              {category.name}
            </MenuItem>
          ))}
        </Select>
      </FormControl>
      
      <FormControl sx={{ minWidth: 120 }} size="small">
        <InputLabel id="status-filter-label">Status</InputLabel>
        <Select
          labelId="status-filter-label"
          value={selectedStatus}
          onChange={handleStatusChange}
          label="Status"
        >
          <MenuItem value="">All Status</MenuItem>
          <MenuItem value="active">Active</MenuItem>
          <MenuItem value="inactive">Inactive</MenuItem>
          <MenuItem value="out_of_stock">Out of Stock</MenuItem>
          <MenuItem value="discontinued">Discontinued</MenuItem>
        </Select>
      </FormControl>
    </Box>
  );

  return (
    <Box>
      {/* Page header with title and actions */}
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
        <Typography variant="h5">Products</Typography>
        {!hideActions && onAddProduct && (
          <WhiteLabelButton
            variant="contained"
            startIcon={<AddIcon />}
            onClick={onAddProduct}
          >
            Add Product
          </WhiteLabelButton>
        )}
      </Box>

      {/* Filter panel */}
      <Box sx={{ mb: 2 }}>
        {filterPanel}
      </Box>

      <DataTable
        columns={tableColumns.map(col => ({
          id: col.id,
          label: col.label,
          align: col.align,
          minWidth: col.id === 'name' ? 200 : undefined,
          format: col.render || undefined
        }))}
        data={products}
        loading={loading}
        error={error}
        emptyMessage="No products found"
        onRowClick={(row) => onEditProduct && onEditProduct(row.id)}
        pagination={totalCount > 0 ? {
          page,
          totalCount,
          rowsPerPage,
          onPageChange: (newPage) => setPage(newPage),
          onRowsPerPageChange: (newRowsPerPage) => {
            setRowsPerPage(newRowsPerPage);
            setPage(0);
          }
        } : undefined}
      />
    </Box>
  );
};

export default ProductList;

import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Typography, 
  Paper, 
  CircularProgress,
  Alert,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Pagination,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  TextField,
  FormControl,
  InputLabel,
  FormHelperText,
  InputAdornment,
} from '@mui/material';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import { format } from 'date-fns';
import { productService } from '../../../services/api';
import { useTheme } from '../../../contexts/ThemeContext';

interface ProductPriceHistoryProps {
  productId: string;
  productName?: string;
  currentPrice?: number;
  canUpdate?: boolean;
  onPriceUpdated?: () => void;
}

const ProductPriceHistory: React.FC<ProductPriceHistoryProps> = ({ 
  productId, 
  productName, 
  currentPrice, 
  canUpdate = false,
  onPriceUpdated
}) => {
  const { theme: whitelabelTheme } = useTheme();
  const [priceHistory, setPriceHistory] = useState<any[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [openPriceDialog, setOpenPriceDialog] = useState(false);
  const [newPrice, setNewPrice] = useState('');
  const [effectiveDate, setEffectiveDate] = useState<Date | null>(new Date());
  const [priceError, setPriceError] = useState('');
  const [submitting, setSubmitting] = useState(false);

  useEffect(() => {
    loadPriceHistory();
  }, [productId, page]);

  const loadPriceHistory = async () => {
    if (!productId) return;
    setLoading(true);
    setError(null);

    try {
      const response = await productService.getPriceHistory(productId, { page, limit: 10 });
      setPriceHistory(response.data || []);
      
      if (response.pagination) {
        setTotalPages(response.pagination.totalPages || 1);
      }
    } catch (err: any) {
      setError(err.message || 'Failed to load price history');
      console.error('Error loading price history:', err);
    } finally {
      setLoading(false);
    }
  };

  const handlePageChange = (_event: React.ChangeEvent<unknown>, value: number) => {
    setPage(value);
  };

  const handlePriceDialogOpen = () => {
    setOpenPriceDialog(true);
    setNewPrice(currentPrice ? currentPrice.toString() : '');
    setEffectiveDate(new Date());
    setPriceError('');
  };

  const handlePriceDialogClose = () => {
    setOpenPriceDialog(false);
  };

  const validatePrice = (price: string): boolean => {
    if (!price.trim()) {
      setPriceError('Price is required');
      return false;
    }

    const numericPrice = parseFloat(price);
    if (isNaN(numericPrice) || numericPrice <= 0) {
      setPriceError('Price must be a positive number');
      return false;
    }

    setPriceError('');
    return true;
  };

  const handlePriceUpdate = async () => {
    if (!validatePrice(newPrice)) return;
    
    setSubmitting(true);
    try {
      const price = parseFloat(newPrice);
      const effectiveDateStr = effectiveDate ? effectiveDate.toISOString() : new Date().toISOString();
      
      await productService.updateProductPrice(productId, price, effectiveDateStr);
      
      handlePriceDialogClose();
      loadPriceHistory();
      
      if (onPriceUpdated) {
        onPriceUpdated();
      }
    } catch (err: any) {
      setPriceError(err.message || 'Failed to update price');
      console.error('Error updating price:', err);
    } finally {
      setSubmitting(false);
    }
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
    }).format(amount);
  };

  const formatDate = (dateString: string) => {
    try {
      return format(new Date(dateString), 'MMM dd, yyyy');
    } catch (e) {
      return dateString;
    }
  };

  return (
    <Box sx={{ mt: 2 }}>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
        <Typography variant="h6">Price History</Typography>
        {canUpdate && (
          <Button 
            variant="contained" 
            color="primary" 
            onClick={handlePriceDialogOpen}
            sx={{
              backgroundColor: whitelabelTheme?.primaryColor,
              '&:hover': {
                backgroundColor: whitelabelTheme?.primaryColor ? `${whitelabelTheme.primaryColor}dd` : undefined,
              },
            }}
          >
            Update Price
          </Button>
        )}
      </Box>

      {loading ? (
        <Box sx={{ display: 'flex', justifyContent: 'center', p: 4 }}>
          <CircularProgress />
        </Box>
      ) : error ? (
        <Alert severity="error">{error}</Alert>
      ) : (
        <>
          <TableContainer component={Paper} variant="outlined">
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Price</TableCell>
                  <TableCell>Effective Date</TableCell>
                  <TableCell>Updated By</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {priceHistory.length > 0 ? (
                  priceHistory.map((item) => (
                    <TableRow key={item.id}>
                      <TableCell>{formatCurrency(item.price)}</TableCell>
                      <TableCell>{formatDate(item.effective_date)}</TableCell>
                      <TableCell>{item.created_by}</TableCell>
                    </TableRow>
                  ))
                ) : (
                  <TableRow>
                    <TableCell colSpan={3} align="center">
                      No price history available
                    </TableCell>
                  </TableRow>
                )}
              </TableBody>
            </Table>
          </TableContainer>

          {totalPages > 1 && (
            <Box sx={{ display: 'flex', justifyContent: 'center', mt: 2 }}>
              <Pagination 
                count={totalPages} 
                page={page} 
                onChange={handlePageChange} 
                color="primary" 
              />
            </Box>
          )}
        </>
      )}

      {/* Price Update Dialog */}
      <Dialog open={openPriceDialog} onClose={handlePriceDialogClose}>
        <DialogTitle>Update Price for {productName}</DialogTitle>
        <DialogContent>
          <Box sx={{ pt: 1 }}>
            <FormControl fullWidth error={!!priceError} sx={{ mb: 3 }}>
              <TextField
                label="New Price"
                value={newPrice}
                onChange={(e) => setNewPrice(e.target.value)}
                fullWidth
                margin="normal"
                InputProps={{
                  startAdornment: <InputAdornment position="start">$</InputAdornment>,
                }}
                error={!!priceError}
              />
              {priceError && <FormHelperText>{priceError}</FormHelperText>}
            </FormControl>

            <LocalizationProvider dateAdapter={AdapterDateFns}>
              <DatePicker
                label="Effective Date"
                value={effectiveDate}
                onChange={(newDate) => setEffectiveDate(newDate)}
                slotProps={{
                  textField: {
                    fullWidth: true,
                    margin: 'normal'
                  },
                }}
              />
            </LocalizationProvider>
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handlePriceDialogClose} disabled={submitting}>
            Cancel
          </Button>
          <Button 
            onClick={handlePriceUpdate} 
            variant="contained" 
            color="primary"
            disabled={submitting}
          >
            {submitting ? <CircularProgress size={24} /> : 'Update Price'}
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default ProductPriceHistory;

import React, { useState } from 'react';
import {
  Box,
  Button,
  Typography,
  Paper,
  TextField,
  CircularProgress,
  Alert,
  Snackbar,
  IconButton,
  Tooltip
} from '@mui/material';
import ContentCopyIcon from '@mui/icons-material/ContentCopy';
import RefreshIcon from '@mui/icons-material/Refresh';
import { format, addDays, isAfter } from 'date-fns';
import { authService } from '../../../services/api';

interface EmployeeInvitationGeneratorProps {
  companyId: string;
  onInvitationGenerated?: (code: string) => void;
}

const EmployeeInvitationGenerator: React.FC<EmployeeInvitationGeneratorProps> = ({ 
  companyId,
  onInvitationGenerated 
}) => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [invitationCode, setInvitationCode] = useState<string | null>(null);
  const [expiresAt, setExpiresAt] = useState<string | null>(null);
  const [snackbarOpen, setSnackbarOpen] = useState(false);
  const [snackbarMessage, setSnackbarMessage] = useState('');
  const [companyName, setCompanyName] = useState<string | null>(null);

  const generateInvitation = async () => {
    if (!companyId) {
      setError('Company ID is required');
      return;
    }

    setLoading(true);
    setError(null);

    try {      const response = await authService.generateInvitationCode({ companyId });
      setInvitationCode(response.data.code);
      setExpiresAt(response.data.expiresIn);
      // Company name is not provided in the backend response, so we're not setting it
      setCompanyName("Your Company");
      
      if (onInvitationGenerated) {
        onInvitationGenerated(response.data.code);
      }
    } catch (err: any) {
      setError(err.message || 'Failed to generate invitation code');
      console.error('Error generating invitation code:', err);
    } finally {
      setLoading(false);
    }
  };

  const copyToClipboard = () => {
    if (invitationCode) {
      navigator.clipboard.writeText(invitationCode)
        .then(() => {
          setSnackbarMessage('Invitation code copied to clipboard');
          setSnackbarOpen(true);
        })
        .catch((err) => {
          console.error('Failed to copy text: ', err);
          setSnackbarMessage('Failed to copy code');
          setSnackbarOpen(true);
        });
    }
  };

  const handleSnackbarClose = () => {
    setSnackbarOpen(false);
  };
  const formatExpiryDate = (dateString: string) => {
    try {
      // If the string is in the format "X days", just return it as is
      if (dateString.includes('days')) {
        return dateString;
      }
      
      // Otherwise try to parse it as a date
      const date = new Date(dateString);
      return format(date, 'MMM dd, yyyy HH:mm:ss');
    } catch (e) {
      return dateString;
    }
  };
  const isExpired = (dateString: string | null) => {
    if (!dateString) return false;
    
    try {
      // If the string is in the format "X days", it's not expired
      if (dateString.includes('days')) {
        return false;
      }
      
      // Otherwise try to parse it as a date
      const date = new Date(dateString);
      return isAfter(new Date(), date);
    } catch (e) {
      return false;
    }
  };

  const getInvitationUrl = () => {
    if (!invitationCode) return '';
    const baseUrl = window.location.origin;
    return `${baseUrl}/join?code=${invitationCode}`;
  };

  return (
    <Paper sx={{ p: 3, mb: 3 }}>
      <Typography variant="h6" gutterBottom>
        Employee Invitation
      </Typography>
      <Typography variant="body2" color="text.secondary" paragraph>
        Generate an invitation code to allow new employees to register to your company. 
        The invitation code is valid for 48 hours.
      </Typography>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      {invitationCode ? (
        <Box sx={{ mt: 2 }}>
          <TextField
            label="Invitation Code"
            value={invitationCode}
            fullWidth
            InputProps={{
              readOnly: true,
              endAdornment: (
                <Tooltip title="Copy code">
                  <IconButton edge="end" onClick={copyToClipboard}>
                    <ContentCopyIcon />
                  </IconButton>
                </Tooltip>
              ),
            }}
            sx={{ mb: 2 }}
          />

          <TextField
            label="Invitation URL"
            value={getInvitationUrl()}
            fullWidth
            InputProps={{
              readOnly: true,
              endAdornment: (
                <Tooltip title="Copy URL">
                  <IconButton 
                    edge="end" 
                    onClick={() => {
                      navigator.clipboard.writeText(getInvitationUrl());
                      setSnackbarMessage('Invitation URL copied to clipboard');
                      setSnackbarOpen(true);
                    }}
                  >
                    <ContentCopyIcon />
                  </IconButton>
                </Tooltip>
              ),
            }}
            sx={{ mb: 2 }}
          />          {expiresAt && (
            <Typography 
              variant="body2" 
              color="text.secondary"
              sx={{ mb: 2 }}
            >
              {expiresAt.includes('days') 
                ? `This code will expire in ${formatExpiryDate(expiresAt)}`
                : isExpired(expiresAt) 
                  ? `This code has expired on ${formatExpiryDate(expiresAt)}`
                  : `This code will expire on ${formatExpiryDate(expiresAt)}`}
            </Typography>
          )}

          <Box sx={{ display: 'flex', justifyContent: 'flex-end', mt: 2 }}>
            <Button
              startIcon={<RefreshIcon />}
              onClick={generateInvitation}
              disabled={loading}
              variant="outlined"
            >
              {loading ? <CircularProgress size={24} /> : 'Generate New Code'}
            </Button>
          </Box>
        </Box>
      ) : (
        <Box sx={{ display: 'flex', justifyContent: 'flex-end', mt: 2 }}>
          <Button
            variant="contained"
            color="primary"
            onClick={generateInvitation}
            disabled={loading}
          >
            {loading ? <CircularProgress size={24} /> : 'Generate Invitation Code'}
          </Button>
        </Box>
      )}

      <Snackbar
        open={snackbarOpen}
        autoHideDuration={3000}
        onClose={handleSnackbarClose}
        message={snackbarMessage}
      />
    </Paper>
  );
};

export default EmployeeInvitationGenerator;

import React, { useState, useEffect } from 'react';
import {
  Box,
  Paper,
  Typography,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Button,
  Chip,
  IconButton,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
  TextField,
  CircularProgress,
  Alert,
  Tooltip
} from '@mui/material';
import CheckIcon from '@mui/icons-material/Check';
import CloseIcon from '@mui/icons-material/Close';
import { format } from 'date-fns';
import { authService } from '../../../services/api';

interface PendingEmployeesListProps {
  onEmployeeStatusChange?: () => void;
}

const PendingEmployeesList: React.FC<PendingEmployeesListProps> = ({
  onEmployeeStatusChange
}) => {
  const [employees, setEmployees] = useState<any[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [rejectDialogOpen, setRejectDialogOpen] = useState(false);
  const [selectedEmployeeId, setSelectedEmployeeId] = useState<string | null>(null);
  const [rejectionReason, setRejectionReason] = useState('');
  const [actionLoading, setActionLoading] = useState(false);

  useEffect(() => {
    fetchPendingEmployees();
  }, []);

  const fetchPendingEmployees = async () => {
    setLoading(true);
    setError(null);

    try {
      const response = await authService.getPendingEmployees();
      setEmployees(response.employees || []);
    } catch (err: any) {
      console.error('Error fetching pending employees:', err);
      setError(err.message || 'Failed to fetch pending employees');
    } finally {
      setLoading(false);
    }
  };

  const handleApprove = async (employeeId: string) => {
    setActionLoading(true);
    setError(null);

    try {
      await authService.verifyEmployee(employeeId);
      
      // Update the local list
      setEmployees(prevEmployees => 
        prevEmployees.filter(emp => emp.id !== employeeId)
      );
      
      if (onEmployeeStatusChange) {
        onEmployeeStatusChange();
      }
    } catch (err: any) {
      console.error('Error approving employee:', err);
      setError(err.message || 'Failed to approve employee');
    } finally {
      setActionLoading(false);
    }
  };

  const openRejectDialog = (employeeId: string) => {
    setSelectedEmployeeId(employeeId);
    setRejectionReason('');
    setRejectDialogOpen(true);
  };

  const closeRejectDialog = () => {
    setRejectDialogOpen(false);
    setSelectedEmployeeId(null);
  };

  const handleReject = async () => {
    if (!selectedEmployeeId) return;

    setActionLoading(true);
    setError(null);

    try {
      await authService.rejectEmployee(
        selectedEmployeeId,
        rejectionReason.trim() || undefined
      );
      
      // Update the local list
      setEmployees(prevEmployees => 
        prevEmployees.filter(emp => emp.id !== selectedEmployeeId)
      );
      
      if (onEmployeeStatusChange) {
        onEmployeeStatusChange();
      }
      
      closeRejectDialog();
    } catch (err: any) {
      console.error('Error rejecting employee:', err);
      setError(err.message || 'Failed to reject employee');
    } finally {
      setActionLoading(false);
    }
  };

  const formatDate = (dateString: string) => {
    try {
      return format(new Date(dateString), 'MMM dd, yyyy');
    } catch (e) {
      return dateString;
    }
  };

  return (
    <Paper sx={{ p: 3 }}>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
        <Typography variant="h6">Pending Employee Registrations</Typography>
        <Button 
          size="small" 
          onClick={fetchPendingEmployees}
          disabled={loading}
        >
          Refresh
        </Button>
      </Box>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      {loading ? (
        <Box sx={{ display: 'flex', justifyContent: 'center', p: 4 }}>
          <CircularProgress />
        </Box>
      ) : (
        <TableContainer>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Name</TableCell>
                <TableCell>Email</TableCell>
                <TableCell>Job Title</TableCell>
                <TableCell>Department</TableCell>
                <TableCell>Registration Date</TableCell>
                <TableCell align="right">Actions</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {employees.length > 0 ? (
                employees.map((employee) => (
                  <TableRow key={employee.id} hover>
                    <TableCell>
                      {employee.firstName} {employee.lastName}
                    </TableCell>
                    <TableCell>{employee.email}</TableCell>
                    <TableCell>{employee.jobTitle || ''}</TableCell>
                    <TableCell>{employee.department || ''}</TableCell>
                    <TableCell>
                      {employee.registrationDate ? formatDate(employee.registrationDate) : ''}
                    </TableCell>
                    <TableCell align="right">
                      <Tooltip title="Approve">
                        <IconButton 
                          color="success"
                          onClick={() => handleApprove(employee.id)}
                          disabled={actionLoading}
                        >
                          <CheckIcon />
                        </IconButton>
                      </Tooltip>
                      <Tooltip title="Reject">
                        <IconButton 
                          color="error"
                          onClick={() => openRejectDialog(employee.id)}
                          disabled={actionLoading}
                        >
                          <CloseIcon />
                        </IconButton>
                      </Tooltip>
                    </TableCell>
                  </TableRow>
                ))
              ) : (
                <TableRow>
                  <TableCell colSpan={6} align="center">
                    <Typography variant="body1" color="textSecondary">
                      No pending employee registrations found
                    </Typography>
                  </TableCell>
                </TableRow>
              )}
            </TableBody>
          </Table>
        </TableContainer>
      )}

      {/* Rejection Dialog */}
      <Dialog open={rejectDialogOpen} onClose={closeRejectDialog}>
        <DialogTitle>Reject Employee Registration</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Please provide a reason for rejecting this employee registration. 
            This will be included in the notification email sent to the applicant.
          </DialogContentText>
          <TextField
            autoFocus
            margin="dense"
            label="Rejection Reason"
            fullWidth
            multiline
            rows={3}
            value={rejectionReason}
            onChange={(e) => setRejectionReason(e.target.value)}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={closeRejectDialog} disabled={actionLoading}>
            Cancel
          </Button>
          <Button 
            onClick={handleReject} 
            color="error" 
            disabled={actionLoading}
          >
            {actionLoading ? <CircularProgress size={24} /> : 'Reject'}
          </Button>
        </DialogActions>
      </Dialog>
    </Paper>
  );
};

export default PendingEmployeesList;

import React, { useState, useEffect, useCallback } from 'react';
import { 
  TextField, 
  Box,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  FormHelperText,
  Switch,
  FormControlLabel,
  Divider,
  Typography,
  Alert,
  Link,
  SelectChangeEvent,
  Stack,
  Paper
} from '@mui/material';
import { useTheme } from '../../../contexts/ThemeContext';
import WhiteLabelButton from '../WhiteLabelButton';
import FormContainer from '../FormContainer';
import { userManagementService, User, UserCreateRequest, UserUpdateRequest } from '../../../services/api';

interface Company {
  id: string;
  name: string;
  type: string;
}

interface UserFormData {
  firstName: string;
  lastName: string;
  email: string;
  password: string;
  confirmPassword: string;
  role: 'admin' | 'manager' | 'staff' | 'supplier' | 'dealer' | '';
  status: 'active' | 'inactive' | 'pending' | 'suspended';
  companyId: string;
  sendInvitation: boolean;
}

interface UserFormErrors {
  firstName?: string;
  lastName?: string;
  email?: string;
  password?: string;
  confirmPassword?: string;
  role?: string;
  companyId?: string;
}

interface UserFormProps {
  userId?: string;
  companyId?: string;
  isAdminView?: boolean;
  onSave?: (user: User) => void;
  onCancel?: () => void;
  companies?: Company[];
}

const UserForm: React.FC<UserFormProps> = ({
  userId,
  companyId,
  isAdminView = false,
  onSave,
  onCancel,
  companies = []
}) => {
  const { theme } = useTheme();
  const isEditMode = !!userId;
  const isNewUser = !isEditMode;
  const [isCheckingEmail, setIsCheckingEmail] = useState(false);
  
  const [formData, setFormData] = useState<UserFormData>({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    confirmPassword: '',
    role: '',
    status: 'active',
    companyId: companyId || '',
    sendInvitation: true
  });
  
  const [errors, setErrors] = useState<UserFormErrors>({});
  const [loading, setLoading] = useState(false);
  const [fetchLoading, setFetchLoading] = useState(false);
  const [apiError, setApiError] = useState<string | null>(null);
  const [invitationCode, setInvitationCode] = useState<string | null>(null);
  
  useEffect(() => {
    if (userId) {
      fetchUserDetails();
    }
  }, [userId]);

  const fetchUserDetails = async () => {
    if (!userId) return;
    
    setFetchLoading(true);
    setApiError(null);
    
    try {
      const user = await userManagementService.getUser(userId);
      setFormData({
        firstName: user.firstName || '',
        lastName: user.lastName || '',
        email: user.email || '',
        password: '',
        confirmPassword: '',
        role: user.role || '',
        status: user.status || 'active',
        companyId: user.company_id || companyId || '',
        sendInvitation: false
      });
    } catch (err: any) {
      setApiError(err.message || 'Failed to load user details');
      console.error('Error fetching user:', err);
    } finally {
      setFetchLoading(false);
    }
  };

  const validateEmail = useCallback(async (email: string): Promise<string | null> => {
    if (!email) return 'Email is required';
    if (!/\S+@\S+\.\S+/.test(email)) return 'Email is invalid';
    
    if (isNewUser) {
      setIsCheckingEmail(true);
      try {
        const isAvailable = await userManagementService.checkEmailAvailability(email);
        return isAvailable ? null : 'Email is already in use';
      } catch (err) {
        console.error('Error checking email availability:', err);
        return null; // Don't block submission on API error
      } finally {
        setIsCheckingEmail(false);
      }
    }
    
    return null;
  }, [isNewUser]);
  
  const validateForm = useCallback(async () => {
    const newErrors: UserFormErrors = {};
    
    if (!formData.firstName.trim()) {
      newErrors.firstName = 'First name is required';
    }
    
    if (!formData.lastName.trim()) {
      newErrors.lastName = 'Last name is required';
    }
    
    const emailError = await validateEmail(formData.email);
    if (emailError) {
      newErrors.email = emailError;
    }
    
    if (isNewUser) {
      if (!formData.password) {
        newErrors.password = 'Password is required';
      } else if (formData.password.length < 8) {
        newErrors.password = 'Password must be at least 8 characters';
      }
      
      if (!formData.confirmPassword) {
        newErrors.confirmPassword = 'Please confirm your password';
      } else if (formData.password !== formData.confirmPassword) {
        newErrors.confirmPassword = 'Passwords do not match';
      }
    }
    
    if (!formData.role) {
      newErrors.role = 'Role is required';
    }
    
    if (!formData.companyId && isAdminView) {
      newErrors.companyId = 'Company is required';
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  }, [formData, isNewUser, isAdminView, validateEmail]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | { name?: string; value: unknown }>) => {
    const { name, value } = e.target;
    if (!name) return;
    
    setFormData((prev) => ({
      ...prev,
      [name]: value
    }));
    
    // Clear error for the field being edited
    if (errors[name as keyof UserFormErrors]) {
      setErrors((prev) => ({
        ...prev,
        [name]: undefined
      }));
    }
  };

  const handleSelectChange = (e: SelectChangeEvent<string>) => {
    handleChange(e as any);
  };
  
  const handleSwitchChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, checked } = e.target;
    
    setFormData((prev) => ({
      ...prev,
      [name]: checked
    }));
  };

  const generateInvitationCode = async () => {
    try {
      if (!formData.role || !['admin', 'manager', 'staff', 'supplier', 'dealer'].includes(formData.role)) {
        throw new Error('Please select a valid role first');
      }

      const code = await userManagementService.generateInvitationCode({
        email: formData.email,
        role: formData.role as UserCreateRequest['role'],
        companyId: formData.companyId,
      });
      setInvitationCode(code);
    } catch (err: any) {
      setApiError(err.message || 'Failed to generate invitation code');
      console.error('Error generating invitation code:', err);
    }
  };
  
  const handleSubmit = async () => {
    if (!(await validateForm())) return;
    
    setLoading(true);
    setApiError(null);
    
    try {
      // Validate required fields for API submission
      if (!formData.role || !formData.companyId) {
        throw new Error('Role and Company are required');
      }

      // Validate that role is one of the allowed values
      if (!['admin', 'manager', 'staff', 'supplier', 'dealer'].includes(formData.role)) {
        throw new Error('Invalid role selected');
      }

      const baseData = {
        firstName: formData.firstName,
        lastName: formData.lastName,
        email: formData.email,
        role: formData.role as 'admin' | 'manager' | 'staff' | 'supplier' | 'dealer', // Cast after validation
        status: formData.status,
        companyId: isAdminView ? formData.companyId : companyId
      };

      // Ensure companyId is available
      if (!baseData.companyId) {
        throw new Error('Company ID is required');
      }

      let savedUser: User;
      
      if (isEditMode && userId) {
        const updateData: UserUpdateRequest = {
          ...baseData,
          status: formData.status
        };
        savedUser = await userManagementService.updateUser(userId, updateData);
      } else {
        const createData: UserCreateRequest = {
          ...baseData,
          password: formData.password,
          sendInvitation: formData.sendInvitation,
          companyId: baseData.companyId // Already validated above
        };
        savedUser = await userManagementService.createUser(createData);
      }
      
      if (onSave && savedUser) {
        onSave(savedUser);
      }
    } catch (err: any) {
      setApiError(err.message || `Failed to ${isEditMode ? 'update' : 'create'} user`);
      console.error(`Error ${isEditMode ? 'updating' : 'creating'} user:`, err);
    } finally {
      setLoading(false);
    }
  };

  const getAvailableRoles = () => {
    if (isAdminView) {
      return [
        { value: 'admin', label: 'Admin' },
        { value: 'manager', label: 'Manager' },
        { value: 'staff', label: 'Staff' },
        { value: 'supplier', label: 'Supplier' },
        { value: 'dealer', label: 'Dealer' }
      ];
    }
    
    return [
      { value: 'manager', label: 'Manager' },
      { value: 'staff', label: 'Staff' }
    ];
  };

  return (
    <FormContainer 
      title={isEditMode ? 'Edit User' : 'Add New User'}
      loading={fetchLoading}
      error={apiError}
      maxWidth="700px"
    >
      <Paper 
        elevation={2} 
        sx={{ 
          p: 3, 
          borderRadius: `${theme.buttonRadius || 4}px`,
          backgroundColor: 'background.paper'
        }}
      >
        <Stack spacing={3}>
          <TextField
            name="firstName"
            label="First Name"
            fullWidth
            required
            value={formData.firstName}
            onChange={handleChange}
            error={!!errors.firstName}
            helperText={errors.firstName}
            disabled={loading}
          />
          
          <TextField
            name="lastName"
            label="Last Name"
            fullWidth
            required
            value={formData.lastName}
            onChange={handleChange}
            error={!!errors.lastName}
            helperText={errors.lastName}
            disabled={loading}
          />
          
          <TextField
            name="email"
            label="Email"
            fullWidth
            required
            type="email"
            value={formData.email}
            onChange={handleChange}
            error={!!errors.email}
            helperText={errors.email || (isCheckingEmail ? 'Checking email availability...' : undefined)}
            disabled={loading || isCheckingEmail || (isEditMode && !isAdminView)}
          />
          
          {isAdminView && (
            <FormControl fullWidth error={!!errors.companyId}>
              <InputLabel id="company-label">Company</InputLabel>
              <Select
                labelId="company-label"
                name="companyId"
                value={formData.companyId}
                onChange={handleSelectChange}
                disabled={loading || (isEditMode && !isAdminView)}
                label="Company"
              >
                <MenuItem value="" disabled>Select a company</MenuItem>
                {companies.map((company) => (
                  <MenuItem key={company.id} value={company.id}>
                    {company.name} ({company.type === 'supplier' ? 'Supplier' : 'Dealer'})
                  </MenuItem>
                ))}
              </Select>
              {errors.companyId && <FormHelperText>{errors.companyId}</FormHelperText>}
            </FormControl>
          )}
          
          <FormControl fullWidth required error={!!errors.role}>
            <InputLabel id="role-label">Role</InputLabel>
            <Select
              labelId="role-label"
              name="role"
              value={formData.role}
              onChange={handleSelectChange}
              disabled={loading || (isEditMode && !isAdminView)}
              label="Role"
            >
              <MenuItem value="" disabled>Select a role</MenuItem>
              {getAvailableRoles().map((role) => (
                <MenuItem key={role.value} value={role.value}>
                  {role.label}
                </MenuItem>
              ))}
            </Select>
            {errors.role && <FormHelperText>{errors.role}</FormHelperText>}
          </FormControl>

          {isEditMode && (
            <FormControl fullWidth>
              <InputLabel id="status-label">Status</InputLabel>
              <Select
                labelId="status-label"
                name="status"
                value={formData.status}
                onChange={handleSelectChange}
                disabled={loading}
                label="Status"
              >
                <MenuItem value="active">Active</MenuItem>
                <MenuItem value="inactive">Inactive</MenuItem>
                <MenuItem value="pending">Pending</MenuItem>
                <MenuItem value="suspended">Suspended</MenuItem>
              </Select>
            </FormControl>
          )}
          
          {isNewUser && (
            <>
              <Divider textAlign="left">
                <Typography 
                  variant="body2" 
                  sx={{ 
                    color: theme.primaryColor,
                    fontFamily: theme.fontFamily
                  }}
                >
                  Login Credentials
                </Typography>
              </Divider>
              
              <TextField
                name="password"
                label="Password"
                fullWidth
                required
                type="password"
                value={formData.password}
                onChange={handleChange}
                error={!!errors.password}
                helperText={errors.password || "Must be at least 8 characters"}
                disabled={loading}
              />
              
              <TextField
                name="confirmPassword"
                label="Confirm Password"
                fullWidth
                required
                type="password"
                value={formData.confirmPassword}
                onChange={handleChange}
                error={!!errors.confirmPassword}
                helperText={errors.confirmPassword}
                disabled={loading}
              />
              
              <FormControlLabel
                control={
                  <Switch
                    name="sendInvitation"
                    checked={formData.sendInvitation}
                    onChange={handleSwitchChange}
                    sx={{
                      '& .MuiSwitch-switchBase.Mui-checked': {
                        color: theme.primaryColor,
                      },
                      '& .MuiSwitch-switchBase.Mui-checked + .MuiSwitch-track': {
                        backgroundColor: theme.primaryColor,
                      },
                    }}
                  />
                }
                label="Send invitation email to the user"
              />
              
              <Alert 
                severity="info"
                sx={{
                  '& .MuiAlert-icon': {
                    color: theme.primaryColor,
                  },
                  '& a': {
                    color: theme.primaryColor,
                    textDecoration: 'none',
                    '&:hover': {
                      textDecoration: 'underline',
                    },
                  },
                }}
              >
                Alternatively, you can <Link 
                  href="#" 
                  onClick={(e: React.MouseEvent<HTMLAnchorElement>) => {
                    e.preventDefault();
                    generateInvitationCode();
                  }}
                >
                  generate an invitation code
                </Link> to share with the user.
                {invitationCode && (
                  <Box sx={{ mt: 1 }}>
                    Invitation Code: <strong>{invitationCode}</strong>
                  </Box>
                )}
              </Alert>
            </>
          )}
        </Stack>
        
        <Box sx={{ mt: 4, display: 'flex', gap: 2, justifyContent: 'flex-end' }}>
          {onCancel && (
            <WhiteLabelButton
              variant="outlined"
              onClick={onCancel}
              disabled={loading}
            >
              Cancel
            </WhiteLabelButton>
          )}
          <WhiteLabelButton
            onClick={handleSubmit}
            disabled={loading}
          >
            {isEditMode ? 'Update User' : 'Create User'}
          </WhiteLabelButton>
        </Box>
      </Paper>
    </FormContainer>
  );
};

export default UserForm;

import React, { useState, useEffect, useCallback } from 'react';
import { 
  Box, 
  Button, 
  Typography, 
  TextField,
  InputAdornment,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Chip,
  IconButton,
  Tooltip,
  useTheme as useMuiTheme,
  SelectChangeEvent
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import AddIcon from '@mui/icons-material/Add';
import PersonAddIcon from '@mui/icons-material/PersonAdd';
import EditIcon from '@mui/icons-material/Edit';
import BlockIcon from '@mui/icons-material/Block';
import LockResetIcon from '@mui/icons-material/LockReset';
import DataTable, { Column } from '../DataTable';
import { useTheme } from '../../../contexts/ThemeContext';
import { userManagementService, User } from '../../../services/api';

interface UserListProps {
  companyId?: string;
  onAddUser?: () => void;
  onEditUser?: (userId: string) => void;
  onInviteUser?: () => void;
  onResetPassword?: (userId: string) => void;
  maxHeight?: number | string;
  hideActions?: boolean;
  roleFilter?: string;
  statusFilter?: string;
  isAdminView?: boolean;
}

const UserList: React.FC<UserListProps> = ({
  companyId,
  onAddUser,
  onEditUser,
  onInviteUser,
  onResetPassword,
  maxHeight,
  hideActions = false,
  roleFilter,
  statusFilter,
  isAdminView = false,
}) => {
  const { theme: whitelabelTheme } = useTheme();
  const muiTheme = useMuiTheme();

  const [users, setUsers] = useState<User[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [isSearching, setIsSearching] = useState(false);
  const [searchAbortController, setSearchAbortController] = useState<AbortController | null>(null);
  const [selectedRole, setSelectedRole] = useState(roleFilter || '');
  const [selectedStatus, setSelectedStatus] = useState(statusFilter || '');
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [totalCount, setTotalCount] = useState(0);

  // Custom button style based on the whitelabel theme
  const buttonStyle = {
    backgroundColor: whitelabelTheme?.primaryColor || muiTheme.palette.primary.main,
    color: '#fff',
    borderRadius: whitelabelTheme?.buttonRadius !== undefined ? `${whitelabelTheme.buttonRadius}px` : undefined,
    '&:hover': {
      backgroundColor: whitelabelTheme?.primaryColor ? 
        `${whitelabelTheme.primaryColor}dd` : muiTheme.palette.primary.dark,
    },
  };

  const fetchUsers = useCallback(async (isSearchRequest = false) => {
    if (isSearchRequest && isSearching) {
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const params = {
        page: page + 1,
        limit: rowsPerPage,
        search: searchTerm || undefined,
        role: selectedRole || undefined,
        status: selectedStatus || undefined,
        company_id: companyId,
      };

      if (isSearchRequest) {
        setIsSearching(true);
      }

      const response = await userManagementService.getUsers(params);
      
      setUsers(response.data || []);
      setTotalCount(response.pagination?.total || 0);
      setError(null);
    } catch (err: any) {
      setError(err.message || 'Failed to load users');
      console.error('Error fetching users:', err);
      setUsers([]);
      setTotalCount(0);
    } finally {
      setLoading(false);
      if (isSearchRequest) {
        setIsSearching(false);
      }
    }
  }, [page, rowsPerPage, searchTerm, selectedRole, selectedStatus, companyId, isSearching]);

  useEffect(() => {
    if (!isSearching) {
      fetchUsers();
    }
  }, [fetchUsers]);

  // Debounced search with cleanup
  useEffect(() => {
    if (!searchTerm && page !== 0) {
      setPage(0);
      return;
    }

    if (searchAbortController) {
      searchAbortController.abort();
    }

    const newController = new AbortController();
    setSearchAbortController(newController);

    const timer = setTimeout(() => {
      if (searchTerm) {
        fetchUsers(true);
      }
    }, 500);

    return () => {
      clearTimeout(timer);
      newController.abort();
    };
  }, [searchTerm, page, fetchUsers]);

  // Reset pagination when filters change
  useEffect(() => {
    if (page !== 0) {
      setPage(0);
    }
  }, [selectedRole, selectedStatus]);

  const handleSearch = useCallback((newSearchTerm: string) => {
    setSearchTerm(newSearchTerm);
    if (!newSearchTerm) {
      setUsers([]);
      setTotalCount(0);
      fetchUsers();
    }
  }, [fetchUsers]);

  const handleRoleChange = (event: SelectChangeEvent<string>) => {
    const newRole = event.target.value;
    if (newRole === selectedRole) return;
    setSelectedRole(newRole);
  };

  const handleStatusChange = (event: SelectChangeEvent<string>) => {
    const newStatus = event.target.value;
    if (newStatus === selectedStatus) return;
    setSelectedStatus(newStatus);
  };

  const getRoleChip = (role: string) => {
    let color: 'default' | 'primary' | 'secondary' | 'error' | 'info' | 'success' | 'warning' = 'default';
    switch (role.toLowerCase()) {
      case 'admin':
        color = 'error';
        break;
      case 'manager':
        color = 'primary';
        break;
      case 'staff':
        color = 'info';
        break;
      case 'supplier':
        color = 'secondary';
        break;
      case 'dealer':
        color = 'success';
        break;
    }
    
    return <Chip label={role} color={color} size="small" />;
  };

  const getStatusChip = (status: string) => {
    let color: 'default' | 'primary' | 'secondary' | 'error' | 'info' | 'success' | 'warning' = 'default';
    switch (status.toLowerCase()) {
      case 'active':
        color = 'success';
        break;
      case 'inactive':
        color = 'default';
        break;
      case 'pending':
        color = 'warning';
        break;
      case 'suspended':
        color = 'error';
        break;
    }
    
    return <Chip label={status} color={color} size="small" />;
  };

  const handleSuspendUser = async (userId: string, currentStatus: string) => {
    try {
      const newStatus = currentStatus.toLowerCase() === 'suspended' ? 'active' : 'suspended';
      await userManagementService.updateUserStatus(userId, newStatus);
      
      // Update local state optimistically
      setUsers(prevUsers => 
        prevUsers.map(user => 
          user.id === userId 
            ? { ...user, status: newStatus }
            : user
        )
      );
    } catch (err: any) {
      setError(err.message || 'Failed to update user status');
      console.error('Error updating user status:', err);
      // Revert on error
      fetchUsers();
    }
  };

  // Define columns for the users table
  const columns: Column[] = [
    { 
      id: 'firstName', 
      label: 'First Name', 
      minWidth: 100
    },
    { 
      id: 'lastName', 
      label: 'Last Name', 
      minWidth: 100
    },
    { 
      id: 'email', 
      label: 'Email', 
      minWidth: 170,
    },
    {
      id: 'role',
      label: 'Role',
      minWidth: 120,
      format: (value) => getRoleChip(value)
    },
    {
      id: 'status',
      label: 'Status',
      minWidth: 120,
      format: (value) => getStatusChip(value)
    }
  ];

  // Add company column only for admin view
  if (isAdminView) {
    columns.splice(3, 0, { 
      id: 'company', 
      label: 'Company', 
      minWidth: 150,
      format: (value) => value?.name || 'N/A'
    });
  }

  // Add actions column if actions are enabled
  if (!hideActions) {
    columns.push({
      id: 'actions',
      label: 'Actions',
      minWidth: 100,
      align: 'right',
      format: (_, row) => (
        <Box>
          {onEditUser && (
            <Tooltip title="Edit User">
              <IconButton size="small" onClick={(e) => {
                e.stopPropagation();
                onEditUser(row.id);
              }}>
                <EditIcon fontSize="small" />
              </IconButton>
            </Tooltip>
          )}
          {onResetPassword && (
            <Tooltip title="Reset Password">
              <IconButton size="small" onClick={(e) => {
                e.stopPropagation();
                onResetPassword(row.id);
              }}>
                <LockResetIcon fontSize="small" />
              </IconButton>
            </Tooltip>
          )}
          <Tooltip title={row.status === 'suspended' ? 'Activate User' : 'Suspend User'}>
            <IconButton 
              size="small" 
              onClick={(e) => {
                e.stopPropagation();
                handleSuspendUser(row.id, row.status);
              }}
              color={row.status === 'suspended' ? 'success' : 'default'}
            >
              <BlockIcon fontSize="small" />
            </IconButton>
          </Tooltip>
        </Box>
      )
    });
  }

  return (
    <Box sx={{ maxHeight }}>
      <Box sx={{ mb: 3, display: 'flex', flexDirection: { xs: 'column', md: 'row' }, gap: 2, justifyContent: 'space-between', alignItems: { xs: 'stretch', md: 'center' } }}>
        <Typography variant="h6" component="h2">
          Users
        </Typography>

        <Box sx={{ display: 'flex', flexDirection: { xs: 'column', sm: 'row' }, gap: 2 }}>
          <Box sx={{ display: 'flex', gap: 2 }}>
            <TextField
              placeholder="Search users..."
              size="small"
              value={searchTerm}
              onChange={(e) => handleSearch(e.target.value)}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <SearchIcon />
                  </InputAdornment>
                ),
                endAdornment: searchTerm && (
                  <InputAdornment position="end">
                    <Button size="small" onClick={() => handleSearch('')}>
                      Clear
                    </Button>
                  </InputAdornment>
                ),
              }}
            />

            <FormControl sx={{ minWidth: 120 }} size="small">
              <InputLabel id="role-filter-label">Role</InputLabel>
              <Select
                labelId="role-filter-label"
                value={selectedRole}
                onChange={handleRoleChange}
                label="Role"
              >
                <MenuItem value="">All Roles</MenuItem>
                <MenuItem value="admin">Admin</MenuItem>
                <MenuItem value="manager">Manager</MenuItem>
                <MenuItem value="staff">Staff</MenuItem>
                <MenuItem value="supplier">Supplier</MenuItem>
                <MenuItem value="dealer">Dealer</MenuItem>
              </Select>
            </FormControl>
            
            <FormControl sx={{ minWidth: 120 }} size="small">
              <InputLabel id="status-filter-label">Status</InputLabel>
              <Select
                labelId="status-filter-label"
                value={selectedStatus}
                onChange={handleStatusChange}
                label="Status"
              >
                <MenuItem value="">All Status</MenuItem>
                <MenuItem value="active">Active</MenuItem>
                <MenuItem value="inactive">Inactive</MenuItem>
                <MenuItem value="pending">Pending</MenuItem>
                <MenuItem value="suspended">Suspended</MenuItem>
              </Select>
            </FormControl>
          </Box>
          
          {!hideActions && (
            <Box sx={{ display: 'flex', gap: 2 }}>
              {onInviteUser && (
                <Button
                  variant="outlined"
                  startIcon={<PersonAddIcon />}
                  onClick={onInviteUser}
                >
                  Invite User
                </Button>
              )}
              
              {onAddUser && (
                <Button
                  variant="contained"
                  startIcon={<AddIcon />}
                  onClick={onAddUser}
                  sx={buttonStyle}
                >
                  Add User
                </Button>
              )}
            </Box>
          )}
        </Box>
      </Box>

      <DataTable
        columns={columns}
        data={users}
        loading={loading}
        error={error}
        onRowClick={(row) => onEditUser && onEditUser(row.id)}
        pagination={{
          page,
          totalCount,
          rowsPerPage,
          onPageChange: (newPage) => setPage(newPage),
          onRowsPerPageChange: (newRowsPerPage) => {
            setRowsPerPage(newRowsPerPage);
            setPage(0);
          },
        }}
      />
    </Box>
  );
};

export default UserList;

import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Button, 
  Typography, 
  TextField,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogContentText,
  DialogActions,
  useTheme as useMuiTheme
} from '@mui/material';
import { useTheme } from '../../../contexts/ThemeContext';
import { companyVerificationService } from '../../../services/api';

interface CompanyVerificationListProps {
  onVerificationComplete?: () => void;
}

const CompanyVerificationList: React.FC<CompanyVerificationListProps> = ({
  onVerificationComplete
}) => {
  const { theme: whitelabelTheme } = useTheme();
  const muiTheme = useMuiTheme();

  const [pendingVerifications, setPendingVerifications] = useState<any[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // For rejection dialog
  const [rejectDialogOpen, setRejectDialogOpen] = useState(false);
  const [rejectCompanyId, setRejectCompanyId] = useState<string | null>(null);
  const [rejectReason, setRejectReason] = useState('');
  const [rejectionError, setRejectionError] = useState<string | null>(null);

  // For document preview
  const [previewUrl, setPreviewUrl] = useState<string | null>(null);
  const [previewDialogOpen, setPreviewDialogOpen] = useState(false);

  // Custom button styles based on the whitelabel theme
  const buttonStyles = {
    approve: {
      backgroundColor: whitelabelTheme?.primaryColor || muiTheme.palette.success.main,
      color: '#fff',
      borderRadius: whitelabelTheme?.buttonRadius !== undefined ? `${whitelabelTheme.buttonRadius}px` : undefined,
      '&:hover': {
        backgroundColor: whitelabelTheme?.primaryColor ? 
          `${whitelabelTheme.primaryColor}dd` : muiTheme.palette.success.dark,
      },
    },
    reject: {
      backgroundColor: muiTheme.palette.error.main,
      color: '#fff',
      borderRadius: whitelabelTheme?.buttonRadius !== undefined ? `${whitelabelTheme.buttonRadius}px` : undefined,
      '&:hover': {
        backgroundColor: muiTheme.palette.error.dark,
      },
    }
  };

  const fetchPendingVerifications = async () => {
    setLoading(true);
    setError(null);
    try {
      const verifications = await companyVerificationService.getPendingVerifications();
      setPendingVerifications(verifications || []);
    } catch (err: any) {
      setError(err.message || 'Failed to load pending verifications');
      console.error('Error fetching verifications:', err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPendingVerifications();
  }, []);

  const handleApprove = async (companyId: string) => {
    try {
      await companyVerificationService.approveCompany(companyId);
      // Remove approved company from the list
      setPendingVerifications(prev => prev.filter(v => v.company_id !== companyId));
      
      if (onVerificationComplete) {
        onVerificationComplete();
      }
    } catch (err: any) {
      setError(err.message || `Failed to approve company: ${companyId}`);
      console.error('Error approving company:', err);
    }
  };

  const openRejectDialog = (companyId: string) => {
    setRejectCompanyId(companyId);
    setRejectReason('');
    setRejectionError(null);
    setRejectDialogOpen(true);
  };

  const handleReject = async () => {
    if (!rejectCompanyId) return;
    
    if (!rejectReason.trim()) {
      setRejectionError('Rejection reason is required');
      return;
    }
    
    try {
      await companyVerificationService.rejectCompany(rejectCompanyId, rejectReason);
      // Remove rejected company from the list
      setPendingVerifications(prev => prev.filter(v => v.company_id !== rejectCompanyId));
      
      if (onVerificationComplete) {
        onVerificationComplete();
      }
      
      setRejectDialogOpen(false);
    } catch (err: any) {
      setRejectionError(err.message || `Failed to reject company: ${rejectCompanyId}`);
      console.error('Error rejecting company:', err);
    }
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString() + ' ' + date.toLocaleTimeString();
  };

  const handleDocumentPreview = (url: string) => {
    setPreviewUrl(url);
    setPreviewDialogOpen(true);
  };

  if (loading && pendingVerifications.length === 0) {
    return (
      <Box sx={{ textAlign: 'center', py: 4 }}>
        <Typography>Loading pending verifications...</Typography>
      </Box>
    );
  }

  if (error) {
    return (
      <Box sx={{ textAlign: 'center', py: 4, color: 'error.main' }}>
        <Typography>{error}</Typography>
        <Button 
          variant="outlined"
          onClick={fetchPendingVerifications}
          sx={{ mt: 2 }}
        >
          Retry
        </Button>
      </Box>
    );
  }

  if (pendingVerifications.length === 0) {
    return (
      <Box sx={{ textAlign: 'center', py: 4 }}>
        <Typography variant="h6">No pending verifications</Typography>
        <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
          All company registrations have been processed
        </Typography>
      </Box>
    );
  }

  return (
    <Box>
      <Typography variant="h6" component="h2" gutterBottom>
        Pending Company Verifications
      </Typography>
      
      <Box sx={{ display: 'flex', flexDirection: 'row', flexWrap: 'wrap', gap: 2 }}>
        {pendingVerifications.map((verification) => (
          <Box
            key={verification.company_id}
            sx={{ flex: '1 1 calc(50% - 16px)', minWidth: '300px', p: 2, border: '1px solid #ddd', borderRadius: 1 }}
          >
            <Typography variant="h6">{verification.company_name}</Typography>
            <Typography variant="body2" color="text.secondary">
              {verification.status}
            </Typography>
          </Box>
        ))}
      </Box>

      {/* Reject Dialog */}
      <Dialog open={rejectDialogOpen} onClose={() => setRejectDialogOpen(false)}>
        <DialogTitle>Reject Company Registration</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Please provide a reason for rejecting this company registration.
            This information will be shared with the company.
          </DialogContentText>
          <TextField
            autoFocus
            margin="dense"
            label="Rejection Reason"
            fullWidth
            multiline
            rows={3}
            value={rejectReason}
            onChange={(e) => setRejectReason(e.target.value)}
            error={!!rejectionError}
            helperText={rejectionError}
            variant="outlined"
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setRejectDialogOpen(false)}>Cancel</Button>
          <Button onClick={handleReject} color="error" variant="contained">Confirm Rejection</Button>
        </DialogActions>
      </Dialog>

      {/* Document Preview Dialog */}
      <Dialog 
        open={previewDialogOpen} 
        onClose={() => setPreviewDialogOpen(false)}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle>Document Preview</DialogTitle>
        <DialogContent>
          {previewUrl && (
            previewUrl.toLowerCase().endsWith('.pdf') ? (
              <Box sx={{ height: '70vh' }}>
                <iframe 
                  src={previewUrl} 
                  width="100%" 
                  height="100%" 
                  title="Document Preview"
                />
              </Box>
            ) : (
              <Box sx={{ textAlign: 'center' }}>
                <img 
                  src={previewUrl} 
                  alt="Document Preview" 
                  style={{ maxWidth: '100%', maxHeight: '70vh' }}
                />
              </Box>
            )
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setPreviewDialogOpen(false)}>Close</Button>
          <Button component="a" href={previewUrl || '#'} target="_blank" rel="noopener noreferrer">
            Open in New Tab
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default CompanyVerificationList;

import React, { createContext, useState, useContext, useEffect } from 'react';
import { authService } from '../services/api';
import { UserInfo } from '../services/api/authService';

interface AuthContextType {
  isAuthenticated: boolean;
  user: UserInfo | null;
  loading: boolean;
  login: (email: string, password: string) => Promise<any>;
  logout: () => void;
  validateToken: () => Promise<boolean>;
}

const AuthContext = createContext<AuthContextType>({
  isAuthenticated: false,
  user: null,
  loading: true,
  login: async () => ({}),
  logout: () => {},
  validateToken: async () => false,
});

export const AuthProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [user, setUser] = useState<UserInfo | null>(null);
  const [loading, setLoading] = useState(true);
  
  // Check authentication status on mount
  useEffect(() => {
    console.log('AuthProvider: Initializing auth state');
    initializeAuth();
  }, []);
  
  const initializeAuth = async () => {
    setLoading(true);
    try {
      // First check if we have an access token
      const token = localStorage.getItem('access_token');
      if (!token) {
        console.log('No access token found, user is not authenticated');
        setUser(null);
        return;
      }
      
      // Then validate the token
      const isValid = await validateToken();
      if (!isValid) {
        console.log('Token is invalid or expired');
        setUser(null);
        return;
      }
      
      // Get user info from storage
      const storedUser = localStorage.getItem('user_info');
      if (storedUser) {
        try {
          const parsedUser = JSON.parse(storedUser);
          console.log('User info retrieved from storage:', parsedUser);
          setUser(parsedUser);
        } catch (error) {
          console.error('Failed to parse stored user info:', error);
          setUser(null);
        }
      } else {
        console.log('No user info found in storage');
        setUser(null);
      }
    } catch (error) {
      console.error('Error initializing auth:', error);
      setUser(null);
    } finally {
      setLoading(false);
    }
  };
  
  const login = async (email: string, password: string) => {
    try {
      const response = await authService.login({
        email,
        password,
      });
      
      console.log('Login successful, auth response:', response);
      
      // The tokens should be automatically stored by authService.login
      // We just need to set the user state here
      const userInfo = authService.getCurrentUser();
      console.log('Current user info:', userInfo);
      
      setUser(userInfo);
      return response;
    } catch (error) {
      console.error('Login error:', error);
      throw error;
    }
  };
  
  const logout = () => {
    console.log('Logging out user');
    authService.logout();
    setUser(null);
  };
  
  const validateToken = async (): Promise<boolean> => {
    try {
      console.log('Validating auth token');
      const valid = await authService.validateToken();
      console.log('Token validation result:', valid);
      return valid;
    } catch (error) {
      console.error('Token validation error:', error);
      return false;
    }
  };
  
  return (
    <AuthContext.Provider
      value={{
        isAuthenticated: !!user,
        user,
        loading,
        login,
        logout,
        validateToken,
      }}
    >
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);

import React, { createContext, useContext, useState, useEffect, ReactNode } from 'react';
import { 
  CompanySettings, 
  defaultSettings, 
  getEffectiveSettings, 
  saveCompanySettings, 
  saveUserOverrides, 
  loadUserOverrides,
  loadCompanySettings,
  resetSection,
  SettingsSection
} from '../utils/companySettings';
import { useAuth } from './AuthContext';

// Define the context type
interface CompanySettingsContextType {
  settings: CompanySettings;
  updateSettings: (newSettings: Partial<CompanySettings>, applyToCompany?: boolean) => void;
  isCompanyAdmin: boolean;
  applyCompanySettings: () => void;
  resetSettingsSection: (section: string) => void;
  userOverrides: Partial<CompanySettings>;
  companySettings: CompanySettings;
}

// Create the context
const CompanySettingsContext = createContext<CompanySettingsContextType>({
  settings: defaultSettings,
  updateSettings: () => {},
  isCompanyAdmin: false,
  applyCompanySettings: () => {},
  resetSettingsSection: () => {},
  userOverrides: {},
  companySettings: defaultSettings,
});

interface CompanySettingsProviderProps {
  children: ReactNode;
}

export const CompanySettingsProvider: React.FC<CompanySettingsProviderProps> = ({ children }) => {
  const { user } = useAuth();
  const [settings, setSettings] = useState<CompanySettings>(defaultSettings);
  const [companySettings, setCompanySettings] = useState<CompanySettings>(defaultSettings);
  const [userOverrides, setUserOverrides] = useState<Partial<CompanySettings>>({});
  const [isCompanyAdmin, setIsCompanyAdmin] = useState(false);  // Function to load settings on initialization or refresh
  const loadUserSettings = () => {
    if (user && user.userId) {
      // Determine if user is a company admin
      setIsCompanyAdmin(user.role === 'admin');
      
      // Load company settings
      const company = loadCompanySettings(user.companyId);
      setCompanySettings(company);
      
      // Load user overrides
      const overrides = loadUserOverrides(user.userId);
      setUserOverrides(overrides);
      
      // Load effective settings (company + user overrides)
      const effectiveSettings = getEffectiveSettings(user.companyId, user.userId);
      setSettings(effectiveSettings);
    }
  };

  // Initialize settings when user info becomes available
  useEffect(() => {
    loadUserSettings();
  }, [user]);
  // Update settings
  const updateSettings = (newSettings: Partial<CompanySettings>, applyToCompany = false) => {
    // Create updated settings object using deep merge
    const updatedSettings = {
      ...settings,
      ...newSettings,
      appearance: {
        ...settings.appearance,
        ...(newSettings.appearance || {}),
      },
      notifications: {
        ...settings.notifications,
        ...(newSettings.notifications || {}),
      },
      integrations: {
        ...settings.integrations,
        ...(newSettings.integrations || {}),
      },
    };

    // Update state
    setSettings(updatedSettings);

    // Save to storage
    if (user) {      
      if (applyToCompany && isCompanyAdmin) {
        // Save as company settings
        saveCompanySettings(user.companyId, updatedSettings);
        
        // Update company settings state
        setCompanySettings(updatedSettings);
      } else {
        // Save as user overrides
        saveUserOverrides(user.userId, updatedSettings);
        
        // Refresh user overrides
        const overrides = loadUserOverrides(user.userId);
        setUserOverrides(overrides);
      }
    }
  };  // Apply company settings by clearing user overrides
  const applyCompanySettings = () => {
    if (user) {
      // Clear user overrides
      saveUserOverrides(user.userId, {});
      
      // Load fresh effective settings
      const effectiveSettings = getEffectiveSettings(user.companyId, user.userId);
      setSettings(effectiveSettings);
      
      // Update user overrides state
      setUserOverrides({});
    }
  };// Function to reset a specific section
  const resetSettingsSection = (section: string) => {
    if (user && user.userId) {
      // Reset the specified section
      const updatedSettings = resetSection(user.userId, user.companyId, section as SettingsSection);
      
      // Update state
      setSettings(updatedSettings);
      
      // Refresh user overrides
      const overrides = loadUserOverrides(user.userId);
      setUserOverrides(overrides);
    }
  };

  return (
    <CompanySettingsContext.Provider 
      value={{ 
        settings, 
        updateSettings, 
        isCompanyAdmin,
        applyCompanySettings,
        resetSettingsSection,
        userOverrides,
        companySettings 
      }}
    >
      {children}
    </CompanySettingsContext.Provider>
  );
};

// Custom hook for using company settings
export const useCompanySettings = () => useContext(CompanySettingsContext);

export default CompanySettingsContext;

import React, { createContext, useContext, useState, useCallback } from 'react';
import { 
  Snackbar,
  Alert,
  AlertColor,
  Slide,
  SlideProps
} from '@mui/material';

type SlideDirection = 'up' | 'down' | 'left' | 'right';

interface Toast {
  id: number;
  message: string;
  severity: AlertColor;
  duration?: number;
  slideDirection?: SlideDirection;
}

interface ToastContextType {
  showToast: (message: string, severity?: AlertColor, duration?: number, slideDirection?: SlideDirection) => void;
  success: (message: string, duration?: number) => void;
  error: (message: string, duration?: number) => void;
  warning: (message: string, duration?: number) => void;
  info: (message: string, duration?: number) => void;
}

const ToastContext = createContext<ToastContextType | undefined>(undefined);

// Custom SlideTransition component
const SlideTransition = (props: SlideProps & { direction: SlideDirection }) => {
  return <Slide {...props} direction={props.direction} />;
};

export const ToastProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [toasts, setToasts] = useState<Toast[]>([]);
  const [open, setOpen] = useState(false);
  const [current, setCurrent] = useState<Toast | null>(null);
  const [toastIdCounter, setToastIdCounter] = useState(0);

  const showToast = useCallback((
    message: string, 
    severity: AlertColor = 'info', 
    duration: number = 4000,
    slideDirection: SlideDirection = 'left'
  ) => {
    const id = toastIdCounter;
    setToastIdCounter(prev => prev + 1);
    
    const newToast = { id, message, severity, duration, slideDirection };
    
    setToasts(prev => [...prev, newToast]);
    
    if (!open) {
      setCurrent(newToast);
      setOpen(true);
    }
  }, [open, toastIdCounter]);
  
  const success = useCallback((message: string, duration?: number) => 
    showToast(message, 'success', duration), [showToast]);
    
  const error = useCallback((message: string, duration?: number) => 
    showToast(message, 'error', duration), [showToast]);
    
  const warning = useCallback((message: string, duration?: number) => 
    showToast(message, 'warning', duration), [showToast]);
    
  const info = useCallback((message: string, duration?: number) => 
    showToast(message, 'info', duration), [showToast]);

  const handleClose = (_event?: React.SyntheticEvent | Event, reason?: string) => {
    if (reason === 'clickaway') {
      return;
    }
    setOpen(false);
  };

  const handleExited = () => {
    setToasts(prev => {
      const newToasts = prev.filter(toast => toast.id !== current?.id);
      
      if (newToasts.length > 0) {
        setCurrent(newToasts[0]);
        setOpen(true);
      } else {
        setCurrent(null);
      }
      
      return newToasts;
    });
  };

  return (
    <ToastContext.Provider value={{ showToast, success, error, warning, info }}>
      {children}
      <Snackbar
        open={open}
        autoHideDuration={current?.duration}
        onClose={handleClose}
        TransitionComponent={(props) => (
          <SlideTransition {...props} direction={current?.slideDirection || 'left'} />
        )}
        TransitionProps={{ 
          onExited: handleExited 
        }}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
      >
        <Alert 
          onClose={handleClose} 
          severity={current?.severity || 'info'}
          variant="filled"
          elevation={6}
          sx={{ width: '100%' }}
        >
          {current?.message || ''}
        </Alert>
      </Snackbar>
    </ToastContext.Provider>
  );
};

export const useToast = (): ToastContextType => {
  const context = useContext(ToastContext);
  if (context === undefined) {
    throw new Error('useToast must be used within a ToastProvider');
  }
  return context;
};

import { useState, useCallback, useRef, useEffect } from 'react';

// Types
export type ApiRequestStatus = 'idle' | 'loading' | 'success' | 'error';

export interface ApiRequestState<T> {
  data: T | null;
  error: Error | null;
  status: ApiRequestStatus;
  timestamp: number | null;
}

export interface ApiRequestOptions<T> {
  onSuccess?: (data: T) => void;
  onError?: (error: Error) => void;
  dependencies?: any[];
  initialData?: T | null;
  cache?: boolean;
  cacheKey?: string;
  cacheDuration?: number; // in milliseconds
  retry?: boolean;
  maxRetries?: number;
  retryDelay?: number; // in milliseconds
  manual?: boolean; // if true, request won't be made automatically
}

// Simple cache implementation
interface CacheItem<T> {
  data: T;
  timestamp: number;
  expiresAt: number;
}

// Global cache store for sharing across hook instances
const apiCache = new Map<string, CacheItem<any>>();

// Function to create a default cache key from a function and arguments
function createCacheKey(
  fn: (...args: any[]) => Promise<any>,
  args: any[]
): string {
  return `${fn.name || 'anonymous'}_${JSON.stringify(args)}`;
}

// Main hook
export function useApiRequest<T, Args extends any[] = any[]>(
  fn: (...args: Args) => Promise<T>,
  defaultArgs?: Args,
  options: ApiRequestOptions<T> = {}
) {
  const {
    onSuccess,
    onError,
    dependencies = [],
    initialData = null,
    cache = false,
    cacheKey: userCacheKey,
    cacheDuration = 5 * 60 * 1000, // 5 minutes default
    retry = false,
    maxRetries = 3,
    retryDelay = 1000,
    manual = false,
  } = options;

  // Initial state
  const [state, setState] = useState<ApiRequestState<T>>({
    data: initialData,
    error: null,
    status: 'idle',
    timestamp: null,
  });

  // Refs for latest values to avoid closure issues in callbacks
  const latestFn = useRef(fn);
  const latestOptions = useRef(options);
  const retryCount = useRef(0);
  const requestInProgress = useRef(false);

  // Keep refs updated
  useEffect(() => {
    latestFn.current = fn;
    latestOptions.current = options;
  }, [fn, options]);

  // Execute request helper function
  const executeRequest = useCallback(
    async (...args: Args): Promise<T> => {
      // Don't allow multiple in-flight requests for the same hook instance
      if (requestInProgress.current) {
        throw new Error('Request already in progress');
      }

      requestInProgress.current = true;
      retryCount.current = 0;
      
      // If cache is enabled, generate cache key or use provided one
      const cacheKey = cache
        ? userCacheKey || createCacheKey(fn, args)
        : null;

      // Check cache first if enabled
      if (cache && cacheKey) {
        const cachedItem = apiCache.get(cacheKey);
        
        // If we have a cached item and it's not expired
        if (
          cachedItem &&
          cachedItem.expiresAt > Date.now()
        ) {
          // Update state with cached data
          setState({
            data: cachedItem.data,
            error: null,
            status: 'success',
            timestamp: cachedItem.timestamp,
          });
          
          // Call onSuccess if provided
          if (onSuccess) onSuccess(cachedItem.data);
          
          requestInProgress.current = false;
          return cachedItem.data;
        }
      }

      // Set loading state
      setState(prev => ({
        ...prev,
        status: 'loading',
        error: null,
      }));

      // Try to execute the request with retry logic
      const attemptRequest = async (attempt: number): Promise<T> => {
        try {
          const data = await latestFn.current(...args);
          
          // Cache result if caching is enabled
          if (cache && cacheKey) {
            const now = Date.now();
            apiCache.set(cacheKey, {
              data,
              timestamp: now,
              expiresAt: now + cacheDuration,
            });
          }
          
          // Update state with successful data
          setState({
            data,
            error: null,
            status: 'success',
            timestamp: Date.now(),
          });
          
          // Call onSuccess if provided
          if (latestOptions.current.onSuccess) {
            latestOptions.current.onSuccess(data);
          }
          
          requestInProgress.current = false;
          return data;
          
        } catch (error) {
          // If we should retry and haven't exceeded max retries
          if (retry && attempt < maxRetries) {
            retryCount.current = attempt + 1;
            
            // Wait before retrying
            await new Promise(resolve => 
              setTimeout(resolve, retryDelay * Math.pow(2, attempt))
            );
            
            // Try again
            return attemptRequest(attempt + 1);
            
          } else {
            // We're out of retries or retrying is disabled
            const errorObj = error instanceof Error ? error : new Error(String(error));
            
            // Update state with the error
            setState({
              data: null,
              error: errorObj,
              status: 'error',
              timestamp: Date.now(),
            });
            
            // Call onError if provided
            if (latestOptions.current.onError) {
              latestOptions.current.onError(errorObj);
            }
            
            requestInProgress.current = false;
            throw errorObj;
          }
        }
      };

      // Start the initial attempt
      return attemptRequest(0);
    },
    [
      fn,
      onSuccess,
      onError,
      cache,
      userCacheKey,
      cacheDuration,
      retry,
      maxRetries,
      retryDelay,
    ]
  );

  // Main request function
  const request = useCallback(
    (...args: Args) => {
      return executeRequest(...(args.length ? args : defaultArgs || [] as unknown as Args));
    },
    [executeRequest, defaultArgs]
  );

  // Auto-execute on mount or when dependencies change, unless manual=true
  useEffect(() => {
    if (!manual && defaultArgs) {
      request(...defaultArgs).catch(err => {
        // Error is already handled in executeRequest
        // This catch is just to prevent unhandled rejection
      });
    }
  }, [request, manual, defaultArgs, ...dependencies]);

  // Function to invalidate cached data
  const invalidateCache = useCallback(
    (specificCacheKey?: string) => {
      if (cache) {
        if (specificCacheKey) {
          apiCache.delete(specificCacheKey);
        } else if (userCacheKey) {
          apiCache.delete(userCacheKey);
        }
      }
    },
    [cache, userCacheKey]
  );

  // Reset state
  const reset = useCallback(() => {
    setState({
      data: initialData,
      error: null,
      status: 'idle',
      timestamp: null,
    });
  }, [initialData]);

  return {
    ...state,
    isLoading: state.status === 'loading',
    isSuccess: state.status === 'success',
    isError: state.status === 'error',
    request,
    reset,
    invalidateCache,
  };
}

export default useApiRequest;

import { useState, useCallback } from 'react';
import useTableData from './useTableData';
import useApiRequest from './useApiRequest';
import { inventoryService } from '../services/api';
import { InventoryItem, InventoryCreateInput, ApiResponse } from '../services/api/inventoryService';

interface UseInventoryOperationsProps {
  companyId: string;
  warehouseId?: string;
  onInventoryCreated?: (item: InventoryItem) => void;
  onInventoryUpdated?: (item: InventoryItem) => void;
  onInventoryDeleted?: (itemId: string) => void;
}

/**
 * Custom hook for inventory operations
 */
export function useInventoryOperations({
  companyId,
  warehouseId,
  onInventoryCreated,
  onInventoryUpdated,
  onInventoryDeleted
}: UseInventoryOperationsProps) {
  // Function to fetch inventory data with parameters
  const fetchInventoryData = useCallback(
    async (params: any) => {
      if (!companyId) {
        return { data: [], totalCount: 0 };
      }

      const apiParams = {
        companyId,
        warehouseId,
        page: params.page,
        limit: params.limit,
        sortBy: params.sortBy,
        sortDirection: params.sortDirection,
        search: params.search || undefined,
        ...params.filters
      };

      try {
        const response = await inventoryService.getInventory(apiParams);
        return {
          data: response.data || [],
          totalCount: response.pagination?.total || response.data?.length || 0
        };
      } catch (error) {
        console.error('Error fetching inventory:', error);
        throw error;
      }
    },
    [companyId, warehouseId]
  );

  // Use table hook for inventory data
  const inventoryTable = useTableData<InventoryItem>({
    defaultSortBy: 'product.name',
    defaultSortDirection: 'asc',
    fetchDataFn: fetchInventoryData
  });
  // API request for creating inventory item
  const createInventoryRequest = useApiRequest<{ data: InventoryItem }, [InventoryCreateInput]>(
    async (itemData) => {
      return await inventoryService.createInventoryItem(itemData);
    },
    [{ } as InventoryCreateInput], // Provide default args with correct type
    {
      manual: true,
      onSuccess: (response) => {
        inventoryTable.fetchData();
        if (onInventoryCreated && response.data) {
          onInventoryCreated(response.data);
        }
      }
    }
  );
  // API request for adjusting inventory
  const adjustInventoryRequest = useApiRequest<ApiResponse<void>, [string, number, string]>(
    async (inventoryId, adjustment, reason) => {
      return await inventoryService.adjustInventory(inventoryId, { adjustment, reason });
    },
    ['', 0, ''], // Provide default args with correct types
    {
      manual: true,
      onSuccess: () => {
        inventoryTable.fetchData();
      }
    }
  );
  // API request for transferring inventory
  const transferInventoryRequest = useApiRequest<any, [any]>(
    async (transferData) => {
      return await inventoryService.transferInventory(transferData);
    },
    [{}], // Provide a default empty object argument
    {
      manual: true,
      onSuccess: () => {
        inventoryTable.fetchData();
      }
    }
  );

  // Helper function to create a new inventory item
  const createInventoryItem = useCallback(
    (itemData: InventoryCreateInput) => {
      if (!companyId) {
        return Promise.reject(new Error('Company ID is missing'));
      }
      
      const fullItemData = {
        ...itemData,
        company_id: companyId
      };
      
      return createInventoryRequest.request(fullItemData);
    },
    [companyId, createInventoryRequest]
  );

  // Helper function to adjust inventory
  const adjustInventory = useCallback(
    (inventoryId: string, adjustment: number, reason: string) => {
      return adjustInventoryRequest.request(inventoryId, adjustment, reason);
    },
    [adjustInventoryRequest]
  );

  // Helper function to transfer inventory
  const transferInventory = useCallback(
    (sourceWarehouseId: string, targetWarehouseId: string, productId: string, quantity: number) => {
      if (!companyId) {
        return Promise.reject(new Error('Company ID is missing'));
      }
      
      const transferData = {
        source_warehouse_id: sourceWarehouseId,
        target_warehouse_id: targetWarehouseId,
        product_id: productId,
        quantity: quantity
      };
      
      return transferInventoryRequest.request(transferData);
    },
    [companyId, transferInventoryRequest]
  );

  // Get combined error from any of the API requests
  const error =
    inventoryTable.error ||
    createInventoryRequest.error?.message ||
    adjustInventoryRequest.error?.message ||
    transferInventoryRequest.error?.message;

  // Check if any request is loading
  const isLoading =
    inventoryTable.loading ||
    createInventoryRequest.isLoading ||
    adjustInventoryRequest.isLoading ||
    transferInventoryRequest.isLoading;

  // Function to refetch data when warehouse changes
  const refreshInventory = useCallback(() => {
    inventoryTable.fetchData();
  }, [inventoryTable]);

  return {
    // State
    inventory: inventoryTable.data,
    isLoading,
    error,
    
    // Table state and actions
    page: inventoryTable.page,
    rowsPerPage: inventoryTable.rowsPerPage,
    totalCount: inventoryTable.totalCount,
    sortBy: inventoryTable.sortBy,
    sortDirection: inventoryTable.sortDirection,
    searchQuery: inventoryTable.searchQuery,
    filters: inventoryTable.filters,
    
    // Table handlers
    handlePageChange: inventoryTable.handlePageChange,
    handleRowsPerPageChange: inventoryTable.handleRowsPerPageChange,
    handleSortByChange: inventoryTable.handleSortByChange,
    handleSortDirectionChange: inventoryTable.handleSortDirectionChange,
    handleSearchQueryChange: inventoryTable.handleSearchQueryChange,
    handleFilterChange: inventoryTable.handleFilterChange,
    resetFilters: inventoryTable.resetFilters,
    
    // Inventory operation functions
    refreshInventory,
    createInventoryItem,
    adjustInventory,
    transferInventory,
    
    // API request states
    createInventoryRequest,
    adjustInventoryRequest,
    transferInventoryRequest
  };
}

export default useInventoryOperations;

import { useState, useEffect } from 'react';
import { useMockData } from '../contexts/MockDataContext';
import { isMockApiEnabled } from '../services/api/apiClient';

/**
 * This hook provides a way to use mock data as a fallback when the API is not available.
 * It's especially useful during development when you want to work without a backend.
 * 
 * @param apiCall - The function that makes the actual API call
 * @param mockDataSelector - A function that selects the appropriate mock data
 * @param dependencies - An array of dependencies that should trigger a re-fetch
 */
export function useMockableData<T>(
  apiCall: () => Promise<T>,
  mockDataSelector: () => T,
  dependencies: any[] = []
) {
  const [data, setData] = useState<T | null>(null);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<Error | null>(null);
  const { isMockEnabled } = useMockData();

  useEffect(() => {
    let isMounted = true;

    const fetchData = async () => {
      setLoading(true);
      setError(null);

      try {
        if (isMockEnabled || isMockApiEnabled) {
          // Use mock data
          const mockData = mockDataSelector();
          
          // Artificial delay to simulate network request
          await new Promise(resolve => setTimeout(resolve, 500));
          
          if (isMounted) {
            setData(mockData);
            setLoading(false);
          }
        } else {
          // Use real API
          const result = await apiCall();
          
          if (isMounted) {
            setData(result);
            setLoading(false);
          }
        }
      } catch (err) {
        console.error('Error fetching data:', err);
        
        if (isMounted) {
          setError(err as Error);
          setLoading(false);
          
          // If real API fails, fallback to mock data
          const mockData = mockDataSelector();
          setData(mockData);
        }
      }
    };

    fetchData();

    return () => {
      isMounted = false;
    };
  }, [...dependencies]);

  return { data, loading, error };
}

export default useMockableData;

import React, { useState, useCallback, useRef } from 'react';
import useApiRequest from './useApiRequest';
import { warehouseService } from '../services/api';
import { Warehouse, WarehouseCreateInput, WarehouseUpdateInput } from '../services/api/warehouseService';

interface UseWarehouseOperationsProps {
  companyId: string;
  onWarehouseCreated?: (warehouse: Warehouse) => void;
  onWarehouseUpdated?: (warehouse: Warehouse) => void;
  onWarehouseDeleted?: (warehouseId: string) => void;
}

/**
 * Custom hook for warehouse CRUD operations
 */
export function useWarehouseOperations({
  companyId,
  onWarehouseCreated,
  onWarehouseUpdated,
  onWarehouseDeleted
}: UseWarehouseOperationsProps) {  // Track the currently selected warehouse
  const [selectedWarehouse, setSelectedWarehouse] = useState<string>('');
  
  // Reference to store the current warehouse ID for delete operation
  const deleteWarehouseIdRef = useRef<string>('');

  // API request for fetching warehouses
  const warehousesRequest = useApiRequest<{ data: Warehouse[] }>(
    async () => {
      if (!companyId) {
        return { data: [] };
      }
      return await warehouseService.getWarehouses(companyId);
    },
    [],
    {
      dependencies: [companyId],
      manual: !companyId
    }
  );
  // API request for fetching a single warehouse by ID
  const singleWarehouseRequest = useApiRequest<{ data: Warehouse }, [string, string]>(
    async (companyId, warehouseId) => {
      return await warehouseService.getWarehouse(companyId, warehouseId);
    },
    ['', ''], // Default empty string arguments
    { manual: true }
  );
  // API request for creating a warehouse
  const createWarehouseRequest = useApiRequest<{ data: Warehouse }, [string, WarehouseCreateInput]>(
    async (companyId, warehouseData) => {
      return await warehouseService.createWarehouse(companyId, warehouseData);
    },
    ['', {} as WarehouseCreateInput], // Default arguments
    {
      manual: true,
      onSuccess: (response) => {
        warehousesRequest.request();
        if (onWarehouseCreated && response.data) {
          onWarehouseCreated(response.data);
        }
      }
    }
  );
  // API request for updating a warehouse
  const updateWarehouseRequest = useApiRequest<{ data: Warehouse }, [string, string, WarehouseUpdateInput]>(
    async (companyId, warehouseId, warehouseData) => {
      return await warehouseService.updateWarehouse(companyId, warehouseId, warehouseData);
    },
    ['', '', {} as WarehouseUpdateInput], // Default arguments
    {
      manual: true,
      onSuccess: (response) => {
        warehousesRequest.request();
        if (onWarehouseUpdated && response.data) {
          onWarehouseUpdated(response.data);
        }
      }
    }
  );
  // API request for deleting a warehouse
  const deleteWarehouseRequest = useApiRequest<any, [string, string]>(
    async (companyId, warehouseId) => {
      return await warehouseService.deleteWarehouse(companyId, warehouseId);
    },
    ['', ''], // Default arguments
    {      manual: true,
      onSuccess: (response) => {
        // Store warehouseId locally from the request context
        const storedWarehouseId = deleteWarehouseIdRef.current;
        
        warehousesRequest.request();
        if (onWarehouseDeleted && storedWarehouseId) {
          onWarehouseDeleted(storedWarehouseId);
        }
        
        // If the deleted warehouse was selected, reset selection
        if (selectedWarehouse === storedWarehouseId) {
          setSelectedWarehouse('');
        }
      }
    }
  );

  // Helper function to fetch all warehouses
  const fetchWarehouses = useCallback(() => {
    if (companyId) {
      warehousesRequest.request();
    }
  }, [companyId, warehousesRequest]);

  // Helper function to fetch a specific warehouse
  const fetchWarehouse = useCallback(
    (warehouseId: string) => {
      if (companyId && warehouseId) {
        return singleWarehouseRequest.request(companyId, warehouseId);
      }
      return Promise.reject(new Error('Company ID or Warehouse ID is missing'));
    },
    [companyId, singleWarehouseRequest]
  );

  // Helper function to create a new warehouse
  const createWarehouse = useCallback(
    (warehouseData: WarehouseCreateInput) => {
      if (companyId) {
        return createWarehouseRequest.request(companyId, warehouseData);
      }
      return Promise.reject(new Error('Company ID is missing'));
    },
    [companyId, createWarehouseRequest]
  );

  // Helper function to update a warehouse
  const updateWarehouse = useCallback(
    (warehouseId: string, warehouseData: WarehouseUpdateInput) => {
      if (companyId && warehouseId) {
        return updateWarehouseRequest.request(companyId, warehouseId, warehouseData);
      }
      return Promise.reject(new Error('Company ID or Warehouse ID is missing'));
    },
    [companyId, updateWarehouseRequest]
  );
  // Helper function to delete a warehouse
  const deleteWarehouse = useCallback(
    (warehouseId: string) => {
      if (companyId && warehouseId) {
        // Store the warehouseId in the ref to access it in onSuccess callback
        deleteWarehouseIdRef.current = warehouseId;
        return deleteWarehouseRequest.request(companyId, warehouseId);
      }
      return Promise.reject(new Error('Company ID or Warehouse ID is missing'));
    },
    [companyId, deleteWarehouseRequest]
  );

  // Get combined error from any of the API requests
  const error =
    warehousesRequest.error ||
    singleWarehouseRequest.error ||
    createWarehouseRequest.error ||
    updateWarehouseRequest.error ||
    deleteWarehouseRequest.error;

  // Check if any request is loading
  const isLoading =
    warehousesRequest.isLoading ||
    singleWarehouseRequest.isLoading ||
    createWarehouseRequest.isLoading ||
    updateWarehouseRequest.isLoading ||
    deleteWarehouseRequest.isLoading;

  // Get warehouses from the request data
  const warehouses = warehousesRequest.data?.data || [];

  return {
    // State
    selectedWarehouse,
    setSelectedWarehouse,
    warehouses,
    error,
    isLoading,
    
    // API request states
    warehousesRequest,
    singleWarehouseRequest,
    createWarehouseRequest,
    updateWarehouseRequest,
    deleteWarehouseRequest,
    
    // Helper methods
    fetchWarehouses,
    fetchWarehouse,
    createWarehouse,
    updateWarehouse,
    deleteWarehouse
  };
}

export default useWarehouseOperations;

import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Container, 
  Paper, 
  Typography,
  Tabs,
  Tab,
  CircularProgress,
  Alert,
  Button
} from '@mui/material';
import PendingEmployeesList from '../components/whitelabel/users/PendingEmployeesList';
import EmployeeInvitationGenerator from '../components/whitelabel/users/EmployeeInvitationGenerator';
import { dashboardService } from '../services/api/dashboardService';
import { productService, orderService, inventoryService } from '../services/api';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate, useLocation } from 'react-router-dom';

interface TabPanelProps {
  children?: React.ReactNode;
  index: number;
  value: number;
}

function TabPanel(props: TabPanelProps) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`dashboard-tabpanel-${index}`}
      aria-labelledby={`dashboard-tab-${index}`}
      {...other }
    >
      {value === index && (
        <Box sx={{ p: 3 }}>
          {children}
        </Box>
      )}
    </div>
  );
}

function a11yProps(index: number) {
  return {
    id: `dashboard-tab-${index}`,
    'aria-controls': `dashboard-tabpanel-${index}`,
  };
}

const Dashboard: React.FC = () => {
  const { user, loading: authLoading } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();
  const [value, setValue] = useState(0);
  const [companyId, setCompanyId] = useState<string>('');
  
  // State for dashboard overview data
  const [orderSummary, setOrderSummary] = useState<any>(null);
  const [productSummary, setProductSummary] = useState<any>(null);
  const [inventorySummary, setInventorySummary] = useState<any>(null);
  
  // State for tab-specific data
  const [products, setProducts] = useState<any[]>([]);
  const [orders, setOrders] = useState<any[]>([]);
  const [inventory, setInventory] = useState<any[]>([]);
  
  // UI state
  const [loading, setLoading] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);
  const [activeTabLoading, setActiveTabLoading] = useState<boolean>(false);
  const [activeTabError, setActiveTabError] = useState<string | null>(null);

  useEffect(() => {
    if (authLoading) {
      console.log('Dashboard: Auth is loading, companyId not yet set from context.');
      // Optionally, you could clear companyId here if strict reset is needed during auth loading
      // setCompanyId(''); 
      return;
    }

    // Auth process is complete
    if (user?.companyId) {
      setCompanyId(user.companyId);
      console.log('Dashboard: CompanyId set from AuthContext:', user.companyId);
    } else {
      setCompanyId(''); // Clear local companyId if not available from context
      console.log('Dashboard: CompanyId cleared (user or user.companyId missing after auth). User:', user);
      if (user && !user.companyId) {
        console.warn('Dashboard: User object from AuthContext is present but companyId is missing.');
      }
    }
  }, [user, authLoading]);

  // Main data fetching logic based on active tab and companyId availability
  useEffect(() => {
    if (authLoading) {
      console.log('Dashboard: Auth is loading, deferring data fetch for tab', value);
      if (value === 0) setLoading(true); else setActiveTabLoading(true); // Show loading indicators
      return;
    }

    // Auth is loaded. Proceed with fetching.
    // Clear previous errors for the current scope (overview or active tab)
    if (value === 0) setError(null); else setActiveTabError(null);

    console.log(`Dashboard: Fetching data for tab ${value}, companyId: "${companyId}"`);

    switch (value) {
      case 0: // Overview
        fetchDashboardData();
        break;
      case 1: // User Management
        // Components handle their own data.
        // EmployeeInvitationGenerator is passed companyId and should react to its changes.
        // Ensure loading states are reset if they were set by other tabs.
        setActiveTabLoading(false);
        break;
      case 2: // Products
        fetchProducts();
        break;
      case 3: // Orders
        fetchOrders();
        break;
      case 4: // Inventory Tab
        if (companyId) {
          fetchInventory(); // This function explicitly uses companyId
        } else {
          // companyId is not available, and auth is complete.
          console.log('Dashboard: Company ID not available for Inventory tab (Tab 4).');
          setInventory([]); // Clear data
          setActiveTabError('Company ID is not available. Cannot load inventory.');
          setActiveTabLoading(false); // Ensure loading is false
        }
        break;
      default:
        // For any other tabs, ensure loading states are reset.
        if (value !==0) setActiveTabLoading(false); else setLoading(false);
        break;
    }
  }, [value, companyId, authLoading]); // Key dependencies: tab, companyId, and auth status

  const fetchDashboardData = async () => {
    setLoading(true);
    setError(null);
    
    try {
      // Fetch all summary data in parallel
      // Add individual error handlers for each request to handle gracefully
      const [orders, products, inventory] = await Promise.all([
        dashboardService.getOrderSummary()
          .catch(err => {
            console.error('Failed to load order summary:', err);
            return null;
          }),
        dashboardService.getProductSummary()
          .catch(err => {
            console.error('Failed to load product summary:', err);
            return null;
          }),
        dashboardService.getInventorySummary()  // Pass the companyId from component state
          .catch(err => {
            console.error('Failed to load inventory summary:', err);
            return null;
          })
      ]);
      
      // Set data for each summary individually
      setOrderSummary(orders);
      setProductSummary(products);
      setInventorySummary(inventory);

      // Show error if all requests failed
      if (!orders && !products && !inventory) {
        setError('Unable to load dashboard data. Please try refreshing the page or contact support if the issue persists.');
      }
    } catch (err: any) {
      console.error('Error fetching dashboard data:', err);
      setError(err.message || 'Failed to load dashboard data');
    } finally {
      setLoading(false);
    }
  };

  const fetchProducts = async () => {
    setActiveTabLoading(true);
    setActiveTabError(null);
    
    try {
      const response = await productService.getProducts({ limit: 5 });
      // Using PaginationResponse format
      setProducts(response.products || []);
    } catch (err: any) {
      console.error('Error fetching products:', err);
      setActiveTabError(err.message || 'Failed to load products');
    } finally {
      setActiveTabLoading(false);
    }
  };

  const fetchOrders = async () => {
    setActiveTabLoading(true);
    setActiveTabError(null);
    
    try {
      const response = await orderService.getOrders({ limit: 5 });
      
      // We know the response is OrdersListResponse with orders array
      if (response && response.orders) {
        setOrders(response.orders);
      } else if (Array.isArray(response)) {
        // Fallback in case of direct array response
        setOrders(response);
      } else {
        console.warn('Unexpected order response format:', response);
        setOrders([]);
      }
    } catch (err: any) {
      console.error('Error fetching orders:', err);
      setActiveTabError(err.message || 'Failed to load orders');
    } finally {
      setActiveTabLoading(false);
    }
  };

  const fetchInventory = async () => {
    setActiveTabLoading(true);
    setActiveTabError(null);
    
    if (!companyId) {
      console.error('fetchInventory: Company ID is required to fetch inventory');
      setActiveTabError('Company ID is required to fetch inventory');
      setActiveTabLoading(false);
      return;
    }

    try {
      // Use getInventory instead of getInventoryItems which doesn't exist
      const response = await inventoryService.getInventory({ companyId, limit: 5 });
      setInventory(response.data || []);
    } catch (err: any) {
      console.error('Error fetching inventory:', err);
      setActiveTabError(err.message || 'Failed to load inventory');
    } finally {
      setActiveTabLoading(false);
    }
  };

  useEffect(() => {
    console.log('Dashboard: Current local companyId state updated:', companyId);
  }, [companyId]); // Debug log for local companyId state changes

  const handleChange = (event: React.SyntheticEvent, newValue: number) => {
    setValue(newValue);
  };

  // Handle navigation with query params instead of routes
  const handleNavigation = (path: string) => {
    // Extract the base path and action
    const [basePath, action] = path.split('/').filter(Boolean);
    
    if (action === 'create' || action === 'transfer' || action === 'adjust') {
      // Instead of navigating to a new route, add query param to existing route
      navigate(`/${basePath}?action=${action}`);
    } else if (path.includes('/')) {
      // For item details, extract the ID and use query param
      const segments = path.split('/');
      const id = segments[segments.length - 1];
      if (segments.length > 2 && segments[2] === 'adjust') {
        navigate(`/${basePath}?id=${id}&action=adjust`);
      } else {
        navigate(`/${basePath}?id=${id}`);
      }
    } else {
      // Regular navigation for main sections
      navigate(path);
    }
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      {/* Sticky Dashboard Header */}
      <Box position="sticky" top={0} zIndex={10} bgcolor="background.paper" boxShadow={1} borderRadius={2} px={{ xs: 2, md: 4 }} py={2} mb={3}>
        <Typography component="h1" variant="h4" color="primary.main" fontWeight={700}>
          Tubex Dashboard
        </Typography>
        <Typography variant="body1" color="text.secondary">
          Manage your inventory, products, orders, and employees from this central dashboard.
        </Typography>
      </Box>

      <Box sx={{ display: 'flex', flexDirection: 'column', gap: 4 }}>
        {/* Dashboard Tabs */}
        <Paper sx={{ width: '100%', borderRadius: 3, boxShadow: 2, p: { xs: 1, md: 2 } }}>
          <Box sx={{ borderBottom: 1, borderColor: 'divider', bgcolor: 'grey.50', borderRadius: 2 }}>
            <Tabs 
              value={value} 
              onChange={handleChange} 
              aria-label="dashboard tabs"
              variant="scrollable"
              scrollButtons="auto"
            >
              <Tab label="Overview" {...a11yProps(0)} />
              <Tab label="User Management" {...a11yProps(1)} />
              <Tab label="Products" {...a11yProps(2)} />
              <Tab label="Orders" {...a11yProps(3)} />
              <Tab label="Inventory" {...a11yProps(4)} />
            </Tabs>
          </Box>

          {/* Overview Tab */}
          <TabPanel value={value} index={0}>
            {error && (
              <Alert severity="error" sx={{ mb: 2 }}>
                {error}
                <Button 
                  size="small" 
                  sx={{ ml: 2 }} 
                  onClick={() => fetchDashboardData()}
                >
                  Retry
                </Button>
              </Alert>
            )}
            {loading ? (
              <Box sx={{ display: 'flex', justifyContent: 'center', p: 4 }}>
                <CircularProgress />
              </Box>
            ) : (
              <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 3, justifyContent: { xs: 'center', md: 'flex-start' } }}>
                {/* Orders Summary */}
                <Box sx={{ flex: '1 1 320px', minWidth: 260 }}>
                  <Paper sx={{ p: 3, borderRadius: 3, bgcolor: 'grey.100', height: 240, display: 'flex', flexDirection: 'column', justifyContent: 'center', boxShadow: 0 }}>
                    <Typography variant="subtitle1" fontWeight={700} color="primary.main" gutterBottom>
                      Recent Orders
                    </Typography>
                    {orderSummary ? (
                      <Box>
                        <Typography variant="body2">
                          <strong>Total Orders:</strong> {orderSummary.totalOrders || 0}
                        </Typography>
                        <Typography variant="body2">
                          <strong>Pending Orders:</strong> {orderSummary.pendingOrders || 0}
                        </Typography>
                        {orderSummary.recentOrders?.length > 0 ? (
                          <Typography variant="body2" sx={{ mt: 2 }}>
                            <strong>Latest:</strong> {orderSummary.recentOrders[0]?.customer || 'N/A'} - ${orderSummary.recentOrders[0]?.total || 0}
                          </Typography>
                        ) : (
                          <Typography variant="body2" sx={{ mt: 2 }}>No recent orders</Typography>
                        )}
                      </Box>
                    ) : (
                      <Typography variant="body2" color="text.secondary">No order data available</Typography>
                    )}
                  </Paper>
                </Box>
                {/* Products Summary */}
                <Box sx={{ flex: '1 1 320px', minWidth: 260 }}>
                  <Paper sx={{ p: 3, borderRadius: 3, bgcolor: 'grey.100', height: 240, display: 'flex', flexDirection: 'column', justifyContent: 'center', boxShadow: 0 }}>
                    <Typography variant="subtitle1" fontWeight={700} color="primary.main" gutterBottom>
                      Products
                    </Typography>
                    {productSummary ? (
                      <Box>
                        <Typography variant="body2">
                          <strong>Total Products:</strong> {productSummary.totalProducts || 0}
                        </Typography>
                        <Typography variant="body2" sx={{ mt: 2 }}>
                          <strong>Featured Products:</strong>
                        </Typography>
                        {productSummary.featuredProducts?.length > 0 ? (
                          <Box>
                            {productSummary.featuredProducts.slice(0, 2).map((product: any) => (
                              <Typography key={product.id} variant="body2">
                                {product.name} - ${product.price}
                              </Typography>
                            ))}
                          </Box>
                        ) : (
                          <Typography variant="body2">No featured products</Typography>
                        )}
                      </Box>
                    ) : (
                      <Typography variant="body2" color="text.secondary">No product data available</Typography>
                    )}
                  </Paper>
                </Box>
                {/* Inventory Summary */}
                <Box sx={{ flex: '1 1 320px', minWidth: 260 }}>
                  <Paper sx={{ p: 3, borderRadius: 3, bgcolor: 'grey.100', height: 240, display: 'flex', flexDirection: 'column', justifyContent: 'center', boxShadow: 0 }}>
                    <Typography variant="subtitle1" fontWeight={700} color="primary.main" gutterBottom>
                      Inventory Status
                    </Typography>
                    {inventorySummary ? (
                      <Box>
                        <Typography variant="body2">
                          <strong>Total Items:</strong> {inventorySummary.totalItems || 0}
                        </Typography>
                        <Typography variant="body2">
                          <strong>Low Stock Items:</strong> {inventorySummary.lowStockItems || 0}
                        </Typography>
                        <Typography variant="body2">
                          <strong>Warehouse Utilization:</strong> {inventorySummary.warehouseUtilization || 0}%
                        </Typography>
                      </Box>
                    ) : (
                      <Typography variant="body2" color="text.secondary">No inventory data available</Typography>
                    )}
                  </Paper>
                </Box>
              </Box>
            )}
          </TabPanel>
          
          {/* User Management Tab */}
          <TabPanel value={value} index={1}>
            <Box sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
              <Box>
                <EmployeeInvitationGenerator companyId={companyId} />
              </Box>
              <Box>
                <PendingEmployeesList onEmployeeStatusChange={() => {}} />
              </Box>
            </Box>
          </TabPanel>

          {/* Products Tab */}
          <TabPanel value={value} index={2}>
            <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
              <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 1 }}>
                <Typography variant="h6" fontWeight={700} color="primary.main">Product Management</Typography>
                <Button 
                  variant="contained" 
                  color="primary"
                  onClick={() => handleNavigation('/products/create')}
                >
                  Add New Product
                </Button>
              </Box>
              {activeTabError && (
                <Alert severity="error" sx={{ mb: 2 }}>
                  {activeTabError}
                  <Button 
                    size="small" 
                    sx={{ ml: 2 }} 
                    onClick={() => fetchProducts()}
                  >
                    Retry
                  </Button>
                </Alert>
              )}
              {activeTabLoading ? (
                <Box sx={{ display: 'flex', justifyContent: 'center', p: 4 }}>
                  <CircularProgress />
                </Box>
              ) : (
                <>
                  {products.length > 0 ? (
                    <Paper sx={{ width: '100%', overflow: 'auto', borderRadius: 2, boxShadow: 0 }}>
                      <Box sx={{ p: 2 }}>
                        <Typography variant="subtitle1" fontWeight={700} color="primary.main" gutterBottom>
                          Recent Products
                        </Typography>
                        <Box component="table" sx={{ width: '100%', borderCollapse: 'collapse' }}>
                          <Box component="thead" sx={{ bgcolor: 'grey.50' }}>
                            <Box component="tr" sx={{ borderBottom: '1px solid #e0e0e0' }}>
                              <Box component="th" sx={{ p: 1, textAlign: 'left' }}>Name</Box>
                              <Box component="th" sx={{ p: 1, textAlign: 'left' }}>Price</Box>
                              <Box component="th" sx={{ p: 1, textAlign: 'left' }}>Status</Box>
                              <Box component="th" sx={{ p: 1, textAlign: 'left' }}>Actions</Box>
                            </Box>
                          </Box>
                          <Box component="tbody">
                            {products.map((product) => (
                              <Box component="tr" key={product.id} sx={{ borderBottom: '1px solid #e0e0e0', transition: 'background 0.2s', '&:hover': { bgcolor: 'grey.100' } }}>
                                <Box component="td" sx={{ p: 1 }}>{product.name}</Box>
                                <Box component="td" sx={{ p: 1 }}>${product.base_price}</Box>
                                <Box component="td" sx={{ p: 1 }}>{product.status}</Box>
                                <Box component="td" sx={{ p: 1 }}>
                                  <Button 
                                    size="small" 
                                    variant="outlined"
                                    onClick={() => handleNavigation(`/products/${product.id}`)}
                                  >
                                    View
                                  </Button>
                                </Box>
                              </Box>
                            ))}
                          </Box>
                        </Box>
                      </Box>
                      <Box sx={{ p: 2, display: 'flex', justifyContent: 'flex-end' }}>
                        <Button 
                          onClick={() => handleNavigation('/products')}
                          color="primary"
                        >
                          View All Products
                        </Button>
                      </Box>
                    </Paper>
                  ) : (
                    <Paper sx={{ p: 3, textAlign: 'center', borderRadius: 2, boxShadow: 0 }}>
                      <Typography>No products found</Typography>
                      <Button 
                        variant="outlined" 
                        color="primary" 
                        sx={{ mt: 2 }}
                        onClick={() => handleNavigation('/products/create')}
                      >
                        Add Your First Product
                      </Button>
                    </Paper>
                  )}
                </>
              )}
            </Box>
          </TabPanel>

          {/* Orders Tab */}
          <TabPanel value={value} index={3}>
            <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
              <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 1 }}>
                <Typography variant="h6" fontWeight={700} color="primary.main">Order Management</Typography>
                <Button 
                  variant="contained" 
                  color="primary"
                  onClick={() => handleNavigation('/orders/create')}
                >
                  Create New Order
                </Button>
              </Box>
              {activeTabError && (
                <Alert severity="error" sx={{ mb: 2 }}>
                  {activeTabError}
                  <Button 
                    size="small" 
                    sx={{ ml: 2 }} 
                    onClick={() => fetchOrders()}
                  >
                    Retry
                  </Button>
                </Alert>
              )}
              {activeTabLoading ? (
                <Box sx={{ display: 'flex', justifyContent: 'center', p: 4 }}>
                  <CircularProgress />
                </Box>
              ) : (
                <>
                  {orders.length > 0 ? (
                    <Paper sx={{ width: '100%', overflow: 'auto', borderRadius: 2, boxShadow: 0 }}>
                      <Box sx={{ p: 2 }}>
                        <Typography variant="subtitle1" fontWeight={700} color="primary.main" gutterBottom>
                          Recent Orders
                        </Typography>
                        <Box component="table" sx={{ width: '100%', borderCollapse: 'collapse' }}>
                          <Box component="thead" sx={{ bgcolor: 'grey.50' }}>
                            <Box component="tr" sx={{ borderBottom: '1px solid #e0e0e0' }}>
                              <Box component="th" sx={{ p: 1, textAlign: 'left' }}>Order ID</Box>
                              <Box component="th" sx={{ p: 1, textAlign: 'left' }}>Date</Box>
                              <Box component="th" sx={{ p: 1, textAlign: 'left' }}>Amount</Box>
                              <Box component="th" sx={{ p: 1, textAlign: 'left' }}>Status</Box>
                              <Box component="th" sx={{ p: 1, textAlign: 'left' }}>Actions</Box>
                            </Box>
                          </Box>
                          <Box component="tbody">
                            {orders.map((order) => (
                              <Box component="tr" key={order.id} sx={{ borderBottom: '1px solid #e0e0e0', transition: 'background 0.2s', '&:hover': { bgcolor: 'grey.100' } }}>
                                <Box component="td" sx={{ p: 1 }}>#{order.id.slice(-6)}</Box>
                                <Box component="td" sx={{ p: 1 }}>{new Date(order.createdAt).toLocaleDateString()}</Box>
                                <Box component="td" sx={{ p: 1 }}>${order.totalAmount}</Box>
                                <Box component="td" sx={{ p: 1 }}>
                                  <Box sx={{
                                    display: 'inline-block',
                                    px: 1,
                                    py: 0.5,
                                    borderRadius: 1,
                                    fontSize: '0.75rem',
                                    bgcolor: 
                                      order.status === 'delivered' ? 'success.light' : 
                                      order.status === 'cancelled' ? 'error.light' :
                                      order.status === 'processing' ? 'info.light' : 'warning.light'
                                  }}>
                                    {order.status}
                                  </Box>
                                </Box>
                                <Box component="td" sx={{ p: 1 }}>
                                  <Button 
                                    size="small" 
                                    variant="outlined"
                                    onClick={() => handleNavigation(`/orders/${order.id}`)}
                                  >
                                    View
                                  </Button>
                                </Box>
                              </Box>
                            ))}
                          </Box>
                        </Box>
                      </Box>
                      <Box sx={{ p: 2, display: 'flex', justifyContent: 'flex-end' }}>
                        <Button 
                          onClick={() => handleNavigation('/orders')}
                          color="primary"
                        >
                          View All Orders
                        </Button>
                      </Box>
                    </Paper>
                  ) : (
                    <Paper sx={{ p: 3, textAlign: 'center', borderRadius: 2, boxShadow: 0 }}>
                      <Typography>No orders found</Typography>
                      <Button 
                        variant="outlined" 
                        color="primary" 
                        sx={{ mt: 2 }}
                        onClick={() => handleNavigation('/orders/create')}
                      >
                        Create Your First Order
                      </Button>
                    </Paper>
                  )}
                </>
              )}
            </Box>
          </TabPanel>

          {/* Inventory Tab */}
          <TabPanel value={value} index={4}>
            <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
              <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 1 }}>
                <Typography variant="h6" fontWeight={700} color="primary.main">Inventory Management</Typography>
                <Box>
                  <Button 
                    variant="contained" 
                    color="primary"
                    onClick={() => handleNavigation('/inventory/create')}
                    sx={{ mr: 1 }}
                  >
                    Add Inventory Item
                  </Button>
                  <Button
                    variant="outlined"
                    onClick={() => handleNavigation('/inventory/transfer')}
                  >
                    Transfer Stock
                  </Button>
                </Box>
              </Box>
              {activeTabError && (
                <Alert severity="error" sx={{ mb: 2 }}>
                  {activeTabError}
                  <Button 
                    size="small" 
                    sx={{ ml: 2 }} 
                    onClick={() => fetchInventory()}
                  >
                    Retry
                  </Button>
                </Alert>
              )}
              {activeTabLoading ? (
                <Box sx={{ display: 'flex', justifyContent: 'center', p: 4 }}>
                  <CircularProgress />
                </Box>
              ) : (
                <>
                  {inventory.length > 0 ? (
                    <Paper sx={{ width: '100%', overflow: 'auto', borderRadius: 2, boxShadow: 0 }}>
                      <Box sx={{ p: 2 }}>
                        <Typography variant="subtitle1" fontWeight={700} color="primary.main" gutterBottom>
                          Current Inventory
                        </Typography>
                        <Box component="table" sx={{ width: '100%', borderCollapse: 'collapse' }}>
                          <Box component="thead" sx={{ bgcolor: 'grey.50' }}>
                            <Box component="tr" sx={{ borderBottom: '1px solid #e0e0e0' }}>
                              <Box component="th" sx={{ p: 1, textAlign: 'left' }}>Product</Box>
                              <Box component="th" sx={{ p: 1, textAlign: 'left' }}>Warehouse</Box>
                              <Box component="th" sx={{ p: 1, textAlign: 'left' }}>Quantity</Box>
                              <Box component="th" sx={{ p: 1, textAlign: 'left' }}>Status</Box>
                              <Box component="th" sx={{ p: 1, textAlign: 'left' }}>Actions</Box>
                            </Box>
                          </Box>
                          <Box component="tbody">
                            {inventory.map((item) => (
                              <Box component="tr" key={item.id} sx={{ borderBottom: '1px solid #e0e0e0', transition: 'background 0.2s', '&:hover': { bgcolor: 'grey.100' } }}>
                                <Box component="td" sx={{ p: 1 }}>{item.product?.name || 'Unknown Product'}</Box>
                                <Box component="td" sx={{ p: 1 }}>{item.warehouse?.name || 'Unknown Warehouse'}</Box>
                                <Box component="td" sx={{ p: 1 }}>{item.quantity} {item.unit}</Box>
                                <Box component="td" sx={{ p: 1 }}>
                                  <Box sx={{
                                    display: 'inline-block',
                                    px: 1,
                                    py: 0.5,
                                    borderRadius: 1,
                                    fontSize: '0.75rem',
                                    bgcolor: 
                                      (item.stockStatus?.isLow) ? 'error.light' : 
                                      (item.quantity === 0) ? 'warning.light' : 'success.light'
                                  }}>
                                    {item.stockStatus?.isLow ? 'Low Stock' : 
                                     item.quantity === 0 ? 'Out of Stock' : 'In Stock'}
                                  </Box>
                                </Box>
                                <Box component="td" sx={{ p: 1 }}>
                                  <Button 
                                    size="small" 
                                    variant="outlined"
                                    onClick={() => handleNavigation(`/inventory/${item.id}`)}
                                    sx={{ mr: 1 }}
                                  >
                                    View
                                  </Button>
                                  <Button 
                                    size="small" 
                                    variant="outlined"
                                    color="secondary"
                                    onClick={() => handleNavigation(`/inventory/${item.id}/adjust`)}
                                  >
                                    Adjust
                                  </Button>
                                </Box>
                              </Box>
                            ))}
                          </Box>
                        </Box>
                      </Box>
                      <Box sx={{ p: 2, display: 'flex', justifyContent: 'flex-end' }}>
                        <Button 
                          onClick={() => handleNavigation('/inventory')}
                          color="primary"
                        >
                          View All Inventory
                        </Button>
                      </Box>
                    </Paper>
                  ) : (
                    <Paper sx={{ p: 3, textAlign: 'center', borderRadius: 2, boxShadow: 0 }}>
                      <Typography>No inventory items found</Typography>
                      <Button 
                        variant="outlined" 
                        color="primary" 
                        sx={{ mt: 2 }}
                        onClick={() => handleNavigation('/inventory/create')}
                      >
                        Add Your First Inventory Item
                      </Button>
                    </Paper>
                  )}
                </>
              )}
            </Box>
          </TabPanel>
        </Paper>
      </Box>
    </Container>
  );
};

export default Dashboard;

import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import { 
  Box, 
  Container, 
  Paper, 
  Typography, 
  Button,
  Tabs,
  Tab,
  Alert
} from '@mui/material';
import  InventoryList  from '../components/whitelabel/inventory/InventoryList';
import  InventoryTransferModal  from '../components/inventory/InventoryTransferModal';
import  InventoryAuditLog  from '../components/inventory/InventoryAuditLog';
import { inventoryService } from '../services/api';
import { useAuth } from '../contexts/AuthContext';

interface TabPanelProps {
  children?: React.ReactNode;
  index: number;
  value: number;
}

function TabPanel(props: TabPanelProps) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`inventory-tabpanel-${index}`}
      aria-labelledby={`inventory-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box sx={{ p: 3 }}>
          {children}
        </Box>
      )}
    </div>
  );
}

function a11yProps(index: number) {
  return {
    id: `inventory-tab-${index}`,
    'aria-controls': `inventory-tabpanel-${index}`,
  };
}

const InventoryManagement: React.FC = () => {
  const [tabValue, setTabValue] = useState(0);
  const [transferModalOpen, setTransferModalOpen] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState<{ id: string; name: string } | null>(null);
  const [selectedInventoryItem, setSelectedInventoryItem] = useState<string | null>(null);
  const [inventory, setInventory] = useState<any[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [viewMode, setViewMode] = useState<'list' | 'create' | 'view' | 'adjust' | 'transfer'>('list');
  const [selectedItem, setSelectedItem] = useState<any | null>(null);
  const location = useLocation();
  // Get company ID from auth context
  const { user, loading: authLoading } = useAuth();
  const [companyId, setCompanyId] = useState<string>('');
  const [isInitialized, setIsInitialized] = useState(false);
  
  const [lowStockItems, setLowStockItems] = useState<any[]>([]);
  const [expiringItems, setExpiringItems] = useState<any[]>([]);
  const [alertsLoading, setAlertsLoading] = useState(false);
  const [alertsError, setAlertsError] = useState<string | null>(null);

  // Set companyId from auth context when user data is available
  useEffect(() => {
    if (user && user.companyId) {
      console.log('Setting company ID:', user.companyId);
      setCompanyId(user.companyId);
      setIsInitialized(true);
    } else if (!authLoading) {
      console.error('User or companyId not available');
      setIsInitialized(true);
    }
  }, [user, authLoading]);
  useEffect(() => {
    if (companyId && isInitialized) {
      fetchAlerts();
    }
  }, [companyId, isInitialized]);
  const fetchAlerts = async () => {
    if (!companyId) {
      console.error('Cannot fetch alerts: Company ID is not available');
      setAlertsError('Company ID is required to fetch inventory alerts');
      return;
    }

    setAlertsLoading(true);
    setAlertsError(null);

    try {
      // Fetch low stock alerts
      const lowStockResponse = await inventoryService.getLowStockItems(companyId);
      setLowStockItems(lowStockResponse.data || []);

      // Fetch expiring batches (30 days threshold)
      const expiringResponse = await inventoryService.getExpiringBatches(companyId, 30);
      setExpiringItems(expiringResponse.data || []);
    } catch (err: any) {
      console.error('Error fetching inventory alerts:', err);
      setAlertsError(err.message || 'Failed to fetch inventory alerts');
    } finally {
      setAlertsLoading(false);
    }
  };

  const handleTabChange = (event: React.SyntheticEvent, newValue: number) => {
    setTabValue(newValue);
  };

  const handleOpenTransferModal = (product: { id: string; name: string }) => {
    setSelectedProduct(product);
    setTransferModalOpen(true);
  };

  const handleCloseTransferModal = () => {
    setTransferModalOpen(false);
    setSelectedProduct(null);
  };

  const handleInventorySelect = (inventoryId: string) => {
    setSelectedInventoryItem(inventoryId);
    setTabValue(3); // Switch to Audit Log tab
  };

  const handleTransferComplete = () => {
    // Refresh inventory list after a transfer
    // If you have a refresh function in your InventoryList component, call it here
    fetchAlerts(); // Refresh alerts as well
  };

  // Parse query parameters and set up the correct view
  useEffect(() => {
    const queryParams = new URLSearchParams(location.search);
    const action = queryParams.get('action');
    const id = queryParams.get('id');
    
    if (action === 'create') {
      setViewMode('create');
      setSelectedItem(null);
    } else if (action === 'transfer') {
      setViewMode('transfer');
      setSelectedItem(null);
    } else if (action === 'adjust' && id) {
      setViewMode('adjust');
      fetchInventoryItem(id);
    } else if (id) {
      setViewMode('view');
      fetchInventoryItem(id);
    } else {
      // Reset to list view
      setViewMode('list');
      setSelectedItem(null);
    }
  }, [location.search]);

  const fetchInventoryItem = async (id: string) => {
    if (!companyId) return;
    
    setLoading(true);
    try {
      const item = await inventoryService.getInventoryItem(id);
      setSelectedItem(item);
    } catch (err: any) {
      console.error('Error fetching inventory item:', err);
      setError(err.message || 'Failed to load inventory item');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      {viewMode === 'list' && (
        <Box sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
          {/* Page Header */}
          <Box>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
              <Typography component="h1" variant="h4">
                Inventory Management
              </Typography>
            </Box>
          </Box>

          {/* Alerts Section */}
          <Box>
            <Paper sx={{ p: 2 }}>
              <Typography variant="h6" gutterBottom>
                Inventory Alerts
              </Typography>

              {alertsError && (
                <Alert severity="error" sx={{ mb: 2 }}>
                  {alertsError}
                </Alert>
              )}

              <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 2 }}>
                <Box sx={{ flex: '1 1 45%', minWidth: '250px' }}>
                  <Paper 
                    variant="outlined" 
                    sx={{ 
                      p: 2, 
                      bgcolor: 'warning.light', 
                      color: 'warning.contrastText',
                      height: '100%'
                    }}
                  >
                    <Typography variant="subtitle1" fontWeight="bold" gutterBottom>
                      Low Stock Items: {lowStockItems.length}
                    </Typography>
                    <Typography variant="body2">
                      {lowStockItems.length > 0 
                        ? 'Some items are running low on stock. Check inventory levels and consider restocking.' 
                        : 'All items have sufficient stock levels.'}
                    </Typography>
                  </Paper>
                </Box>
                <Box sx={{ flex: '1 1 45%', minWidth: '250px' }}>
                  <Paper 
                    variant="outlined" 
                    sx={{ 
                      p: 2, 
                      bgcolor: 'error.light', 
                      color: 'error.contrastText',
                      height: '100%'
                    }}
                  >
                    <Typography variant="subtitle1" fontWeight="bold" gutterBottom>
                      Expiring Batches: {expiringItems.length}
                    </Typography>
                    <Typography variant="body2">
                      {expiringItems.length > 0 
                        ? 'Some batches are expiring within 30 days. Review and take action.' 
                        : 'No batches expiring within the next 30 days.'}
                    </Typography>
                  </Paper>
                </Box>
              </Box>

              <Box sx={{ display: 'flex', justifyContent: 'flex-end', mt: 2 }}>
                <Button 
                  onClick={fetchAlerts} 
                  variant="outlined" 
                  size="small"
                  disabled={alertsLoading}
                >
                  Refresh Alerts
                </Button>
              </Box>
            </Paper>
          </Box>

          {/* Main Content Tabs */}
          <Box>
            <Paper sx={{ width: '100%' }}>
              <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
                <Tabs 
                  value={tabValue} 
                  onChange={handleTabChange} 
                  aria-label="inventory management tabs"
                  variant="scrollable"
                  scrollButtons="auto"
                >
                  <Tab label="Inventory List" {...a11yProps(0)} />
                  <Tab label="Low Stock" {...a11yProps(1)} />
                  <Tab label="Expiring Items" {...a11yProps(2)} />
                  {selectedInventoryItem && <Tab label="Audit Log" {...a11yProps(3)} />}
                </Tabs>
              </Box>            {/* Inventory List Tab */}
              <TabPanel value={tabValue} index={0}>
                {!isInitialized ? (
                  <Box sx={{ display: 'flex', justifyContent: 'center', p: 3 }}>
                    <Typography>Loading...</Typography>
                  </Box>
                ) : companyId ? (
                  <InventoryList 
                    companyId={companyId}
                    onTransferClick={handleOpenTransferModal}
                    onInventorySelect={handleInventorySelect}
                  />
                ) : (
                  <Alert severity="error" sx={{ mb: 2 }}>
                    Company ID not available. Please ensure you're logged in with a valid company account.
                  </Alert>
                )}
              </TabPanel>

              {/* Low Stock Tab */}
              <TabPanel value={tabValue} index={1}>
                {/* Add a dedicated LowStockItems component if needed */}
                <Typography variant="h6">Low Stock Items</Typography>
                {/* Display low stock items */}
              </TabPanel>

              {/* Expiring Items Tab */}
              <TabPanel value={tabValue} index={2}>
                {/* Add a dedicated ExpiringItems component if needed */}
                <Typography variant="h6">Expiring Batches</Typography>
                {/* Display expiring items */}
              </TabPanel>

              {/* Audit Log Tab - Conditionally rendered */}
              {selectedInventoryItem && (
                <TabPanel value={tabValue} index={3}>
                  <InventoryAuditLog 
                    inventoryId={selectedInventoryItem}
                    title="Inventory Transaction History"
                  />
                </TabPanel>
              )}
            </Paper>
          </Box>
        </Box>
      )}
      
      {viewMode === 'create' && (
        <Box>
          <Typography variant="h6">Create Inventory Item</Typography>
          {/* Render create form */}
          {/* ...existing code... */}
        </Box>
      )}
      
      {viewMode === 'view' && selectedItem && (
        <Box>
          <Typography variant="h6">Inventory Item Details</Typography>
          {/* Render inventory item details */}
          {/* ...existing code... */}
        </Box>
      )}
      
      {viewMode === 'adjust' && selectedItem && (
        <Box>
          <Typography variant="h6">Adjust Inventory</Typography>
          {/* Render adjustment form */}
          {/* ...existing code... */}
        </Box>
      )}
      
      {viewMode === 'transfer' && (
        <Box>
          <Typography variant="h6">Transfer Inventory</Typography>
          {/* Render transfer form */}
          {/* ...existing code... */}
        </Box>
      )}

      {/* Inventory Transfer Modal */}
      {selectedProduct && (
        <InventoryTransferModal
          open={transferModalOpen}
          onClose={handleCloseTransferModal}
          productId={selectedProduct.id}
          productName={selectedProduct.name}
          companyId={companyId}
          onTransferComplete={handleTransferComplete}
        />
      )}
    </Container>
  );
};

