export default LoginPage;

import React, { useState, useEffect } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import {
  Box,
  Container,
  Typography,
  Paper,
  Tabs,
  Tab,
  Button,
  TextField,
  CircularProgress,
  Alert,
  IconButton,
  Menu,
  MenuItem,
  Chip
} from '@mui/material';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';
import { LocalizationProvider, DatePicker } from '@mui/x-date-pickers';
import MoreVertIcon from '@mui/icons-material/MoreVert';
import FilterListIcon from '@mui/icons-material/FilterList';
import SearchIcon from '@mui/icons-material/Search';
import { orderService } from '../services/api';
import OrderDetails from '../components/whitelabel/orders/OrderDetails';
import CreateOrderForm from '../components/whitelabel/orders/CreateOrderForm';
import { format } from 'date-fns';
import { useAuth } from '../contexts/AuthContext';

interface TabPanelProps {
  children?: React.ReactNode;
  index: number;
  value: number;
}

function TabPanel(props: TabPanelProps) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`order-tabpanel-${index}`}
      aria-labelledby={`order-tab-${index}`}
      {...other}
    >
      {value === index && <Box sx={{ p: 3 }}>{children}</Box>}
    </div>
  );
}

function a11yProps(index: number) {
  return {
    id: `order-tab-${index}`,
    'aria-controls': `order-tabpanel-${index}`,
  };
}

const statusColors: Record<string, "default" | "primary" | "secondary" | "error" | "info" | "success" | "warning"> = {
  'pending': 'warning',
  'processing': 'info',
  'shipped': 'primary',
  'delivered': 'success',
  'cancelled': 'error',
  'returned': 'secondary',
  'default': 'default'
};

const OrderManagement: React.FC = () => {
  const [tabValue, setTabValue] = useState(0);
  const [orders, setOrders] = useState<any[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [selectedOrder, setSelectedOrder] = useState<any | null>(null);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [filterStatus, setFilterStatus] = useState('all');
  const [dateRange, setDateRange] = useState<{ start: Date | null; end: Date | null }>({
    start: null,
    end: null,
  });
  
  // Get company ID from auth context
  const { user } = useAuth();
  const [companyId, setCompanyId] = useState<string>('');
  
  const [anchorEl, setAnchorEl] = useState<null | HTMLElement>(null);
  const [actionOrderId, setActionOrderId] = useState<string | null>(null);

  const location = useLocation();
  const params = useParams();

  // Set companyId from auth context when user data is available
  useEffect(() => {
    if (user && user.companyId) {
      setCompanyId(user.companyId);
    }
  }, [user]);

  // Fetch orders when component mounts or filters change
  useEffect(() => {
    if (companyId) {
      fetchOrders();
    }
  }, [filterStatus, dateRange, companyId]);

  // Parse query parameters and set up the correct view
  useEffect(() => {
    const queryParams = new URLSearchParams(location.search);
    const action = queryParams.get('action');
    const id = queryParams.get('id');
    
    if (action === 'create') {
      setSelectedOrder(null);
      setShowCreateForm(true);
    } else if (id) {
      // Fetch and show specific order
      const fetchOrderDetails = async () => {
        setLoading(true);
        try {
          const order = await orderService.getOrderById(id);
          setSelectedOrder(order);
          setShowCreateForm(false);
        } catch (err: any) {
          console.error('Error fetching order details:', err);
          setError(err.message || 'Failed to load order details');
        } finally {
          setLoading(false);
        }
      };
      fetchOrderDetails();
    } else {
      // Reset to list view
      setSelectedOrder(null);
      setShowCreateForm(false);
    }
  }, [location.search]);

  const fetchOrders = async () => {
    setLoading(true);
    setError(null);

    try {
      const params: any = { companyId };
      if (filterStatus !== 'all') {
        params.status = filterStatus;
      }
      if (dateRange.start) {
        params.startDate = dateRange.start.toISOString();
      }
      if (dateRange.end) {
        params.endDate = dateRange.end.toISOString();
      }
      if (searchQuery) {
        params.search = searchQuery;
      }

      const response = await orderService.getOrders(params);
      setOrders(response.orders || []); // Adjusted to use 'orders' instead of 'data'
    } catch (err: any) {
      console.error('Error fetching orders:', err);
      setError(err.message || 'Failed to fetch orders');
    } finally {
      setLoading(false);
    }
  };

  const handleTabChange = (event: React.SyntheticEvent, newValue: number) => {
    setTabValue(newValue);
  };

  const handleViewOrder = (order: any) => {
    setSelectedOrder(order);
    setShowCreateForm(false);
  };

  const handleCreateOrder = () => {
    setSelectedOrder(null);
    setShowCreateForm(true);
  };

  const handleBackToList = () => {
    setSelectedOrder(null);
    setShowCreateForm(false);
  };

  const handleSearch = () => {
    fetchOrders();
  };

  const handleActionClick = (event: React.MouseEvent<HTMLElement>, orderId: string) => {
    setAnchorEl(event.currentTarget);
    setActionOrderId(orderId);
  };

  const handleActionClose = () => {
    setAnchorEl(null);
    setActionOrderId(null);
  };

  const handleStatusChange = async (status: 'confirmed' | 'processing' | 'shipped' | 'delivered' | 'cancelled') => {
    if (!actionOrderId) return;

    setLoading(true);
    setError(null);

    try {
      await orderService.updateOrder(actionOrderId, { status });
      fetchOrders();
    } catch (err: any) {
      console.error('Error updating order status:', err);
      setError(err.message || 'Failed to update order status');
    } finally {
      setLoading(false);
    }

    handleActionClose();
  };

  const handleOrderSave = () => {
    fetchOrders();
    handleBackToList();
  };

  const formatDate = (dateString: string) => {
    try {
      return format(new Date(dateString), 'MMM dd, yyyy');
    } catch (e) {
      return dateString;
    }
  };

  const formatCurrency = (value: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
    }).format(value);
  };

  const renderOrderList = () => (
    <Box>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography variant="h4" component="h1">
          Orders
        </Typography>
        <Button
          variant="contained"
          color="primary"
          onClick={handleCreateOrder}
        >
          Create New Order
        </Button>
      </Box>

      {/* Search and Filter */}
      <Paper sx={{ p: 2, mb: 3 }}>
        <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 2, alignItems: 'center' }}>
          <Box sx={{ display: 'flex', flex: '1 1 300px', gap: 1 }}>
            <TextField
              placeholder="Search by order ID or customer..."
              variant="outlined"
              size="small"
              fullWidth
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <Button
              variant="contained"
              color="primary"
              onClick={handleSearch}
              startIcon={<SearchIcon />}
            >
              Search
            </Button>
          </Box>

          <Box sx={{ display: 'flex', gap: 2, flexWrap: 'wrap', alignItems: 'center' }}>
            <TextField
              select
              label="Status"
              value={filterStatus}
              onChange={(e) => setFilterStatus(e.target.value)}
              size="small"
              sx={{ minWidth: 120 }}
            >
              <MenuItem value="all">All Status</MenuItem>
              <MenuItem value="pending">Pending</MenuItem>
              <MenuItem value="processing">Processing</MenuItem>
              <MenuItem value="shipped">Shipped</MenuItem>
              <MenuItem value="delivered">Delivered</MenuItem>
              <MenuItem value="cancelled">Cancelled</MenuItem>
              <MenuItem value="returned">Returned</MenuItem>
            </TextField>

            <LocalizationProvider dateAdapter={AdapterDateFns}>
              <DatePicker
                label="From"
                value={dateRange.start}
                onChange={(date) => setDateRange({ ...dateRange, start: date })}
                slotProps={{ textField: { size: 'small' } }}
              />
              <DatePicker
                label="To"
                value={dateRange.end}
                onChange={(date) => setDateRange({ ...dateRange, end: date })}
                slotProps={{ textField: { size: 'small' } }}
              />
            </LocalizationProvider>

            <Button
              variant="outlined"
              startIcon={<FilterListIcon />}
              onClick={fetchOrders}
            >
              Apply Filters
            </Button>
          </Box>
        </Box>
      </Paper>

      {/* Orders Table */}
      {loading ? (
        <Box sx={{ display: 'flex', justifyContent: 'center', p: 3 }}>
          <CircularProgress />
        </Box>
      ) : error ? (
        <Alert severity="error" sx={{ mb: 3 }}>
          {error}
        </Alert>
      ) : (
        <Box>
          {orders.length > 0 ? (
            orders.map((order) => (
              <Paper
                key={order.id}
                sx={{
                  p: 2,
                  mb: 2,
                  cursor: 'pointer',
                  '&:hover': { bgcolor: 'action.hover' }
                }}
                onClick={() => handleViewOrder(order)}
              >
                <Box sx={{ display: 'flex', flexDirection: { xs: 'column', sm: 'row' }, alignItems: { sm: 'center' }, justifyContent: 'space-between' }}>
                  <Box>
                    <Box sx={{ display: 'flex', gap: 1, alignItems: 'center', mb: 1 }}>
                      <Typography variant="subtitle1" fontWeight="bold">
                        #{order.order_number}
                      </Typography>
                      <Chip
                        size="small"
                        label={order.status}
                        color={statusColors[order.status] || statusColors.default}
                      />
                    </Box>
                    <Typography variant="body2" color="text.secondary">
                      {order.customer_name}  {formatDate(order.created_at)}
                    </Typography>
                  </Box>

                  <Box sx={{ display: 'flex', gap: 2, alignItems: 'center', mt: { xs: 1, sm: 0 } }}>
                    <Typography variant="subtitle1" fontWeight="bold">
                      {formatCurrency(order.total_amount)}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      {order.items_count} items
                    </Typography>
                    <IconButton
                      onClick={(e) => {
                        e.stopPropagation();
                        handleActionClick(e, order.id);
                      }}
                    >
                      <MoreVertIcon />
                    </IconButton>
                  </Box>
                </Box>
              </Paper>
            ))
          ) : (
            <Paper sx={{ p: 3, textAlign: 'center' }}>
              <Typography variant="body1" color="text.secondary">
                No orders found. Try a different filter or create a new order.
              </Typography>
              <Button
                variant="outlined"
                color="primary"
                sx={{ mt: 2 }}
                onClick={handleCreateOrder}
              >
                Create New Order
              </Button>
            </Paper>
          )}
        </Box>
      )}

      {/* Action Menu */}
      <Menu
        anchorEl={anchorEl}
        open={Boolean(anchorEl)}
        onClose={handleActionClose}
      >
        <MenuItem onClick={() => handleStatusChange('processing')}>Mark as Processing</MenuItem>
        <MenuItem onClick={() => handleStatusChange('shipped')}>Mark as Shipped</MenuItem>
        <MenuItem onClick={() => handleStatusChange('delivered')}>Mark as Delivered</MenuItem>
        <MenuItem onClick={() => handleStatusChange('cancelled')}>Cancel Order</MenuItem>
      </Menu>
    </Box>
  );

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      {/* Main Content */}
      {!selectedOrder && !showCreateForm && renderOrderList()}

      {/* Order Details View */}
      {selectedOrder && (
        <Box>
          <Button
            variant="text"
            onClick={handleBackToList}
            sx={{ mb: 2 }}
          >
             Back to Orders
          </Button>

          <OrderDetails
            order={selectedOrder}
            onStatusChange={(status) => {
              handleStatusChange(status as 'confirmed' | 'processing' | 'shipped' | 'delivered' | 'cancelled');
              handleBackToList(); // Go back to list after status change
            }}
          />
        </Box>
      )}

      {/* Create Order Form */}
      {showCreateForm && (
        <Box>
          <Button
            variant="text"
            onClick={handleBackToList}
            sx={{ mb: 2 }}
          >
             Back to Orders
          </Button>

          <CreateOrderForm
            companyId={companyId}
            onSave={handleOrderSave}
            onCancel={handleBackToList}
          />
        </Box>
      )}
    </Container>
  );
};

export default OrderManagement;

import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import {
  Box,
  Container,
  Typography,
  IconButton,
  Tab,
  Tabs
} from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import ProductList from '../components/whitelabel/products/ProductList';
import ProductForm from '../components/whitelabel/products/ProductForm';
import { useAuth } from '../contexts/AuthContext';

const ProductManagement: React.FC = () => {
  const [showAddForm, setShowAddForm] = useState(false);
  const [selectedProductId, setSelectedProductId] = useState<string | null>(null);
  const [activeTab, setActiveTab] = useState<number>(0);
  const location = useLocation();
  
  // Get company ID from auth context
  const { user } = useAuth();
  const [companyId, setCompanyId] = useState<string>('');
  
  // Set companyId from auth context when user data is available
  useEffect(() => {
    if (user && user.companyId) {
      setCompanyId(user.companyId);
    }
  }, [user]);

  // Parse query parameters
  useEffect(() => {
    const queryParams = new URLSearchParams(location.search);
    const action = queryParams.get('action');
    const id = queryParams.get('id');
    
    if (action === 'create') {
      setShowAddForm(true);
      setSelectedProductId(null);
    } else if (id) {
      setSelectedProductId(id);
      setShowAddForm(false);
    } else {
      // Reset to list view if no params
      setShowAddForm(false);
      setSelectedProductId(null);
    }
  }, [location.search]);

  const handleAddProduct = () => {
    setShowAddForm(true);
    setSelectedProductId(null);
  };

  const handleEditProduct = (productId: string) => {
    setSelectedProductId(productId);
    setShowAddForm(false);
  };

  const handleCloseForm = () => {
    setShowAddForm(false);
    setSelectedProductId(null);
  };

  const handleProductSave = () => {
    // Refresh the product list after saving
    handleCloseForm();
  };

  const handleTabChange = (event: React.SyntheticEvent, newValue: number) => {
    setActiveTab(newValue);
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      {/* Product List View */}
      {!showAddForm && selectedProductId === null && (
        <ProductList
          companyId={companyId}
          onAddProduct={handleAddProduct}
          onEditProduct={handleEditProduct}
        />
      )}

      {/* Add/Edit Product Form */}
      {(showAddForm || selectedProductId !== null) && (
        <Box>
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
            <IconButton
              edge="start"
              color="inherit"
              onClick={handleCloseForm}
              sx={{ mr: 2 }}
            >
              <ArrowBackIcon />
            </IconButton>
            <Typography variant="h5">
              {selectedProductId ? 'Edit Product' : 'Add New Product'}
            </Typography>
          </Box>

          <ProductForm
            productId={selectedProductId || undefined}
            companyId={companyId}
            onSave={handleProductSave}
            onCancel={handleCloseForm}
          />
        </Box>
      )}
    </Container>
  );
};

export default ProductManagement;

import React, { useState, useEffect } from 'react';
import {
  Box,
  Container,
  Paper,
  Typography,
  Tabs,
  Tab,
  Switch,
  FormControlLabel,
  FormGroup,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Button,
  Divider,
  Slider,
  TextField,
  Alert,
  CircularProgress,
  Card,
  CardContent,
  ListItemText,
  List,
  ListItem,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogContentText,
  DialogActions
} from '@mui/material';
import {
  DarkMode as DarkModeIcon,
  LightMode as LightModeIcon,
  Notifications as NotificationsIcon,
  Language as LanguageIcon,
  Security as SecurityIcon,
  Storage as StorageIcon,
  Palette as PaletteIcon,
  AddCircleOutline as AddIcon,
  Check as CheckIcon
} from '@mui/icons-material';
import { useTheme } from '../contexts/ThemeContext';
import TenantConfigPanel from '../components/whitelabel/TenantConfigPanel';
import { useAuth } from '../contexts/AuthContext';
import { useCompanySettings } from '../contexts/CompanySettingsContext';
import SettingsOverrideDisplay from '../components/settings/SettingsOverrideDisplay';

// Define interface for integration objects
interface Integration {
  id: number;
  name: string;
  connected: boolean;
}

interface TabPanelProps {
  children?: React.ReactNode;
  index: number;
  value: number;
}

function TabPanel(props: TabPanelProps) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`settings-tabpanel-${index}`}
      aria-labelledby={`settings-tab-${index}`}
      {...other}
    >
      {value === index && <Box sx={{ p: 3 }}>{children}</Box>}
    </div>
  );
}

const Settings: React.FC = () => {
  const { user } = useAuth();
  const { theme, updateTheme } = useTheme();
  const { 
    settings, 
    updateSettings, 
    isCompanyAdmin, 
    applyCompanySettings, 
    resetSettingsSection,
    userOverrides,
    companySettings 
  } = useCompanySettings();
  
  const [tabValue, setTabValue] = useState(0);
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState<string | null>(null);
  const [error, setError] = useState<string | null>(null);
  
  // Extract appearance settings
  const [darkMode, setDarkMode] = useState(() => settings.appearance.darkMode);
  const [language, setLanguage] = useState(() => settings.appearance.language);
  const [fontSize, setFontSize] = useState(() => settings.appearance.fontSize);
  
  // Extract notification settings
  const [emailNotifications, setEmailNotifications] = useState(() => settings.notifications.email);
  const [pushNotifications, setPushNotifications] = useState(() => settings.notifications.push);
  const [newOrderNotifications, setNewOrderNotifications] = useState(() => settings.notifications.newOrders);
  const [inventoryAlerts, setInventoryAlerts] = useState(() => settings.notifications.inventory);
  
  // Extract integration settings
  const [apiKey, setApiKey] = useState(() => settings.integrations.apiKey);
  const [integrations, setIntegrations] = useState<Integration[]>(() => 
    settings.integrations.connectedServices || [
      { id: 1, name: 'Warehouse System', connected: true },
      { id: 2, name: 'Accounting Software', connected: false },
      { id: 3, name: 'CRM System', connected: false }
    ]
  );
  
  // Update local state when settings change
  useEffect(() => {
    setDarkMode(settings.appearance.darkMode);
    setLanguage(settings.appearance.language);
    setFontSize(settings.appearance.fontSize);
    setEmailNotifications(settings.notifications.email);
    setPushNotifications(settings.notifications.push);
    setNewOrderNotifications(settings.notifications.newOrders);
    setInventoryAlerts(settings.notifications.inventory);
    setApiKey(settings.integrations.apiKey);
    if (settings.integrations.connectedServices) {
      setIntegrations(settings.integrations.connectedServices);
    }
  }, [settings]);
  
  // Dialog state
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const [exportDialogOpen, setExportDialogOpen] = useState(false);
  
  const handleTabChange = (event: React.SyntheticEvent, newValue: number) => {
    setTabValue(newValue);
  };
  const handleSaveAppSettings = async () => {
    setLoading(true);
    setError(null);
    setSuccess(null);
    
    try {
      // Determine if we should apply settings company-wide
      const applyToCompany = isCompanyAdmin && document.getElementById('applyCompanyWide')?.getAttribute('data-checked') === 'true';
      
      // Update company settings context
      updateSettings({
        appearance: {
          darkMode,
          language,
          fontSize
        }
      }, applyToCompany);
      
      // Update theme with proper dark mode colors
      if (darkMode) {
        updateTheme({
          backgroundColor: '#121212',
          textColor: '#ffffff'
        });
      } else {
        updateTheme({
          backgroundColor: '#fafafa',
          textColor: '#333333'
        });
      }
      
      // Simulate API call (so users see the loading state)
      await new Promise(resolve => setTimeout(resolve, 500));
      
      setSuccess(`Application settings saved successfully${applyToCompany ? ' and applied company-wide' : ''}`);
    } catch (err: any) {
      setError(err.message || 'Failed to save settings');
    } finally {
      setLoading(false);
    }
  };
  const handleSaveNotificationSettings = async () => {
    setLoading(true);
    setError(null);
    setSuccess(null);
    
    try {
      // Determine if we should apply settings company-wide
      const applyToCompany = isCompanyAdmin && document.getElementById('notificationsApplyCompanyWide')?.getAttribute('data-checked') === 'true';
      
      // Update company settings context
      updateSettings({
        notifications: {
          email: emailNotifications,
          push: pushNotifications,
          newOrders: newOrderNotifications,
          inventory: inventoryAlerts
        }
      }, applyToCompany);
      
      // Simulate API call (so users see the loading state)
      await new Promise(resolve => setTimeout(resolve, 500));
      
      setSuccess(`Notification settings saved successfully${applyToCompany ? ' and applied company-wide' : ''}`);
    } catch (err: any) {
      setError(err.message || 'Failed to save settings');
    } finally {
      setLoading(false);
    }
  };
    const handleGenerateApiKey = async () => {
    setLoading(true);
    setError(null);
    setSuccess(null);
    
    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 800));
      
      // Generate a random API key for demo purposes
      const newApiKey = `api_${Math.random().toString(36).substring(2, 15)}_${Date.now().toString(36)}`;
      
      // Determine if we should apply settings company-wide
      const applyToCompany = isCompanyAdmin && document.getElementById('integrationsApplyCompanyWide')?.getAttribute('data-checked') === 'true';
      
      // Update company settings context
      updateSettings({
        integrations: {
          ...settings.integrations, // Spread existing integrations to keep all properties
          apiKey: newApiKey
        }
      }, applyToCompany);
      
      // Update local state
      setApiKey(newApiKey);
      
      setSuccess(`New API key generated successfully${applyToCompany ? ' and applied company-wide' : ''}`);
    } catch (err: any) {
      setError(err.message || 'Failed to generate API key');
    } finally {
      setLoading(false);
    }
  };
    const handleConnectIntegration = async (integrationId: number) => {
    setLoading(true);
    setError(null);
    setSuccess(null);
    
    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 700));
      
      // Update local state
      const updatedIntegrations = integrations.map(integration =>
        integration.id === integrationId
          ? { ...integration, connected: !integration.connected }
          : integration
      );
      
      // Determine if we should apply settings company-wide
      const applyToCompany = isCompanyAdmin && document.getElementById('integrationsApplyCompanyWide')?.getAttribute('data-checked') === 'true';
      
      // Update company settings context
      updateSettings({
        integrations: {
          ...settings.integrations, // Spread existing integrations to keep all properties
          connectedServices: updatedIntegrations
        }
      }, applyToCompany);
      
      // Update local state
      setIntegrations(updatedIntegrations);
      
      setSuccess(`Integration updated successfully${applyToCompany ? ' and applied company-wide' : ''}`);
    } catch (err: any) {
      setError(err.message || 'Failed to update integration');
    } finally {
      setLoading(false);
    }
  };

  // Apply font size changes immediately
  useEffect(() => {
    document.documentElement.style.fontSize = `${fontSize}px`;
    return () => {
      // Reset font size when component unmounts
      document.documentElement.style.fontSize = ''; 
    };
  }, [fontSize]);
  
  // Apply dark mode immediately for better visual feedback
  useEffect(() => {
    if (darkMode) {
      document.body.classList.add('dark-mode');
      // Apply immediate theme changes for dark mode
      updateTheme({
        backgroundColor: '#121212',
        textColor: '#ffffff'
      });
    } else {
      document.body.classList.remove('dark-mode');
      // Apply immediate theme changes for light mode
      updateTheme({
        backgroundColor: '#fafafa',
        textColor: '#333333'
      });
    }
  }, [darkMode]);

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Paper sx={{ p: { xs: 2, md: 3 } }}>
        <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', mb: 3 }}>
          <Typography variant="h4" component="h1">
            Settings
          </Typography>
          
          {isCompanyAdmin ? (
            <Typography variant="subtitle1" color="primary">
              Company Admin
            </Typography>
          ) : (
            <Button 
              variant="outlined" 
              size="small"
              onClick={applyCompanySettings}
              startIcon={<PaletteIcon />}
            >
              Use Company Defaults
            </Button>
          )}
        </Box>
        
        {/* Display customized settings */}
        {!isCompanyAdmin && Object.keys(userOverrides).length > 0 && (
          <SettingsOverrideDisplay 
            companySettings={companySettings}
            userSettings={userOverrides}
            onResetToCompany={resetSettingsSection}
          />
        )}

        <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
          <Tabs value={tabValue} onChange={handleTabChange} aria-label="settings tabs">
            <Tab icon={<PaletteIcon />} iconPosition="start" label="Appearance" />
            <Tab icon={<NotificationsIcon />} iconPosition="start" label="Notifications" />
            <Tab icon={<StorageIcon />} iconPosition="start" label="Integrations" />
            <Tab icon={<SecurityIcon />} iconPosition="start" label="Privacy & Data" />
          </Tabs>
        </Box>
        
        {success && <Alert severity="success" sx={{ mt: 2 }}>{success}</Alert>}
        {error && <Alert severity="error" sx={{ mt: 2 }}>{error}</Alert>}
        
        <TabPanel value={tabValue} index={0}>
          <Box sx={{ maxWidth: 800 }}>
            <Typography variant="h6" gutterBottom>
              Theme Settings
            </Typography>
            
            <FormGroup>
              <FormControlLabel
                control={
                  <Switch
                    checked={darkMode}
                    onChange={(e) => setDarkMode(e.target.checked)}
                  />
                }
                label={
                  <Box sx={{ display: 'flex', alignItems: 'center' }}>
                    {darkMode ? <DarkModeIcon sx={{ mr: 1 }} /> : <LightModeIcon sx={{ mr: 1 }} />}
                    {darkMode ? 'Dark Mode' : 'Light Mode'}
                  </Box>
                }
              />
            </FormGroup>
            
            <Typography id="font-size-slider" gutterBottom>
              Font Size
            </Typography>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
              <Box sx={{ flex: 1 }}>
                <Slider
                  value={fontSize}
                  min={12}
                  max={24}
                  step={1}
                  onChange={(e: Event, newValue: number | number[]) => setFontSize(newValue as number)}
                  aria-labelledby="font-size-slider"
                />
              </Box>
              <Box>
                <Typography>{fontSize}px</Typography>
              </Box>
            </Box>
            
            <FormControl fullWidth sx={{ mt: 2 }}>
              <InputLabel id="language-select-label">Language</InputLabel>
              <Select
                labelId="language-select-label"
                value={language}
                label="Language"
                onChange={(e) => setLanguage(e.target.value as string)}
                startAdornment={<LanguageIcon sx={{ mr: 1, ml: -0.5 }} />}
              >
                <MenuItem value="en">English</MenuItem>
                <MenuItem value="vi">Vietnamese</MenuItem>
                <MenuItem value="ja">Japanese</MenuItem>
                <MenuItem value="ko">Korean</MenuItem>
                <MenuItem value="zh">Chinese</MenuItem>
              </Select>
            </FormControl>
            
            {isCompanyAdmin && (
              <FormControlLabel
                control={
                  <Switch
                    id="applyCompanyWide"
                    onChange={(e) => e.target.setAttribute('data-checked', e.target.checked.toString())}
                  />
                }
                label="Apply to all company users"
                sx={{ mt: 2, display: 'block' }}
              />
            )}
            
            <Button
              variant="contained"
              color="primary"
              sx={{ mt: 3 }}
              onClick={handleSaveAppSettings}
              disabled={loading}
              startIcon={loading ? <CircularProgress size={20} /> : null}
            >
              Save Appearance Settings
            </Button>
            
            <Divider sx={{ my: 4 }} />
            
            {user?.role === 'admin' && (
              <>
                <Typography variant="h6" gutterBottom>
                  White Label Settings
                </Typography>
                
                <Typography variant="body2" color="text.secondary" paragraph>
                  Customize your company's branding and appearance. These settings apply to all users in your organization.
                </Typography>
                
                <TenantConfigPanel tenantId={user.companyId} />
              </>
            )}
          </Box>
        </TabPanel>
        
        <TabPanel value={tabValue} index={1}>
          <Box sx={{ maxWidth: 800 }}>
            <Typography variant="h6" gutterBottom>
              Notification Preferences
            </Typography>
            <FormGroup>
              <Typography variant="subtitle1" sx={{ mt: 3 }}>
                Channels
              </Typography>
              <FormControlLabel
                control={
                  <Switch
                    checked={emailNotifications}
                    onChange={(e) => setEmailNotifications(e.target.checked)}
                  />
                }
                label="Email Notifications"
              />
              <FormControlLabel
                control={
                  <Switch
                    checked={pushNotifications}
                    onChange={(e) => setPushNotifications(e.target.checked)}
                  />
                }
                label="Push Notifications"
              />
            </FormGroup>
            <FormGroup>
              <Typography variant="subtitle1" sx={{ mt: 3 }}>
                Events
              </Typography>
              <FormControlLabel
                control={
                  <Switch
                    checked={newOrderNotifications}
                    onChange={(e) => setNewOrderNotifications(e.target.checked)}
                  />
                }
                label="New Orders"
              />
              <FormControlLabel
                control={
                  <Switch
                    checked={inventoryAlerts}
                    onChange={(e) => setInventoryAlerts(e.target.checked)}
                  />
                }
                label="Low Inventory Alerts"
              />
            </FormGroup>
            
            {isCompanyAdmin && (
              <FormControlLabel
                control={
                  <Switch
                    id="notificationsApplyCompanyWide"
                    onChange={(e) => e.target.setAttribute('data-checked', e.target.checked.toString())}
                  />
                }
                label="Apply to all company users"
                sx={{ mt: 2, display: 'block' }}
              />
            )}
            
            <Button
              variant="contained"
              color="primary"
              sx={{ mt: 3 }}
              onClick={handleSaveNotificationSettings}
              disabled={loading}
              startIcon={loading ? <CircularProgress size={20} /> : null}
            >
              Save Notification Settings
            </Button>
          </Box>
        </TabPanel>
        
        <TabPanel value={tabValue} index={2}>
          <Box sx={{ maxWidth: 800 }}>
            <Typography variant="h6" gutterBottom>
              API Configuration
            </Typography>
            <Card variant="outlined" sx={{ mb: 4 }}>
              <CardContent>
                <Typography variant="subtitle1" gutterBottom>
                  Your API Key
                </Typography>
                <TextField
                  fullWidth
                  value={apiKey || ''}
                  InputProps={{
                    readOnly: true
                  }}
                  variant="outlined"
                  sx={{ mb: 2 }}
                />
                {isCompanyAdmin && (
                  <FormControlLabel
                    control={
                      <Switch
                        id="integrationsApplyCompanyWide"
                        onChange={(e) => e.target.setAttribute('data-checked', e.target.checked.toString())}
                      />
                    }
                    label="Apply to all company users"
                  />
                )}
                <Button
                  variant="contained"
                  onClick={handleGenerateApiKey}
                  disabled={loading}
                  startIcon={loading ? <CircularProgress size={20} /> : null}
                >
                  Generate New API Key
                </Button>
              </CardContent>
            </Card>
            
            <Typography variant="h6" gutterBottom>
              Connected Systems
            </Typography>
            <List>
              {integrations.map((integration) => (
                <ListItem
                  key={integration.id}
                  secondaryAction={
                    <Button
                      variant={integration.connected ? "outlined" : "contained"}
                      color={integration.connected ? "error" : "primary"}
                      onClick={() => handleConnectIntegration(integration.id)}
                      size="small"
                    >
                      {integration.connected ? 'Disconnect' : 'Connect'}
                    </Button>
                  }
                  disablePadding
                  sx={{ mb: 1, border: '1px solid', borderColor: 'divider', borderRadius: 1 }}
                >
                  <Box sx={{ p: 2, width: '100%', display: 'flex', alignItems: 'center' }}>
                    {integration.connected && (
                      <CheckIcon color="success" sx={{ mr: 1 }} />
                    )}
                    <ListItemText
                      primary={integration.name}
                      secondary={integration.connected ? 'Connected' : 'Not connected'}
                    />
                  </Box>
                </ListItem>
              ))}
            </List>
            <Button
              variant="outlined"
              startIcon={<AddIcon />}
              sx={{ mt: 2 }}
            >
              Add New Integration
            </Button>
          </Box>
        </TabPanel>
        
        <TabPanel value={tabValue} index={3}>
          <Box sx={{ maxWidth: 800 }}>
            <Typography variant="h6" gutterBottom>
              Data Management
            </Typography>
            <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 3 }}>
              <Box sx={{ flex: { xs: '1 1 100%', md: '0 0 calc(50% - 12px)' } }}>
                <Card variant="outlined">
                  <CardContent>
                    <Typography variant="subtitle1" gutterBottom>
                      Export Your Data
                    </Typography>
                    <Typography variant="body2" color="text.secondary" paragraph>
                      Download a copy of all your data in CSV or JSON format.
                    </Typography>
                    <Button
                      variant="outlined"
                      onClick={() => setExportDialogOpen(true)}
                    >
                      Export Data
                    </Button>
                  </CardContent>
                </Card>
              </Box>
              <Box sx={{ flex: { xs: '1 1 100%', md: '0 0 calc(50% - 12px)' } }}>
                <Card variant="outlined" sx={{ borderColor: 'error.light' }}>
                  <CardContent>
                    <Typography variant="subtitle1" color="error" gutterBottom>
                      Delete Account
                    </Typography>
                    <Typography variant="body2" color="text.secondary" paragraph>
                      Permanently delete your account and all associated data.
                    </Typography>
                    <Button
                      variant="outlined"
                      color="error"
                      onClick={() => setDeleteDialogOpen(true)}
                    >
                      Delete Account
                    </Button>
                  </CardContent>
                </Card>
              </Box>
            </Box>
          </Box>
        </TabPanel>
      </Paper>
      
      {/* Export Data Dialog */}
      <Dialog
        open={exportDialogOpen}
        onClose={() => setExportDialogOpen(false)}
      >
        <DialogTitle>Export Your Data</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Choose the format you want to export your data in:
          </DialogContentText>
          <Box sx={{ display: 'flex', justifyContent: 'space-around', mt: 2 }}>
            <Button variant="outlined">
              CSV Format
            </Button>
            <Button variant="outlined">
              JSON Format
            </Button>
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setExportDialogOpen(false)}>Cancel</Button>
        </DialogActions>
      </Dialog>
      
      {/* Delete Account Dialog */}
      <Dialog
        open={deleteDialogOpen}
        onClose={() => setDeleteDialogOpen(false)}
      >
        <DialogTitle>
          <Typography color="error">Delete Account</Typography>
        </DialogTitle>
        <DialogContent>
          <DialogContentText>
            Are you sure you want to delete your account? This action cannot be undone and all your data will be permanently deleted.
          </DialogContentText>
          <TextField
            autoFocus
            margin="dense"
            id="confirmation"
            label="Type 'DELETE' to confirm"
            fullWidth
            variant="outlined"
            sx={{ mt: 2 }}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setDeleteDialogOpen(false)}>Cancel</Button>
          <Button color="error">
            Delete Permanently
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default Settings;

import React, { useState, useEffect } from 'react';
import {
  Box,
  Container,
  Typography,
  Paper,
  Button,
  TextField,
  Chip,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  FormHelperText,
  CircularProgress,
  Alert,
  Tabs,
  Tab,
  Switch,
  FormControlLabel,
} from '@mui/material';
import { userManagementService, UserListParams, UserListResponse, UserUpdateRequest, UserCreateRequest } from '../services/api';
import { useAuth } from '../contexts/AuthContext';
import UsersTab from '../components/whitelabel/user-management/UsersTab';
import RolesTab from '../components/whitelabel/user-management/RolesTab';
import InvitationsTab from '../components/whitelabel/user-management/InvitationsTab';

interface TabPanelProps {
  children?: React.ReactNode;
  index: number;
  value: number;
}

function TabPanel(props: TabPanelProps) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`user-tabpanel-${index}`}
      aria-labelledby={`user-tab-${index}`}
      {...other}
    >
      {value === index && <Box sx={{ pt: 2 }}>{children}</Box>}
    </div>
  );
}

function a11yProps(index: number) {
  return {
    id: `user-tab-${index}`,
    'aria-controls': `user-tabpanel-${index}`,
  };
}

const UserManagement: React.FC = () => {
  const { user } = useAuth(); // Retrieve user object from AuthContext
  const companyId = user?.companyId; // Extract companyId dynamically

  // Tab state
  const [tabValue, setTabValue] = useState(0);
  
  // Users state
  const [users, setUsers] = useState<any[]>([]);
  const [roles, setRoles] = useState<any[]>([]);
  const [invitations, setInvitations] = useState<any[]>([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  
  // User dialog state
  const [userDialogOpen, setUserDialogOpen] = useState(false);
  const [selectedUser, setSelectedUser] = useState<any | null>(null);
  const [userFormData, setUserFormData] = useState({
    name: '',
    email: '',
    role: '',
    isActive: true
  });
  const [userFormErrors, setUserFormErrors] = useState<Record<string, string>>({});
  
  // Role dialog state
  const [roleDialogOpen, setRoleDialogOpen] = useState(false);
  const [selectedRole, setSelectedRole] = useState<any | null>(null);
  const [roleFormData, setRoleFormData] = useState({
    name: '',
    description: '',
    permissions: [] as string[]
  });
  const [roleFormErrors, setRoleFormErrors] = useState<Record<string, string>>({});
  
  // Invitation dialog state
  const [inviteDialogOpen, setInviteDialogOpen] = useState(false);
  const [inviteFormData, setInviteFormData] = useState({
    email: '',
    role: '',
    message: ''
  });
  const [inviteFormErrors, setInviteFormErrors] = useState<Record<string, string>>({});

  // Confirmation dialog state
  const [confirmDialogOpen, setConfirmDialogOpen] = useState(false);
  const [confirmAction, setConfirmAction] = useState<(() => Promise<void>) | null>(null);
  const [confirmTitle, setConfirmTitle] = useState('');
  const [confirmMessage, setConfirmMessage] = useState('');

  // Available permissions
  const [availablePermissions, setAvailablePermissions] = useState<string[]>([
    'users:read', 'users:write', 'users:delete',
    'inventory:read', 'inventory:write', 'inventory:delete',
    'products:read', 'products:write', 'products:delete',
    'orders:read', 'orders:write', 'orders:delete',
    'reports:read'
  ]);

  // Fetch all data when component mounts
  useEffect(() => {
    fetchUsers();
    fetchRoles();
    fetchInvitations();
  }, []);

  // Fetch users based on search query
  const fetchUsers = async () => {
    setLoading(true);
    setError(null);
    
    try {
      const params: UserListParams = {
        company_id: companyId,
        search: searchQuery || undefined, // Ensure compatibility with `UserListParams`
      };

      const response: UserListResponse = await userManagementService.getUsers(params);
      setUsers(response.data || []); // Adjusted to use `data` instead of `users`
    } catch (err: any) {
      console.error('Error fetching users:', err);
      setError(err.message || 'Failed to load users');
    } finally {
      setLoading(false);
    }
  };

  const fetchRoles = async () => {
    console.warn('`getRoles` method is not implemented in `userManagementService`.');
  };

  const fetchInvitations = async () => {
    console.warn('`getInvitations` method is not implemented in `userManagementService`.');
  };

  // Tab change handler
  const handleTabChange = (event: React.SyntheticEvent, newValue: number) => {
    setTabValue(newValue);
  };

  // User form handlers
  const openUserDialog = (user: any = null) => {
    if (user) {
      setSelectedUser(user);
      setUserFormData({
        name: user.name || '',
        email: user.email || '',
        role: user.role?.id || '',
        isActive: user.isActive !== false
      });
    } else {
      setSelectedUser(null);
      setUserFormData({
        name: '',
        email: '',
        role: '',
        isActive: true
      });
    }
    setUserFormErrors({});
    setUserDialogOpen(true);
  };

  const closeUserDialog = () => {
    setUserDialogOpen(false);
    setSelectedUser(null);
  };

  const handleUserFormChange = (field: string, value: any) => {
    if (field === 'role' && !['admin', 'manager', 'staff', 'supplier', 'dealer'].includes(value)) {
      setUserFormErrors((prev) => ({ ...prev, role: 'Invalid role selected' }));
      return;
    }

    setUserFormData((prev) => ({ ...prev, [field]: value }));
    if (userFormErrors[field]) {
      setUserFormErrors((prev) => ({ ...prev, [field]: '' }));
    }
  };

  const validateUserForm = () => {
    const errors: Record<string, string> = {};
    
    if (!userFormData.name.trim()) {
      errors['name'] = 'Name is required';
    }
    
    if (!userFormData.email.trim()) {
      errors['email'] = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(userFormData.email)) {
      errors['email'] = 'Invalid email format';
    }
    
    if (!userFormData.role) {
      errors['role'] = 'Role is required';
    }
    
    setUserFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleUserSave = async () => {
    if (!validateUserForm()) return;
    
    setLoading(true);
    try {
      if (selectedUser) {
        const updateData: UserUpdateRequest = {
          firstName: userFormData.name.split(' ')[0],
          lastName: userFormData.name.split(' ').slice(1).join(' ') || '',
          email: userFormData.email,
          role: userFormData.role as UserUpdateRequest['role'],
          status: userFormData.isActive ? 'active' : 'inactive',
          companyId: companyId ?? '',
        };
        await userManagementService.updateUser(selectedUser.id, updateData);
      } else {
        const createData: UserCreateRequest = {
          firstName: userFormData.name.split(' ')[0],
          lastName: userFormData.name.split(' ').slice(1).join(' ') || '',
          email: userFormData.email,
          password: 'defaultPassword123', // Placeholder password
          role: userFormData.role as UserCreateRequest['role'],
          status: userFormData.isActive ? 'active' : 'inactive',
          companyId: companyId ?? '',
        };
        await userManagementService.createUser(createData);
      }
      
      fetchUsers();
      closeUserDialog();
    } catch (err: any) {
      console.error('Error saving user:', err);
      setError(err.message || 'Failed to save user');
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteUser = (userId: string) => {
    setConfirmTitle('Delete User');
    setConfirmMessage('Are you sure you want to delete this user? This action cannot be undone.');
    setConfirmAction(async () => {
      try {
        await userManagementService.deleteUser(userId);
        fetchUsers();
      } catch (err: any) {
        console.error('Error deleting user:', err);
        setError(err.message || 'Failed to delete user');
      }
    });
    setConfirmDialogOpen(true);
  };

  // Role form handlers
  const openRoleDialog = (role: any = null) => {
    if (role) {
      setSelectedRole(role);
      setRoleFormData({
        name: role.name || '',
        description: role.description || '',
        permissions: role.permissions || []
      });
    } else {
      setSelectedRole(null);
      setRoleFormData({
        name: '',
        description: '',
        permissions: []
      });
    }
    setRoleFormErrors({});
    setRoleDialogOpen(true);
  };

  const closeRoleDialog = () => {
    setRoleDialogOpen(false);
    setSelectedRole(null);
  };

  const handleRoleFormChange = (field: string, value: any) => {
    setRoleFormData(prev => ({ ...prev, [field]: value }));
    if (roleFormErrors[field]) {
      setRoleFormErrors(prev => ({ ...prev, [field]: '' }));
    }
  };

  const handlePermissionToggle = (permission: string) => {
    setRoleFormData(prev => {
      const isSelected = prev.permissions.includes(permission);
      const updatedPermissions = isSelected
        ? prev.permissions.filter(p => p !== permission)
        : [...prev.permissions, permission];
        
      return { ...prev, permissions: updatedPermissions };
    });
  };

  const validateRoleForm = () => {
    const errors: Record<string, string> = {};
    
    if (!roleFormData.name.trim()) {
      errors['name'] = 'Role name is required';
    }
    
    if (roleFormData.permissions.length === 0) {
      errors['permissions'] = 'At least one permission must be selected';
    }
    
    setRoleFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleRoleSave = async () => {
    if (!validateRoleForm()) return;
    
    setLoading(true);
    try {
      if (selectedRole) {
        await userManagementService.updateRole(selectedRole.id, {
          ...roleFormData,
          companyId
        });
      } else {
        await userManagementService.createRole({
          ...roleFormData,
          companyId
        });
      }
      
      fetchRoles();
      closeRoleDialog();
    } catch (err: any) {
      console.error('Error saving role:', err);
      setError(err.message || 'Failed to save role');
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteRole = (roleId: string) => {
    setConfirmTitle('Delete Role');
    setConfirmMessage('Are you sure you want to delete this role? Users with this role will need to be reassigned.');
    setConfirmAction(async () => {
      try {
        await userManagementService.deleteRole(roleId);
        fetchRoles();
      } catch (err: any) {
        console.error('Error deleting role:', err);
        setError(err.message || 'Failed to delete role');
      }
    });
    setConfirmDialogOpen(true);
  };

  // Invitation form handlers
  const openInviteDialog = () => {
    setInviteFormData({
      email: '',
      role: '',
      message: 'I would like to invite you to join our team on Tubex.'
    });
    setInviteFormErrors({});
    setInviteDialogOpen(true);
  };

  const closeInviteDialog = () => {
    setInviteDialogOpen(false);
  };

  const handleInviteFormChange = (field: string, value: any) => {
    setInviteFormData(prev => ({ ...prev, [field]: value }));
    if (inviteFormErrors[field]) {
      setInviteFormErrors(prev => ({ ...prev, [field]: '' }));
    }
  };

  const validateInviteForm = () => {
    const errors: Record<string, string> = {};
    
    if (!inviteFormData.email.trim()) {
      errors['email'] = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(inviteFormData.email)) {
      errors['email'] = 'Invalid email format';
    }
    
    if (!inviteFormData.role) {
      errors['role'] = 'Role is required';
    }
    
    setInviteFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleSendInvite = async () => {
    if (!validateInviteForm()) return;
    
    setLoading(true);
    try {
      await userManagementService.sendInvitation({
        ...inviteFormData,
        companyId
      });
      
      fetchInvitations();
      closeInviteDialog();
    } catch (err: any) {
      console.error('Error sending invitation:', err);
      setError(err.message || 'Failed to send invitation');
    } finally {
      setLoading(false);
    }
  };

  const handleResendInvite = (invitationId: string) => {
    setLoading(true);
    setError(null);
    
    (async () => {
      try {
        await userManagementService.resendInvitation(invitationId);
        fetchInvitations();
      } catch (err: any) {
        console.error('Error resending invitation:', err);
        setError(err.message || 'Failed to resend invitation');
      } finally {
        setLoading(false);
      }
    })();
  };

  const handleCancelInvite = (invitationId: string) => {
    setConfirmTitle('Cancel Invitation');
    setConfirmMessage('Are you sure you want to cancel this invitation?');
    setConfirmAction(async () => {
      try {
        await userManagementService.cancelInvitation(invitationId);
        fetchInvitations();
      } catch (err: any) {
        console.error('Error cancelling invitation:', err);
        setError(err.message || 'Failed to cancel invitation');
      }
    });
    setConfirmDialogOpen(true);
  };

  // Permission group rendering
  const renderPermissionGroup = (group: string) => {
    const groupPermissions = availablePermissions.filter(p => p.startsWith(`${group}:`));
    
    return (
      <Paper key={group} sx={{ p: 2, mb: 2 }}>
        <Typography variant="subtitle1" sx={{ mb: 1 }}>
          {group.charAt(0).toUpperCase() + group.slice(1)}
        </Typography>
        <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1 }}>
          {groupPermissions.map(permission => {
            const action = permission.split(':')[1];
            const isSelected = roleFormData.permissions.includes(permission);
            
            return (
              <Chip
                key={permission}
                label={action}
                color={isSelected ? 'primary' : 'default'}
                onClick={() => handlePermissionToggle(permission)}
                sx={{ textTransform: 'capitalize' }}
              />
            );
          })}
        </Box>
      </Paper>
    );
  };

  // Get unique permission groups
  const permissionGroups = Array.from(new Set(availablePermissions.map((p) => p.split(':')[0])));

  if (!companyId) {
    return <Typography variant="h6">Company ID is not available. Please log in again.</Typography>;
  }

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography variant="h4" component="h1">
          User Management
        </Typography>
      </Box>

      {error && (
        <Alert severity="error" sx={{ mb: 3 }}>
          {error}
        </Alert>
      )}

      <Paper sx={{ mb: 3 }}>
        <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
          <Tabs value={tabValue} onChange={handleTabChange} aria-label="user management tabs">
            <Tab label="Users" {...a11yProps(0)} />
            <Tab label="Roles" {...a11yProps(1)} />
            <Tab label="Invitations" {...a11yProps(2)} />
          </Tabs>
        </Box>

        <TabPanel value={tabValue} index={0}>
          <UsersTab
            users={users}
            loading={loading}
            fetchUsers={fetchUsers}
            openUserDialog={openUserDialog}
            handleDeleteUser={handleDeleteUser}
          />
        </TabPanel>

        <TabPanel value={tabValue} index={1}>
          <RolesTab
            roles={roles}
            loading={loading}
            openRoleDialog={openRoleDialog}
            handleDeleteRole={handleDeleteRole}
          />
        </TabPanel>

        <TabPanel value={tabValue} index={2}>
          <InvitationsTab
            invitations={invitations}
            loading={loading}
            openInviteDialog={openInviteDialog}
            handleResendInvite={handleResendInvite}
            handleCancelInvite={handleCancelInvite}
          />
        </TabPanel>
      </Paper>

      {/* User Dialog */}
      <Dialog 
        open={userDialogOpen} 
        onClose={closeUserDialog}
        maxWidth="sm"
        fullWidth
      >
        <DialogTitle>
          {selectedUser ? 'Edit User' : 'Add New User'}
        </DialogTitle>
        <DialogContent>
          <Box sx={{ pt: 1, display: 'flex', flexDirection: 'column', gap: 2 }}>
            <TextField
              label="Name"
              fullWidth
              value={userFormData.name}
              onChange={(e) => handleUserFormChange('name', e.target.value)}
              error={!!userFormErrors.name}
              helperText={userFormErrors.name}
            />
            
            <TextField
              label="Email"
              type="email"
              fullWidth
              value={userFormData.email}
              onChange={(e) => handleUserFormChange('email', e.target.value)}
              error={!!userFormErrors.email}
              helperText={userFormErrors.email}
              disabled={!!selectedUser} // Email can't be changed for existing users
            />
            
            <FormControl fullWidth error={!!userFormErrors.role}>
              <InputLabel>Role</InputLabel>
              <Select
                value={userFormData.role}
                label="Role"
                onChange={(e) => handleUserFormChange('role', e.target.value)}
              >
                {roles.map(role => (
                  <MenuItem key={role.id} value={role.id}>
                    {role.name}
                  </MenuItem>
                ))}
              </Select>
              {userFormErrors.role && (
                <FormHelperText>{userFormErrors.role}</FormHelperText>
              )}
            </FormControl>
            
            <FormControlLabel
              control={
                <Switch
                  checked={userFormData.isActive}
                  onChange={(e) => handleUserFormChange('isActive', e.target.checked)}
                />
              }
              label="Active"
            />
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={closeUserDialog}>Cancel</Button>
          <Button 
            onClick={handleUserSave} 
            variant="contained" 
            color="primary"
            disabled={loading}
          >
            {loading ? <CircularProgress size={24} /> : 'Save'}
          </Button>
        </DialogActions>
      </Dialog>

      {/* Role Dialog */}
      <Dialog 
        open={roleDialogOpen} 
        onClose={closeRoleDialog}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle>
          {selectedRole ? 'Edit Role' : 'Add New Role'}
        </DialogTitle>
        <DialogContent>
          <Box sx={{ pt: 1, display: 'flex', flexDirection: 'column', gap: 2 }}>
            <TextField
              label="Role Name"
              fullWidth
              value={roleFormData.name}
              onChange={(e) => handleRoleFormChange('name', e.target.value)}
              error={!!roleFormErrors.name}
              helperText={roleFormErrors.name}
            />
            
            <TextField
              label="Description"
              fullWidth
              value={roleFormData.description}
              onChange={(e) => handleRoleFormChange('description', e.target.value)}
            />
            
            <Typography variant="subtitle1" gutterBottom>
              Permissions
            </Typography>
            
            {roleFormErrors.permissions && (
              <FormHelperText error>{roleFormErrors.permissions}</FormHelperText>
            )}
            
            {permissionGroups.map(group => renderPermissionGroup(group))}
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={closeRoleDialog}>Cancel</Button>
          <Button 
            onClick={handleRoleSave} 
            variant="contained" 
            color="primary"
            disabled={loading}
          >
            {loading ? <CircularProgress size={24} /> : 'Save'}
          </Button>
        </DialogActions>
      </Dialog>

      {/* Invite Dialog */}
      <Dialog 
        open={inviteDialogOpen} 
        onClose={closeInviteDialog}
        maxWidth="sm"
        fullWidth
      >
        <DialogTitle>Invite User</DialogTitle>
        <DialogContent>
          <Box sx={{ pt: 1, display: 'flex', flexDirection: 'column', gap: 2 }}>
            <TextField
              label="Email Address"
              type="email"
              fullWidth
              value={inviteFormData.email}
              onChange={(e) => handleInviteFormChange('email', e.target.value)}
              error={!!inviteFormErrors.email}
              helperText={inviteFormErrors.email}
            />
            
            <FormControl fullWidth error={!!inviteFormErrors.role}>
              <InputLabel>Role</InputLabel>
              <Select
                value={inviteFormData.role}
                label="Role"
                onChange={(e) => handleInviteFormChange('role', e.target.value)}
              >
                {roles.map(role => (
                  <MenuItem key={role.id} value={role.id}>
                    {role.name}
                  </MenuItem>
                ))}
              </Select>
              {inviteFormErrors.role && (
                <FormHelperText>{inviteFormErrors.role}</FormHelperText>
              )}
            </FormControl>
            
            <TextField
              label="Invitation Message"
              multiline
              rows={3}
              fullWidth
              value={inviteFormData.message}
              onChange={(e) => handleInviteFormChange('message', e.target.value)}
            />
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={closeInviteDialog}>Cancel</Button>
          <Button 
            onClick={handleSendInvite} 
            variant="contained" 
            color="primary"
            disabled={loading}
          >
            {loading ? <CircularProgress size={24} /> : 'Send Invitation'}
          </Button>
        </DialogActions>
      </Dialog>

      {/* Confirmation Dialog */}
      <Dialog open={confirmDialogOpen} onClose={() => setConfirmDialogOpen(false)}>
        <DialogTitle>{confirmTitle}</DialogTitle>
        <DialogContent>
          <Typography>{confirmMessage}</Typography>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setConfirmDialogOpen(false)}>Cancel</Button>
          <Button
            variant="contained"
            color="error"
            onClick={async () => {
              if (confirmAction) {
                setLoading(true);
                await confirmAction();
                setLoading(false);
              }
              setConfirmDialogOpen(false);
            }}
            disabled={loading}
          >
            {loading ? <CircularProgress size={24} /> : 'Confirm'}
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default UserManagement;

import React, { useState, useEffect } from 'react';
import {
  Box,
  Paper,
  Typography,
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  IconButton,
  Tooltip,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
} from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import AddCircleOutlineIcon from '@mui/icons-material/AddCircleOutline';
import { inventoryService, warehouseService } from '../services/api';
import { InventoryItem } from '../services/api/inventoryService';
import { Warehouse, ApiError } from '../services/api/warehouseService';
import { useAuth } from '../contexts/AuthContext';

const WarehouseManagement: React.FC = () => {
  const [warehouses, setWarehouses] = useState<Warehouse[]>([]);
  const [inventory, setInventory] = useState<InventoryItem[]>([]);
  const [selectedWarehouse, setSelectedWarehouse] = useState<string>('');
  const [openDialog, setOpenDialog] = useState(false);
  const [newWarehouse, setNewWarehouse] = useState({ name: '', address: '', capacity: '' });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  
  // Get company ID from auth context
  const { user } = useAuth();
  const [companyId, setCompanyId] = useState<string>('');
  
  // Set companyId from auth context when user data is available
  useEffect(() => {
    if (user && user.companyId) {
      setCompanyId(user.companyId);
    }
  }, [user]);

  useEffect(() => {
    fetchWarehouses();
  }, [companyId]); // Re-fetch when companyId changes

  useEffect(() => {
    if (selectedWarehouse) {
      fetchInventory();
    }
  }, [selectedWarehouse]);
  const fetchWarehouses = async () => {
    try {
      if (!companyId) {
        setWarehouses([]);
        return;
      }
      
      setLoading(true);
      const response = await warehouseService.getWarehouses(companyId);
      
      // Debug the response structure
      console.log('Warehouse API response:', response);
      
      // Check the structure of the response and extract the warehouses array
      if (Array.isArray(response.data)) {
        setWarehouses(response.data);
      } 
      // If response.data contains a warehouses property that is an array
      else if (response.data && typeof response.data === 'object' && 'warehouses' in response.data && 
               Array.isArray((response.data as any).warehouses)) {
        setWarehouses((response.data as any).warehouses);
      }
      // If response.data.data contains the warehouses array
      else if (response.data && typeof response.data === 'object' && 'data' in response.data && 
               Array.isArray((response.data as any).data)) {
        setWarehouses((response.data as any).data);
      }
      // Default to empty array if no matching structure is found
      else {
        console.error('Unexpected API response format:', response);
        setWarehouses([]);
      }
      
      setError(null);
    } catch (err) {
      if (err instanceof ApiError) {
        setError(err.message);
      } else {
        setError('Failed to fetch warehouses');
        console.error(err);
      }
      // Always set warehouses to an empty array on error
      setWarehouses([]);
    } finally {
      setLoading(false);
    }
  };

  const fetchInventory = async () => {
    try {
      setLoading(true);
      const response = await inventoryService.getInventory({
        companyId,
        warehouseId: selectedWarehouse
      });
      setInventory(response.data);
      setError(null);
    } catch (err) {
      setError('Failed to fetch inventory');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleWarehouseSelect = (warehouseId: string) => {
    setSelectedWarehouse(warehouseId);
  };

  const handleOpenDialog = () => {
    setOpenDialog(true);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
    setNewWarehouse({ name: '', address: '', capacity: '' });
  };
  const handleCreateWarehouse = async () => {
    try {
      if (!newWarehouse.name.trim()) {
        setError('Warehouse name is required');
        return;
      }
      if (!newWarehouse.address.trim()) {
        setError('Warehouse address is required');
        return;
      }
      if (!newWarehouse.capacity || isNaN(Number(newWarehouse.capacity)) || Number(newWarehouse.capacity) <= 0) {
        setError('Valid warehouse capacity is required');
        return;
      }

      await warehouseService.createWarehouse(companyId, {
        name: newWarehouse.name.trim(),
        address: newWarehouse.address.trim(),
        capacity: Number(newWarehouse.capacity),
        // Removed 'status' as it is not part of 'WarehouseCreateInput'
      });
      
      await handleCloseDialog();
      await fetchWarehouses();
      setError(null);
    } catch (err) {
      if (err instanceof ApiError) {
        setError(err.message);
      } else {
        setError('Failed to create warehouse');
        console.error(err);
      }
    }
  };

  const handleDeleteWarehouse = async (warehouseId: string) => {
    try {
      await warehouseService.deleteWarehouse(companyId, warehouseId);
      await fetchWarehouses();
      if (selectedWarehouse === warehouseId) {
        setSelectedWarehouse('');
      }
      setError(null);
    } catch (err) {
      if (err instanceof ApiError) {
        setError(err.message);
      } else {
        setError('Failed to delete warehouse');
        console.error(err);
      }
    }
  };

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" gutterBottom>
        Warehouse Management
      </Typography>

      {error && (
        <Paper sx={{ p: 2, mb: 2, backgroundColor: 'error.light' }}>
          <Typography color="error">{error}</Typography>
        </Paper>
      )}

      <Box sx={{ display: 'flex', gap: 3 }}>
        {/* Warehouses List */}
        <Box sx={{ flex: 1 }}>
          <Paper sx={{ p: 2 }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
              <Typography variant="h6">Warehouses</Typography>
              <Button
                startIcon={<AddCircleOutlineIcon />}
                onClick={handleOpenDialog}
                variant="contained"
                color="primary"
              >
                Add Warehouse
              </Button>
            </Box>
            <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1 }}>              {Array.isArray(warehouses) ? warehouses.map((warehouse) => (
                <Paper
                  key={warehouse.id}
                  elevation={selectedWarehouse === warehouse.id ? 3 : 1}
                  sx={{
                    p: 2,
                    cursor: 'pointer',
                    backgroundColor: selectedWarehouse === warehouse.id ? 'primary.light' : 'background.paper',
                  }}
                  onClick={() => handleWarehouseSelect(warehouse.id)}
                >
                  <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                    <Typography>{warehouse.name}</Typography>
                    <Box>
                      <IconButton size="small" onClick={(e) => {
                        e.stopPropagation();
                        // Add edit logic
                      }}>
                        <EditIcon fontSize="small" />
                      </IconButton>
                      <IconButton
                        size="small"
                        onClick={(e) => {
                          e.stopPropagation();
                          handleDeleteWarehouse(warehouse.id);
                        }}
                      >
                        <DeleteIcon fontSize="small" />                      </IconButton>
                    </Box>
                  </Box>
                </Paper>
              )) : (
                <Paper elevation={1} sx={{ p: 2 }}>
                  <Typography color="text.secondary">No warehouses available</Typography>
                </Paper>
              )}
            </Box>
          </Paper>
        </Box>

        {/* Inventory List */}
        <Box sx={{ flex: 2 }}>
          <Paper sx={{ p: 2 }}>
            <Typography variant="h6" gutterBottom>
              Inventory
            </Typography>
            {selectedWarehouse ? (
              <TableContainer>
                <Table>
                  <TableHead>
                    <TableRow>
                      <TableCell>Product</TableCell>
                      <TableCell align="right">Quantity</TableCell>
                      <TableCell>Location</TableCell>
                      <TableCell>Status</TableCell>
                      <TableCell align="right">Actions</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {inventory.map((item) => (
                      <TableRow key={item.id}>
                        <TableCell>{item.product?.name}</TableCell>
                        <TableCell align="right">{item.quantity}</TableCell>
                        <TableCell>{item.location || 'N/A'}</TableCell>
                        <TableCell>
                          {item.quantity <= (item.min_threshold || 0) ? 'Low Stock' : 'In Stock'}
                        </TableCell>
                        <TableCell align="right">
                          <Tooltip title="Edit">
                            <IconButton size="small">
                              <EditIcon fontSize="small" />
                            </IconButton>
                          </Tooltip>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            ) : (
              <Typography variant="body1" sx={{ textAlign: 'center', py: 3 }}>
                Select a warehouse to view its inventory
              </Typography>
            )}
          </Paper>
        </Box>
      </Box>

      {/* Add Warehouse Dialog */}
      <Dialog open={openDialog} onClose={handleCloseDialog}>
        <DialogTitle>Add New Warehouse</DialogTitle>
        <DialogContent>
          <Box sx={{ pt: 2, display: 'flex', flexDirection: 'column', gap: 2 }}>
            <TextField
              label="Warehouse Name"
              fullWidth
              value={newWarehouse.name}
              onChange={(e) => setNewWarehouse({ ...newWarehouse, name: e.target.value })}
            />
            <TextField
              label="Address"
              fullWidth
              multiline
              rows={2}
              value={newWarehouse.address}
              onChange={(e) => setNewWarehouse({ ...newWarehouse, address: e.target.value })}
            />
            <TextField
              label="Capacity"
              fullWidth
              type="number"
              value={newWarehouse.capacity}
              onChange={(e) => setNewWarehouse({ ...newWarehouse, capacity: e.target.value })}
            />
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog}>Cancel</Button>
          <Button onClick={handleCreateWarehouse} variant="contained" color="primary">
            Create
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default WarehouseManagement;

import React, { useState, useEffect } from 'react';
import {
  Box,
  Paper,
  Typography,
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  TextField,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TablePagination,
  CircularProgress,
  Alert,
  InputAdornment,
  IconButton
} from '@mui/material';
import AddCircleOutlineIcon from '@mui/icons-material/AddCircleOutline';
import SearchIcon from '@mui/icons-material/Search';
import RefreshIcon from '@mui/icons-material/Refresh';
import { WarehouseCreateInput } from '../services/api/warehouseService';
import { useAuth } from '../contexts/AuthContext';
import useForm from '../hooks/useForm';
import InventoryItemRow from '../components/inventory/InventoryItemRow';
import useWarehouseOperations from '../hooks/useWarehouseOperations';
import useInventoryOperations from '../hooks/useInventoryOperations';

// Interface for warehouse dialog form
interface WarehouseFormValues {
  name: string;
  address: string;
  capacity: string;
  type: 'main' | 'secondary' | 'distribution' | 'storage';
  notes: string;
}

// Form validation function for warehouse
const validateWarehouseForm = (values: WarehouseFormValues) => {
  const errors: Partial<Record<keyof WarehouseFormValues, string>> = {};
  
  if (!values.name.trim()) {
    errors.name = 'Warehouse name is required';
  }
  
  if (!values.address.trim()) {
    errors.address = 'Warehouse address is required';
  }
  
  if (!values.capacity || isNaN(Number(values.capacity)) || Number(values.capacity) <= 0) {
    errors.capacity = 'Valid warehouse capacity is required';
  }
  
  return errors;
};

const WarehouseManagementOptimized: React.FC = () => {
  // Get company ID from auth context
  const { user } = useAuth();
  const companyId = user?.companyId || '';
  
  // Dialog state
  const [openDialog, setOpenDialog] = useState(false);
  const [isEditMode, setIsEditMode] = useState(false);
  
  // Initialize warehouse operations hook
  const {
    selectedWarehouse,
    setSelectedWarehouse,
    warehouses,
    error: warehouseError,
    isLoading: warehouseLoading,
    fetchWarehouses,
    createWarehouse,
    updateWarehouse,
    deleteWarehouse
  } = useWarehouseOperations({
    companyId,
    onWarehouseDeleted: (warehouseId) => {
      if (selectedWarehouse === warehouseId) {
        setSelectedWarehouse('');
      }
    }
  });
  
  // Initialize inventory operations hook
  const {
    inventory,
    isLoading: inventoryLoading,
    error: inventoryError,
    page,
    rowsPerPage,
    totalCount,
    sortBy,
    sortDirection,
    searchQuery,
    handlePageChange,
    handleRowsPerPageChange,
    handleSortByChange,
    handleSortDirectionChange,
    handleSearchQueryChange,
    refreshInventory
  } = useInventoryOperations({
    companyId,
    warehouseId: selectedWarehouse
  });
  
  // Initialize form for warehouse creation/editing
  const warehouseForm = useForm<WarehouseFormValues>({
    initialValues: {
      name: '',
      address: '',
      capacity: '',
      type: 'storage',
      notes: ''
    },
    validate: validateWarehouseForm
  });
  
  // Fetch warehouses when companyId changes
  useEffect(() => {
    if (companyId) {
      fetchWarehouses();
    }
  }, [companyId, fetchWarehouses]);
  
  // Fetch inventory when selectedWarehouse changes
  useEffect(() => {
    if (selectedWarehouse) {
      refreshInventory();
    }
  }, [selectedWarehouse, refreshInventory]);
  
  // Handle opening dialog for creating/editing a warehouse
  const handleOpenDialog = (warehouseId?: string) => {
    if (warehouseId) {
      // Edit mode - find the warehouse and set form values
      const warehouseToEdit = warehouses.find(w => w.id === warehouseId);
      if (warehouseToEdit) {
        setIsEditMode(true);
        warehouseForm.setValues({
          name: warehouseToEdit.name,
          address: warehouseToEdit.address,
          capacity: warehouseToEdit.capacity.toString(),
          type: warehouseToEdit.type || 'storage',
          notes: warehouseToEdit.notes || ''
        });
      }
    } else {
      // Create mode
      setIsEditMode(false);
      warehouseForm.resetForm();
    }
    setOpenDialog(true);
  };
  
  // Handle closing the dialog
  const handleCloseDialog = () => {
    setOpenDialog(false);
  };
  
  // Handle creating or updating a warehouse
  const handleCreateOrUpdateWarehouse = async () => {
    if (!warehouseForm.validateForm()) return;
    
    const warehouseData: WarehouseCreateInput = {
      name: warehouseForm.values.name.trim(),
      address: warehouseForm.values.address.trim(),
      capacity: Number(warehouseForm.values.capacity),
      type: warehouseForm.values.type,
      notes: warehouseForm.values.notes.trim() || undefined
    };
    
    try {
      if (isEditMode && selectedWarehouse) {
        await updateWarehouse(selectedWarehouse, warehouseData);
      } else {
        await createWarehouse(warehouseData);
      }
      handleCloseDialog();
    } catch (error) {
      console.error('Error saving warehouse:', error);
    }
  };
  
  // Handle deleting a warehouse
  const handleDeleteWarehouse = (warehouseId: string) => {
    if (window.confirm('Are you sure you want to delete this warehouse?')) {
      deleteWarehouse(warehouseId);
    }
  };
  
  // Handle selecting a warehouse
  const handleWarehouseSelect = (warehouseId: string) => {
    setSelectedWarehouse(warehouseId);
  };
  
  // Display combined error message from various operations
  const errorMessage = warehouseError?.message || inventoryError;
  
  // Determine if any operation is loading
  const isLoading = warehouseLoading || inventoryLoading;
  
  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" gutterBottom>
        Warehouse Management
      </Typography>

      {errorMessage && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {errorMessage}
        </Alert>
      )}

      <Box sx={{ display: 'flex', gap: 3 }}>
        {/* Warehouses List */}
        <Box sx={{ flex: 1 }}>
          <Paper sx={{ p: 2 }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
              <Typography variant="h6">Warehouses</Typography>
              <Box sx={{ display: 'flex', gap: 1 }}>
                <Button
                  size="small"
                  startIcon={<RefreshIcon />}
                  onClick={fetchWarehouses}
                  disabled={warehouseLoading}
                >
                  Refresh
                </Button>
                <Button
                  startIcon={<AddCircleOutlineIcon />}
                  onClick={() => handleOpenDialog()}
                  variant="contained"
                  color="primary"
                  disabled={warehouseLoading}
                >
                  Add Warehouse
                </Button>
              </Box>
            </Box>
            
            {warehouseLoading ? (
              <Box sx={{ display: 'flex', justifyContent: 'center', py: 3 }}>
                <CircularProgress />
              </Box>
            ) : (
              <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1 }}>
                {warehouses.length > 0 ? (
                  warehouses.map((warehouse) => (
                    <Paper
                      key={warehouse.id}
                      elevation={selectedWarehouse === warehouse.id ? 3 : 1}
                      sx={{
                        p: 2,
                        cursor: 'pointer',
                        backgroundColor: selectedWarehouse === warehouse.id ? 'primary.light' : 'background.paper',
                        '&:hover': {
                          backgroundColor: selectedWarehouse === warehouse.id 
                            ? 'primary.light' 
                            : 'action.hover'
                        }
                      }}
                      onClick={() => handleWarehouseSelect(warehouse.id)}
                    >
                      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
                        <Box>
                          <Typography variant="subtitle1">{warehouse.name}</Typography>
                          <Typography variant="body2" color="text.secondary">
                            {warehouse.address}
                          </Typography>
                          <Box sx={{ display: 'flex', gap: 2, mt: 0.5 }}>
                            <Typography variant="body2" color="text.secondary">
                              Capacity: {warehouse.capacity}
                            </Typography>
                            <Typography variant="body2" color="text.secondary">
                              Type: {warehouse.type}
                            </Typography>
                            <Typography variant="body2" color="text.secondary">
                              Status: {warehouse.status}
                            </Typography>
                          </Box>
                        </Box>
                        <Box>
                          <Button
                            size="small"
                            onClick={(e) => {
                              e.stopPropagation();
                              handleOpenDialog(warehouse.id);
                            }}
                          >
                            Edit
                          </Button>
                          <Button
                            size="small"
                            color="error"
                            onClick={(e) => {
                              e.stopPropagation();
                              handleDeleteWarehouse(warehouse.id);
                            }}
                          >
                            Delete
                          </Button>
                        </Box>
                      </Box>
                    </Paper>
                  ))
                ) : (
                  <Paper elevation={1} sx={{ p: 2 }}>
                    <Typography color="text.secondary">No warehouses available</Typography>
                  </Paper>
                )}
              </Box>
            )}
          </Paper>
        </Box>

        {/* Inventory List */}
        <Box sx={{ flex: 2 }}>
          <Paper sx={{ p: 2 }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
              <Typography variant="h6">Inventory</Typography>
              
              {/* Search field for inventory */}
              {selectedWarehouse && (
                <Box sx={{ display: 'flex', gap: 1 }}>
                  <TextField
                    placeholder="Search inventory"
                    size="small"
                    value={searchQuery}
                    onChange={(e) => handleSearchQueryChange(e.target.value)}
                    InputProps={{
                      startAdornment: (
                        <InputAdornment position="start">
                          <SearchIcon fontSize="small" />
                        </InputAdornment>
                      )
                    }}
                  />
                  <Button
                    size="small"
                    startIcon={<RefreshIcon />}
                    onClick={refreshInventory}
                    disabled={inventoryLoading}
                  >
                    Refresh
                  </Button>
                </Box>
              )}
            </Box>
            
            {selectedWarehouse ? (
              <>
                <TableContainer>
                  <Table>
                    <TableHead>
                      <TableRow>
                        <TableCell 
                          onClick={() => {
                            if (sortBy === 'product.name') {
                              handleSortDirectionChange();
                            } else {
                              handleSortByChange('product.name');
                            }
                          }}
                          sx={{ cursor: 'pointer' }}
                        >
                          Product 
                          {sortBy === 'product.name' && (
                            sortDirection === 'asc' ? ' ' : ' '
                          )}
                        </TableCell>
                        <TableCell 
                          align="right"
                          onClick={() => {
                            if (sortBy === 'quantity') {
                              handleSortDirectionChange();
                            } else {
                              handleSortByChange('quantity');
                            }
                          }}
                          sx={{ cursor: 'pointer' }}
                        >
                          Quantity
                          {sortBy === 'quantity' && (
                            sortDirection === 'asc' ? ' ' : ' '
                          )}
                        </TableCell>
                        <TableCell>Location</TableCell>
                        <TableCell>Status</TableCell>
                        <TableCell align="right">Actions</TableCell>
                      </TableRow>
                    </TableHead>
                    <TableBody>
                      {inventoryLoading && inventory.length === 0 ? (
                        <TableRow>
                          <TableCell colSpan={5} align="center">
                            <CircularProgress size={24} sx={{ my: 3 }} />
                          </TableCell>
                        </TableRow>
                      ) : inventory.length > 0 ? (
                        inventory.map((item) => (
                          <InventoryItemRow 
                            key={item.id}
                            item={item}
                            onEdit={(item) => console.log('Edit item:', item)}
                            onDelete={(itemId) => console.log('Delete item:', itemId)}
                          />
                        ))
                      ) : (
                        <TableRow>
                          <TableCell colSpan={5} align="center">
                            <Typography sx={{ py: 2 }}>No inventory items found</Typography>
                          </TableCell>
                        </TableRow>
                      )}
                    </TableBody>
                  </Table>
                </TableContainer>
                
                <TablePagination
                  component="div"
                  count={totalCount}
                  page={page}
                  onPageChange={(_, page) => handlePageChange(page)}
                  rowsPerPage={rowsPerPage}
                  onRowsPerPageChange={(e) => 
                    handleRowsPerPageChange(parseInt(e.target.value))
                  }
                  rowsPerPageOptions={[5, 10, 25, 50]}
                />
              </>
            ) : (
              <Box sx={{ py: 5, textAlign: 'center' }}>
                <Typography variant="body1" color="text.secondary">
                  Select a warehouse to view its inventory
                </Typography>
              </Box>
            )}
          </Paper>
        </Box>
      </Box>

      {/* Add/Edit Warehouse Dialog */}
      <Dialog open={openDialog} onClose={handleCloseDialog} maxWidth="sm" fullWidth>
        <DialogTitle>
          {isEditMode ? 'Edit Warehouse' : 'Add New Warehouse'}
        </DialogTitle>
        <DialogContent>
          <Box sx={{ pt: 2, display: 'flex', flexDirection: 'column', gap: 2 }}>
            <TextField
              label="Warehouse Name"
              fullWidth
              {...warehouseForm.getFieldProps('name')}
              error={!!warehouseForm.touched.name && !!warehouseForm.errors.name}
              helperText={warehouseForm.touched.name ? warehouseForm.errors.name : ''}
            />
            <TextField
              label="Address"
              fullWidth
              multiline
              rows={2}
              {...warehouseForm.getFieldProps('address')}
              error={!!warehouseForm.touched.address && !!warehouseForm.errors.address}
              helperText={warehouseForm.touched.address ? warehouseForm.errors.address : ''}
            />
            <TextField
              label="Capacity"
              fullWidth
              type="number"
              {...warehouseForm.getFieldProps('capacity')}
              error={!!warehouseForm.touched.capacity && !!warehouseForm.errors.capacity}
              helperText={warehouseForm.touched.capacity ? warehouseForm.errors.capacity : ''}
            />
            <TextField
              label="Notes (Optional)"
              fullWidth
              multiline
              rows={2}
              {...warehouseForm.getFieldProps('notes')}
            />
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog}>Cancel</Button>
          <Button 
            onClick={handleCreateOrUpdateWarehouse} 
            variant="contained" 
            color="primary"
            disabled={warehouseLoading}
          >
            {warehouseLoading ? (
              <CircularProgress size={24} />
            ) : isEditMode ? (
              'Update'
            ) : (
              'Create'
            )}
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default WarehouseManagementOptimized;

