                <TableContainer>
                  <Table>
                    <TableHead>
                      <TableRow>
                        <TableCell 
                          onClick={() => {
                            if (inventoryTable.sortBy === 'product.name') {
                              inventoryTable.handleSortDirectionChange();
                            } else {
                              inventoryTable.handleSortByChange('product.name');
                            }
                          }}
                          sx={{ cursor: 'pointer' }}
                        >
                          Product 
                          {inventoryTable.sortBy === 'product.name' && (
                            inventoryTable.sortDirection === 'asc' ? ' ' : ' '
